INSERT INTO `articles` VALUES (1, 2, 1, 0, 'CII: Deutsche Bank đăng ký mua 2,5 triệu cp và bán 1 triệu cp', 'cii-deutsche-bank-dang-ky-mua-25-trieu-cp-va-ban-1-trieu-cp', 'CTCP Đầu tư Hạ tầng Kỹ thuật TP.HCM (mã CII, HoSE) thông báo kết quả và giao dịch tiếp của cổ đông lớn.', NULL, '<p>\r\n	<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">Theo đ&oacute;, cổ đ&ocirc;ng lớn của c&ocirc;ng ty l&agrave; Deutsche Bank AG London đăng k&yacute; b&aacute;n tiếp 2,5 triệu cp v&agrave; b&aacute;n 1 triệu cp từ 6/12/2010 đến 28/1/2011 th&ocirc;ng qua h&igrave;nh thức khớp lệnh v&agrave; thỏa thuận.<br />\r\n	<br />\r\n	Trước đ&oacute;, từ 5/10/25010 đến 30/11/2010 cổ đ&ocirc;ng n&agrave;y đ&atilde; mua 711.770 đơn vị (trong số 1,1 triệu cp đ&atilde; đăng k&yacute;); đ&atilde; b&aacute;n 36.410 đơn vị (trong số 1,1 triệu CP đăng k&yacute; b&aacute;n). L&yacute; do kh&ocirc;ng giao dịch mua v&agrave; b&aacute;n hết số lượng đăng k&yacute; v&igrave; thị trường biến động n&ecirc;n thay đổi kế hoạch.</span></p>', '2010-12-05 00:00:00', NULL, 1, 0, 0);
INSERT INTO `articles` VALUES (2, 2, 1, 0, 'VNE: thành viên HĐQT bán 100 ngàn CP', 'vne-thanh-vien-hdqt-ban-100-ngan-cp', 'Tổng công ty Cổ phần Xây dựng điện Việt Nam (mã VNE, HoSE) thông báo giao dịch của cổ đông nội bộ.', NULL, '<div align=\\"justify\\">\r\n	<p>\r\n		<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">Theo đ&oacute;, &ocirc;ng Nguyễn Th&agrave;nh Đồng, th&agrave;nh vi&ecirc;n HĐQT đăng k&yacute; b&aacute;n 100 ng&agrave;n CP th&ocirc;ng qua h&igrave;nh thức khớp lệnh.<br />\r\n		<br />\r\n		Thời gian dự kiến giao dịch từ 6/12/2010 đến 6/2/2011.<br />\r\n		<br />\r\n		Số lượng CP cổ đ&ocirc;ng n&agrave;y nắm giữ trước khi thực hiện giao dịch l&agrave; 186.800 CP, chiếm 0,293% vốn điều lệ. Nếu giao dịch th&agrave;nh c&ocirc;ng th&igrave; số CP nắm giữ giảm xuống c&ograve;n 86.800 CP.</span></p>\r\n	<p style=\\"text-align: right;\\">\r\n		&nbsp;</p>\r\n</div>', '2010-12-05 00:00:00', NULL, 1, 0, 0);
INSERT INTO `articles` VALUES (3, 2, 1, 0, 'PXS: 11 tháng lãi 44,98 tỷ đồng sau thuế, đạt 83,13% KH năm', 'pxs-11-thang-lai-4498-ty-dong-sau-thue-dat-8313-kh-nam', 'Đại hội đồng cổ đông Công ty Cổ phần Kết cấu Kim loại và Lắp máy Dầu khí (PXS - HoSE) vừa qua đã thông qua...', NULL, '<div align=\\"justify\\">\r\n	<p style=\\"text-align: justify;\\">\r\n		<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">T&iacute;nh đến ng&agrave;y 30/11/2010, <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=s/4fnTNiugU=\\" target=\\"_blank\\">PXS</a></b> đ&atilde; ho&agrave;n th&agrave;nh 399 tỷ đồng doanh thu v&agrave; 44,98 tỷ đồng lợi nhuận sau thuế, tương đương đạt 83,13% kế hoạch doanh thu v&agrave; đạt 89,5% kế hoạch lợi nhuận năm. Trong 11 th&aacute;ng đầu năm 2010, tổng thu tiền về t&agrave;i khỏan của <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=s/4fnTNiugU=\\" target=\\"_blank\\">PXS</a></b> đạt 392 tỷ đồng, dự kiến th&aacute;ng 12 sẽ tiếp tục thu 75 tỷ đồng. Tổng cộng tiền thu trong năm đạt 467 tỷ đồng, chiếm 97,3% so với tổng doanh thu.<br />\r\n		<br />\r\n		Từ đầu qu&yacute; 3 năm 2010, Ban l&atilde;nh đạo C&ocirc;ng ty đ&atilde; th&agrave;nh lập Tổ thu hồi vốn doTGĐ c&ocirc;ng ty trực tiếp l&agrave;m tổ trưởng, để đ&ocirc;n đốc thu hồi khối lượng tại tất cả c&aacute;c c&ocirc;ng tr&igrave;nh m&agrave; <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=s/4fnTNiugU=\\" target=\\"_blank\\">PXS</a></b> tham gia thực hiện.<br />\r\n		<br />\r\n		C&ocirc;ng t&aacute;c thu hồi được triển khai đồng loạt ở c&aacute;c c&ocirc;ng tr&igrave;nh trọng điểm do <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=s/4fnTNiugU=\\" target=\\"_blank\\">PXS</a></b> thi c&ocirc;ng: Nh&agrave; m&aacute;y chu tr&igrave;nh Điện hỗn hợp Nhơn Trạch 2, C&ocirc;ng tr&igrave;nh chế tạo Ch&acirc;n đế mỏ Đại H&ugrave;ng, Chế tạo Ch&acirc;n đế - khối thượng tầng Gi&agrave;n khai th&aacute;c Mộc Tinh &ndash; Biển Đ&ocirc;ng, Chế tạo Ch&acirc;n đế - khối thượng tầng Gi&agrave;n khai th&aacute;c BK15 &ndash; Mỏ Bạch Hổ, Chế tạo Ch&acirc;n đế - khối thượng tầng Gi&agrave;n khai th&aacute;c RC3 &ndash; Mỏ Rồng&hellip;To&agrave;n bộ hồ sơ ho&agrave;n c&ocirc;ng, nghiệm thu đang được <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=s/4fnTNiugU=\\" target=\\"_blank\\">PXS</a></b> ho&agrave;n tất để kịp xuất ho&aacute; đơn trong th&aacute;ng 12/2010.</span></p>\r\n</div>', '2010-12-05 00:00:00', NULL, 1, 0, 0);
INSERT INTO `articles` VALUES (4, 2, 1, 0, 'SMC: 13/12 chốt quyền nhận cổ tức 10% bằng tiền mặt', 'smc-1312-chot-quyen-nhan-co-tuc-10-bang-tien-mat', 'Công ty Cổ phần Đầu tư – Thương mại SMC (mã SMC, HoSE) thông báo trả cổ tức đợt 1/2010.', NULL, '<p>\r\n	<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">Ng&agrave;y 13/12 l&agrave; ng&agrave;y đăng k&yacute; cuối c&ugrave;ng v&agrave; ng&agrave;y 9/12 l&agrave; GDKHQ để nhận cổ tức đợt 1 năm 2010 tỷ lệ 10%/mệnh gi&aacute; (01 cổ phiếu nhận được 1.000 đồng). Thời gian chi trả v&agrave;o ng&agrave;y 22/12/2010.<br />\r\n	<br />\r\n	- Địa điểm thực hiện:<br />\r\n	<br />\r\n	+ Đối với chứng kho&aacute;n đ&atilde; lưu k&yacute;: Người sở hữu chứng kho&aacute;n l&agrave;m thủ tục nhận cổ tức tại c&aacute;c TVLK nơi mở t&agrave;i khoản lưu k&yacute; chứng kho&aacute;n.<br />\r\n	<br />\r\n	+ Đối với chứng kho&aacute;n chưa lưu k&yacute;: Người sở hữu chứng kho&aacute;n l&agrave;m thủ tục nhận cổ tức tại Văn ph&ograve;ng C&ocirc;ng ty.<br />\r\n	<br />\r\n	Được biết, lũy kế 9 th&aacute;ng đầu năm 2010, <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=jBwI8niAXfY=\\" target=\\"_blank\\">SMC</a></b> đạt 68,51 tỷ đồng LNST trong đ&oacute;, phần LNST của cổ đ&ocirc;ng c&ocirc;ng ty mẹ l&agrave; 68,01 tỷ đồng. Mức lợi nhuận n&agrave;y tăng 24,72% so với 9 th&aacute;ng đầu năm 2009. So với kế hoạch 80 tỷ đồng LNST cả năm 2010, kết th&uacute;c qu&yacute; III, <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=jBwI8niAXfY=\\" target=\\"_blank\\">SMC</a></b> ho&agrave;n th&agrave;nh 85,64%.</span></p>', '2010-12-05 00:00:00', NULL, 1, 0, 0);
INSERT INTO `articles` VALUES (5, 2, 1, 0, 'CII: Cầu đường Bình Triệu đăng ký mua 2 triệu CP', 'cii-cau-duong-binh-trieu-dang-ky-mua-2-trieu-cp', 'CTCP Đầu tư và Xây dựng Cầu đường Bình Triệu - tổ chức có liên quan đến cổ đông nội bộ CTCP Đầu tư Hạ tầng...', 'files/news/20101205/20101205085615.jpg', '<p>\r\n	<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">Giao dịch dự kiến được thực hiện trong thời gian từ ng&agrave;y 6/12/2010 đến 6/2/2011, bằng phương thức khớp lệnh hoặc thỏa thuận.<br />\r\n	<br />\r\n	Hiện Cầu đường B&igrave;nh Triệu đang nắm giữ 2.250.000 CP <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=+DQJ4QhOiV4=\\" target=\\"_blank\\">CII</a></b>, tương đương tỷ lệ 3%. Nếu giao dịch th&agrave;nh c&ocirc;ng th&igrave; số lượng sở hữu sẽ tăng l&ecirc;n 4.250.000 đơn vị, tương đương tỷ lệ 5,7%.<br />\r\n	&nbsp;<br />\r\n	Được biết, từ 6/12/2010 đến 28/1/2011, cổ đ&ocirc;ng lớn của <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=+DQJ4QhOiV4=\\" target=\\"_blank\\">CII</a></b> c&ocirc;ng ty l&agrave; Deutsche Bank AG London cũng đăng k&yacute; mua tiếp 2,5 triệu CP v&agrave; b&aacute;n 1 triệu CP, th&ocirc;ng qua h&igrave;nh thức khớp lệnh v&agrave; thỏa thuận. Trước đ&oacute;, từ 5/10/25010 đến 30/11/2010 cổ đ&ocirc;ng n&agrave;y đ&atilde; mua 711.770 đơn vị (trong số 1,1 triệu cp đ&atilde; đăng k&yacute;); đ&atilde; b&aacute;n 36.410 đơn vị (trong số 1,1 triệu CP đăng k&yacute; b&aacute;n).<br />\r\n	<br />\r\n	* Ng&agrave;y 10/12 l&agrave; ng&agrave;y giao dịch kh&ocirc;ng hưởng quyền v&agrave; ng&agrave;y 14/12/2010 l&agrave; ng&agrave;y đăng k&yacute; cuối c&ugrave;ng chốt danh s&aacute;ch cổ đ&ocirc;ng để <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=+DQJ4QhOiV4=\\" target=\\"_blank\\">CII</a></b> thực hiện tạm ứng cổ tức đợt 1/2010 bằng tiền theo tỷ lệ 10%/mệnh gi&aacute;. Ng&agrave;y chi trả l&agrave; 14/1/2011. <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=+DQJ4QhOiV4=\\" target=\\"_blank\\">CII</a></b> cũng chốt quyền để tổ chức ĐHĐCĐ bất thường năm 2011 nhằm lấy &yacute; kiến về việc ph&aacute;t h&agrave;nh th&ecirc;m 15 triệu USD tr&aacute;i phiếu chuyển đổi cho Goldman Sachs.</span></p>', '2010-12-05 00:00:00', NULL, 1, 0, 0);
INSERT INTO `articles` VALUES (6, 2, 1, 0, 'ITC: HĐQT chấp thuận mua lại 3 triệu CP làm CP quỹ', 'itc-hdqt-chap-thuan-mua-lai-3-trieu-cp-lam-cp-quy', 'Công ty Cổ phần Đầu tư - Kinh doanh nhà (mã ITC, HoSE) công bố Nghị quyết HĐQT về việc mua lại cổ phiếu quỹ.', NULL, '<p>\r\n	<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">Theo đ&oacute;, HĐQT chấp thuận mua lại 3 triệu CP l&agrave;m CP quỹ bằng nguồn vốn thặng dư. Mục đ&iacute;ch mua cổ phiếu quỹ l&agrave; chia cổ tức cho cổ đ&ocirc;ng hoặc cổ phiếu thưởng.<br />\r\n	<br />\r\n	HĐQT ủy quyền cho TGĐ quyết định gi&aacute; mua v&agrave; kế hoạch mua.</span></p>', '2010-12-05 00:00:00', NULL, 1, 0, 1);
INSERT INTO `articles` VALUES (7, 2, 1, 0, 'Phong tỏa tài khoản của ông Lê Văn Dũng, 2 Công ty và 13 người có liên quan', 'phong-toa-tai-khoan-cua-ong-le-van-dung-2-cong-ty-va-13-nguoi-co-lien-quan', 'Cơ quan An ninh điều tra - Bộ Công an đã khởi tố vụ án hình sự, khởi tố bị can, bắt tạm giam đối với ông Lê Văn Dũng (nguyên Chủ tịch Hội đồng Quản trị, kiêm Tổng Giám đốc Công ty Cổ phần Dược...', NULL, '<p>\r\n	<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">Theo y&ecirc;u cầu của cơ quan An ninh để phục vụ c&ocirc;ng t&aacute;c điều tra v&agrave; bảo vệ quyền, lợi &iacute;ch hợp ph&aacute;p của nh&agrave; đầu tư, Ủy ban Chứng kho&aacute;n Nh&agrave; nước đ&atilde; c&oacute; c&ocirc;ng văn y&ecirc;u cầu Trung t&acirc;m Lưu k&yacute; Chứng kho&aacute;n Việt Nam v&agrave; 04 C&ocirc;ng ty chứng kho&aacute;n (C&ocirc;ng ty Cổ phần Chứng kho&aacute;n S&agrave;i G&ograve;n Thương T&iacute;n (SBS); C&ocirc;ng ty Cổ phần Chứng kho&aacute;n S&agrave;i G&ograve;n H&agrave; Nội (SHS); C&ocirc;ng ty Cổ phần Chứng kho&aacute;n S&agrave;i G&ograve;n (SSI) v&agrave; C&ocirc;ng ty Cổ phần Chứng kho&aacute;n Bảo Việt (BVSC) phong tỏa ngay t&agrave;i khoản của &ocirc;ng L&ecirc; Văn Dũng, 2 C&ocirc;ng ty (C&ocirc;ng ty Cổ phần Đầu tư Y tế Medi, C&ocirc;ng ty TNHH Ph&aacute;t triển thương mại v&agrave; Kỹ thuật Ho&agrave;n Thiện) v&agrave; 13 người c&oacute; li&ecirc;n quan;<br />\r\n	<br />\r\n	Đồng thời, Ủy ban chứng kho&aacute;n y&ecirc;u cầu c&aacute;c Sở Giao dịch chứng kho&aacute;n gi&aacute;m s&aacute;t việc thực hiện phong tỏa n&ecirc;u tr&ecirc;n.<br />\r\n	<br />\r\n	Được biết, ng&agrave;y 26/11/2010 Cơ quan an ninh điều tra - Bộ C&ocirc;ng an đ&atilde; c&oacute; th&ocirc;ng b&aacute;o bắt &ocirc;ng L&ecirc; Văn Dũng, Chủ tịch Hội đồng Quản trị ki&ecirc;m Tổng gi&aacute;m đốc C&ocirc;ng ty Cổ phần Dược phẩm Viễn Đ&ocirc;ng (<b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=xmWK5jqc0Zs=\\" target=\\"_blank\\">DVD</a></b>) do li&ecirc;n quan đến h&agrave;nh vi thao t&uacute;ng gi&aacute; chứng kho&aacute;n.<br />\r\n	<br />\r\n	Cổ phiếu <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=xmWK5jqc0Zs=\\" target=\\"_blank\\">DVD</a></b> được đưa v&agrave;o diện bị kiểm so&aacute;t kể từ ng&agrave;y 2/12/2010 do việc giải tỉnh khacứ phục thiếu s&oacute;t dẫn đến việc đ&igrave;nh chỉ đợt ch&agrave;o b&aacute;n chứng kho&aacute;n ra c&ocirc;ng ch&uacute;ng chưa đ&aacute;p ứng đầy đủ y&ecirc;u cầu của Ủy ban Chứng kho&aacute;n, vấn đề n&agrave;y c&oacute; thể ảnh hưởng đến quyết định của nh&agrave; đầu tư đối với cổ phiếu <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=xmWK5jqc0Zs=\\" target=\\"_blank\\">DVD</a></b> v&agrave; hạn chế thời gian giao dịch đối với cổ phiếu <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=xmWK5jqc0Zs=\\" target=\\"_blank\\">DVD</a></b> l&agrave; chỉ được thực hiện giao dịch 15 ph&uacute;t trong phi&ecirc;n khớp lệnh định kỳ x&aacute;c định gi&aacute; đ&oacute;ng cửa.</span></p>', '2010-12-05 00:00:00', NULL, 1, 1, 0);
INSERT INTO `articles` VALUES (8, 2, 1, 0, 'Habeco: Đấu giá gần 7 triệu cổ phiếu với giá khởi điểm 32.000 đồng', 'habeco-dau-gia-gan-7-trieu-co-phieu-voi-gia-khoi-diem-32000-dong', 'Sở Giao dịch Chứng khoán Hà Nội (HNX) thông báo về việc đấu giá cổ phần của Tổng Công ty Cổ phần Bia – Rượu – Nước Giải khát Hà Nội (Habeco).', NULL, '<p>\r\n	<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">Theo đ&oacute;, ng&agrave;y 24/12/2010, HNX sẽ tổ chức đấu gi&aacute; 6.954.000 cổ phần của Tổng C&ocirc;ng ty Cổ phần Bia &ndash; Rượu &ndash; Nước Giải kh&aacute;t H&agrave; Nội (Habeco) với gi&aacute; khởi điểm đấu gi&aacute; l&agrave; 32.000 đồng.<br />\r\n	<br />\r\n	Habeco hiện c&oacute; vốn điều lệ 2.318 tỷ đồng, trụ sở ch&iacute;nh đặt tại 183 Ho&agrave;ng Hoa Th&aacute;m, Ba Đ&igrave;nh, H&agrave; Nội. C&ocirc;ng ty hoạt động ch&iacute;nh trong lĩnh vực sản xuất kinh doanh bia, rượu, nước giải kh&aacute;t.<br />\r\n	<br />\r\n	Năm 2010, Habeco th&ocirc;ng qua kế hoạch kinh doanh với tổng sản lượng bua ti&ecirc;u thụ l&agrave; 418.200 triệu l&iacute;t; tổng doanh thu v&agrave; thu nhập đạt 4.946,948 tỷ đồng; tổng lợi nhuận trước thuế v&agrave; sau thuế lần lượt đạt 649,954 tỷ đồng v&agrave; 492,194 tỷ đồng; cổ tức 12%/năm.<br />\r\n	<br />\r\n	Được biết, tổng doanh thu b&aacute;n h&agrave;ng v&agrave; dịch vụ năm 2009 đạt 4.079,358 tỷ đồng; doanh thu hoạt động t&agrave;i ch&iacute;nh đạt 116,417 tỷ đồng; lợi nhuận trước v&agrave; sau thuế lần lượt đạt 375,263 tỷ đồng v&agrave; 293,054 tỷ đồng.</span></p>', '2010-12-05 00:00:00', NULL, 1, 1, 0);
INSERT INTO `articles` VALUES (9, 2, 1, 0, 'VFG: 13/12 chốt quyền nhận cổ tức 10%', 'vfg-1312-chot-quyen-nhan-co-tuc-10', 'CTCP Khử Trùng Việt Nam (VFG, HoSE) thông báo chốt quyền trả cổ tức đợt 2 năm 2010 bằng tiền mặt.', 'files/news/20101205/20101205085901.jpg', '<p>\r\n	<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">Ng&agrave;y giao dịch kh&ocirc;ng hưởng quyền: 9/12/2010. Ng&agrave;y đăng k&yacute; cuối c&ugrave;ng: 13/12/2010.<br />\r\n	<br />\r\n	Theo đ&oacute;, c&ocirc;ng ty chi trả cổ tức đợt 2 năm 2010 bằng tiền mặt tỷ lệ 10%. Cổ tức được thanh to&aacute;n ng&agrave;y 27/12/2010.<br />\r\n	<br />\r\n	Đối với chứng kho&aacute;n đ&atilde; lưu k&yacute; : Người sở hữu l&agrave;m thủ tục nhận cổ tức tại c&aacute;c TVLK nơi mở t&agrave;i khoản lưu k&yacute;.<br />\r\n	&nbsp;<br />\r\n	Đối với chứng kho&aacute;n chưa lưu k&yacute; : Người sở hữu l&agrave;m thủ tục nhận cổ tức bằng tiền mặt (hoặc chuyển khoản) tại C&ocirc;ng ty Cổ phần Khử Tr&ugrave;ng Việt Nam. </span></p>', '2010-12-05 00:00:00', NULL, 1, 1, 1);
INSERT INTO `articles` VALUES (10, 2, 1, 0, 'KDC chào bán hơn 18 triệu cổ phần cho việc sáp nhập', 'kdc-chao-ban-hon-18-trieu-co-phan-cho-viec-sap-nhap', 'Đợt chào bán 18,2 triệu cổ phần của Kinh Đô lần này nhằm đổi lấy cổ phiếu NKD và Kido đang lưu hành ngoài thị trường cho mục đích sáp nhập đã thông qua tại Đại hội cổ đông hồi giữa năm của cả 3 doanh nghiệp.', 'files/news/20101205/20101205090009.jpg', '<p>\r\n	<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">Ủy ban Chứng kho&aacute;n vừa cấp giấy chứng nhận ch&agrave;o b&aacute;n cổ phiếu ra c&ocirc;ng ch&uacute;ng cho C&ocirc;ng ty cổ phần Kinh Đ&ocirc; (m&atilde; <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=c3B5rqoh1h4=\\" target=\\"_blank\\">KDC</a></b>). Trong số 18.244.743 cổ phần ph&aacute;t h&agrave;nh th&ecirc;m, 75% (tương đương 13,7 triệu) d&ugrave;ng để ho&aacute;n đổi với NKD (cổ phiếu của C&ocirc;ng ty thực phẩm Kinh Đ&ocirc; miền Bắc) v&agrave; số c&ograve;n lại gần 4,5 triệu d&agrave;nh cho việc chuyển đổi cổ phiếu Kido (C&ocirc;ng ty Kem Ki do) theo tỷ lệ 1:1,1. Lượng ch&agrave;o b&aacute;n th&ecirc;m sẽ ch&iacute;nh thức giao dịch bổ sung tại HOSE sau một th&aacute;ng nữa.<br />\r\n	<br />\r\n	Tỷ lệ số cổ phần ph&aacute;t h&agrave;nh th&ecirc;m bằng 18,2% tổng số cổ phần đang lưu h&agrave;nh. Gi&aacute; trị ch&agrave;o b&aacute;n theo mệnh gi&aacute; 182,4 tỷ đồng.<br />\r\n	<br />\r\n	Theo Ủy ban Chứng kho&aacute;n Nh&agrave; nước, gi&aacute; <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=c3B5rqoh1h4=\\" target=\\"_blank\\">KDC</a></b> kh&ocirc;ng bị điều chỉnh giảm, do số ph&aacute;t h&agrave;nh th&ecirc;m d&ugrave;ng để ho&aacute;n đổi cổ phiếu thực hiện việc s&aacute;p nhập. Kinh Đ&ocirc; dự kiến lợi nhuận sau thuế v&agrave; EPS l&agrave; 936 tỷ đồng, 7.034 đồng, tăng lần lượt 13% v&agrave; 9% so với trước s&aacute;p nhập. Doanh thu năm đầu ti&ecirc;n sau s&aacute;p nhập ước đạt 167 triệu USD.<br />\r\n	<br />\r\n	Phương &aacute;n s&aacute;p nhập của bộ ba <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=c3B5rqoh1h4=\\" target=\\"_blank\\">KDC</a></b>, NKD, Kido sau nhiều lần tr&igrave; ho&atilde;n đ&atilde; đi đến bước cuối c&ugrave;ng, khi cổ đ&ocirc;ng của 3 doanh nghiệp duyệt lộ tr&igrave;nh cũng như gi&aacute; chuyển đổi tại đại hội cổ đ&ocirc;ng hồi th&aacute;ng 5. Theo đ&oacute;, NKD v&agrave; Kido sẽ trở th&agrave;nh những c&ocirc;ng ty con dưới h&igrave;nh thức c&ocirc;ng ty TNHH một th&agrave;nh vi&ecirc;n, m&agrave; chủ sở hữu l&agrave; <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=c3B5rqoh1h4=\\" target=\\"_blank\\">KDC</a></b>. Vốn điều lệ mới n&acirc;ng l&ecirc;n gần 1.200 tỷ đồng, gấp 7 lần vốn điều lệ hiện tại của NKD v&agrave; gần 20 lần so với Kido.</span></p>', '2010-12-05 00:00:00', NULL, 1, 1, 1);
INSERT INTO `articles` VALUES (11, 3, 1, 0, 'POM: Đăng ký mua 1.640.560 cp quỹ (HOSE)', 'pom-dang-ky-mua-1640560-cp-quy-hose', '', NULL, '<p>\r\n	<strong>C&ocirc;ng ty CP Th&eacute;p <a href=\\"\\\\&quot;http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=QaE4UO2USm4=\\\\&quot;\\" target=\\"\\\\&quot;_blank\\\\&quot;\\"><strong>POM</strong></a>INA (<a href=\\"\\\\&quot;http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=QaE4UO2USm4=\\\\&quot;\\" target=\\"\\\\&quot;_blank\\\\&quot;\\"><strong>POM</strong></a>)CBTT đăng k&yacute; mua cổ phiếu quỹ như sau:</strong></p>\r\n<ul>\r\n	<li>\r\n		<span id=\\"\\\\&quot;ctl00_mainContent_lblContent\\\\&quot;\\">Số lượng cổ phiếu quỹ hiện tại: 359.440 cổ phiếu</span></li>\r\n	<li>\r\n		Số lượng đăng k&yacute; mua: 1.640.560 cổ phiếu</li>\r\n	<li>\r\n		Tổng số cổ phiếu sau khi giao dịch: 2.000.000 cổ phiếu</li>\r\n	<li>\r\n		Mục đ&iacute;ch: ổn định gi&aacute; cổ phiếu v&agrave; sử dụng hiệu quả nguồn vốn</li>\r\n	<li>\r\n		Nguồn vốn thực hiện: thặng dư vốn v&agrave; lợi nhuận giữ lại t&iacute;nh đến ng&agrave;y 30/06/2010</li>\r\n	<li>\r\n		Phương thức giao dịch: khớp lệnh</li>\r\n	<li>\r\n		Thời gian đăng k&yacute; giao dịch: từ ng&agrave;y 09/12/2010 đến ng&agrave;y 09/03/2011</li>\r\n	<li>\r\n		C&ocirc;ng ty Chứng kho&aacute;n được ủy quyền thực hiện việc mua: C&ocirc;ng ty CP Chứng kho&aacute;n S&agrave;i G&ograve;n</li>\r\n</ul>', '2010-12-05 00:00:00', '2010-12-05 00:00:00', 1, 0, 0);
INSERT INTO `articles` VALUES (12, 3, 1, 0, 'TTF: Ông Tạ Văn Nam - TV.HĐQT kiêm Phó TGĐ đã bán 90.000 cp (HOSE)', 'ttf-ong-ta-van-nam---tvhdqt-kiem-pho-tgd-da-ban-90000-cp-hose', '', NULL, '<p>\r\n	<span class=\\"\\\\&quot;p_te8\\\\&quot;\\" id=\\"\\\\&quot;UcNewsDetail1_lblNoiDung\\\\&quot;\\" style=\\"\\" width:=\\"\\">B&aacute;o c&aacute;o kết quả giao dịch cổ đ&ocirc;ng nội bộ của CTCP Tập đo&agrave;n Kỹ nghệ Gỗ Trường Th&agrave;nh n</span></p>', '2010-12-05 00:00:00', '2010-12-05 00:00:00', 1, 0, 0);
INSERT INTO `articles` VALUES (13, 3, 1, 0, 'LIX: Red River Holding - Cổ đông lớn đã mua 30.000 cp (HOSE)', 'lix-red-river-holding---co-dong-lon-da-mua-30000-cp-hose', '', NULL, '<p>\r\n	<span class=\\"\\\\&quot;\\\\\\\\&quot;p_te8\\\\\\\\&quot;\\\\&quot;\\" id=\\"\\\\&quot;\\\\\\\\&quot;UcNewsDetail1_lblNoiDung\\\\\\\\&quot;\\\\&quot;\\" style=\\"\\" width:=\\"\\\\&quot;\\\\&quot;\\">B&aacute;o c&aacute;o kết quả giao dịch cổ đ&ocirc;ng lớn C&ocirc;ng ty Cổ phần Bột giặt <b><a href=\\"\\\\&quot;\\\\\\\\&quot;http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=NlWlY+jJwLI=\\\\\\\\&quot;\\\\&quot;\\" target=\\"\\\\&quot;\\\\\\\\&quot;_blank\\\\\\\\&quot;\\\\&quot;\\">LIX</a></b></span></p>', '2010-12-05 00:00:00', '2010-12-05 00:00:00', 1, 1, 0);
INSERT INTO `articles` VALUES (14, 3, 1, 0, 'FBT: Tổ chức ĐHCĐ bất thường năm 2010 (HOSE)', 'fbt-to-chuc-dhcd-bat-thuong-nam-2010-hose', '', NULL, '<p>\r\n	<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">CTCP Xuất nhập khẩu L&acirc;m thủy sản Bến tre th&ocirc;ng b&aacute;o tổ chức Đại hội cổ đ&ocirc;ng bất thường năm 2010</span></p>', '2010-12-05 00:00:00', NULL, 1, 1, 0);
