INSERT INTO `articles` VALUES (17, 3, 1, 0, 'MTG: Quyết định niêm yết bổ sung gần 4 triệu cp (HOSE)', 'mtg-quyet-dinh-niem-yet-bo-sung-gan-4-trieu-cp-hose', '', NULL, '<p>\r\n	<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">Quyết định ni&ecirc;m yết bổ sung cổ phiếu của C&ocirc;ng ty Cổ phần MT GAS</span></p>', '2010-12-05 00:00:00', NULL, 1, 1, 0);
INSERT INTO `articles` VALUES (18, 3, 1, 0, 'PAC: FTIF – Templeton frontier markets fund - Cổ đông lớn đã mua 229.090 cp (HOSE)', 'pac-ftif--templeton-frontier-markets-fund---co-dong-lon-da-mua-229090-cp-hose', '', NULL, '<p>\r\n	<strong>Th&ocirc;ng b&aacute;o sở hữu của cổ đ&ocirc;ng lớn của CTCP Pin Ắc quy Miền nam (<a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=bzEuqShuMz8=\\" target=\\"_blank\\"><strong>PAC</strong></a>) như sau:</strong></p>\r\n<ul>\r\n	<li>\r\n		<span id=\\"ctl00_mainContent_lblContent\\">T&ecirc;n nhà đ&acirc;̀u tư thực hi&ecirc;̣n giao dịch: FTIF &ndash; Templeton frontier markets fund</span></li>\r\n	<li>\r\n		Mã chứng khoán giao dịch: <a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=bzEuqShuMz8=\\" target=\\"_blank\\"><strong>PAC</strong></a></li>\r\n	<li>\r\n		Số lượng, tỷ lệ c&ocirc;̉ phi&ecirc;́u nắm giữ trước khi thực hi&ecirc;̣n giao dịch: 1.132.130 c&ocirc;̉ phi&ecirc;́u (5,1%)</li>\r\n	<li>\r\n		Số lượng c&ocirc;̉ phi&ecirc;́u đ&atilde; mua: 229.090 cổ phiếu</li>\r\n	<li>\r\n		Số lượng, tỷ lệ c&ocirc;̉ phi&ecirc;́u nắm giữ sau khi thực hi&ecirc;̣n giao dịch: 1.361.220 c&ocirc;̉ phi&ecirc;́u (6,13%)</li>\r\n	<li>\r\n		L&yacute; do thay đổi sở hữu: thực hiện đầu tư</li>\r\n	<li>\r\n		Phương thức giao dịch: khớp lệnh</li>\r\n	<li>\r\n		Thời gian thay đổi sở hữu v&agrave; trở th&agrave;nh cổ đ&ocirc;ng lớn: ng&agrave;y 18/11/2010</li>\r\n</ul>', '2010-12-05 00:00:00', NULL, 1, 1, 0);
INSERT INTO `articles` VALUES (19, 3, 1, 0, 'KDC: Bản cáo bạch chào bán cp (HOSE)', 'kdc-ban-cao-bach-chao-ban-cp-hose', '', NULL, '<p>\r\n	<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">Bản c&aacute;o bạch ch&agrave;o b&aacute;n cổ phiếu của C&ocirc;ng ty Cổ phần Kinh Đ&ocirc;</span></p>', '2010-12-05 00:00:00', NULL, 1, 1, 0);
INSERT INTO `articles` VALUES (20, 3, 1, 0, 'TTF: Ông Tạ Văn Nam - TV.HĐQT kiêm Phó TGĐ đăng ký bán 107.500 cp (HOSE)', 'ttf-ong-ta-van-nam---tvhdqt-kiem-pho-tgd-dang-ky-ban-107500-cp-hose', '', NULL, '<p>\r\n	<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">Th&ocirc;ng b&aacute;o giao dịch cổ đ&ocirc;ng nội bộ của CTCP Tập đo&agrave;n Kỹ nghệ Gỗ Trường Th&agrave;nh</span></p>', '2010-12-05 00:00:00', NULL, 1, 1, 0);
INSERT INTO `articles` VALUES (21, 3, 1, 0, 'PRUBF1: Thông báo về việc chào mua công khai (HOSE)', 'prubf1-thong-bao-ve-viec-chao-mua-cong-khai-hose', '', NULL, '<p>\r\n	<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">Quỹ đầu tư C&acirc;n bằng Prudential - PRUBF1 th&ocirc;ng b&aacute;o về việc ch&agrave;o mua c&ocirc;ng khai</span></p>', '2010-12-05 00:00:00', NULL, 1, 1, 0);
INSERT INTO `articles` VALUES (22, 26, 1, 0, 'Bảo hiểm đầu tư ra nước ngoài: Không chạy theo trào lưu', 'bao-hiem-dau-tu-ra-nuoc-ngoai-khong-chay-theo-trao-luu', '"Đầu tư ra nước ngoài không phải chạy theo trào lưu, mà đều được các DN bảo hiểm cân nhắc, nghiên cứu kỹ lưỡng dựa trên chiến...', 'files/news/20101205/20101205162636.jpg', '<p>\r\n	<em><strong>Đ&atilde; c&oacute; những &yacute; kiến e ngại, thậm ch&iacute; l&agrave; nghi ngờ về &quot;tr&agrave;o lưu&quot; đầu tư ra nước ngo&agrave;i của c&aacute;c DN bảo hiểm phi nh&acirc;n thọ, nhất l&agrave; khi thị trường trong nước c&ograve;n m&ecirc;nh m&ocirc;ng chưa khai ph&aacute; hết. L&agrave; người trong cuộc, &ocirc;ng nhận định thế n&agrave;o về việc đầu tư ra nước ngo&agrave;i n&agrave;y?</strong></em><br />\r\n	<br />\r\n	Với sự ph&aacute;t triển kh&aacute; tốt của nền kinh tế, cộng với d&acirc;n số đ&ocirc;ng, m&ocirc;i trường hấp dẫn cho c&aacute;c dự &aacute;n đầu tư nước ngo&agrave;i, Việt Nam vẫn được đ&aacute;nh gi&aacute; l&agrave; thị trường tiềm năng cho c&aacute;c DN bảo hiểm trong v&agrave; ngo&agrave;i nước khai ph&aacute;.<br />\r\n	<br />\r\n	Tuy nhi&ecirc;n, do thực trạng chung l&agrave; c&oacute; qu&aacute; nhiều DN hoạt động tr&ecirc;n thị trường, tr&igrave;nh độ nhận thức của người d&acirc;n về bảo hiểm chưa cao, n&ecirc;n sự ph&aacute;t triển của c&aacute;c DN bảo hiểm tại Việt Nam c&ograve;n rất hạn chế. T&igrave;nh h&igrave;nh cạnh tranh giữa c&aacute;c DN l&agrave; kh&aacute; gay gắt, bao gồm cả c&aacute;c h&igrave;nh thức cạnh tranh phi kỹ thuật như hạ ph&iacute;, hạ mức khấu trừ, mở rộng điều khoản điều kiện&hellip; Mở rộng đầu tư ra ngo&agrave;i thị trường Việt Nam l&agrave; một hướng đi mới v&agrave; c&oacute; thể coi l&agrave; một xu hướng tất yếu của c&aacute;c DN bảo hiểm nhằm khai th&aacute;c c&aacute;c thị trường mới nổi, c&oacute; nhiều cơ hội, v&agrave; đang rộng mở đối với c&aacute;c NĐT.<br />\r\n	<br />\r\n	<em><strong>Ngo&agrave;i c&aacute;c NĐT l&agrave; DN bảo hiểm Việt Nam, thị trường bảo hiểm Campuchia c&oacute; hấp dẫn c&aacute;c NĐT nước ngo&agrave;i kh&ocirc;ng, thưa &ocirc;ng?</strong></em><br />\r\n	<br />\r\n	Hiện nay, thị trường bảo hiểm Campuchia c&oacute; 6 DN bảo hiểm v&agrave; 1 DN t&aacute;i bảo hiểm. Ngo&agrave;i C&ocirc;ng ty T&aacute;i bảo hiểm Quốc gia Campuchia v&agrave; C&ocirc;ng ty Bảo hiểm Quốc gia Campuchia (Caminco) c&oacute; 100% vốn trong nước, c&aacute;c DN c&ograve;n lại đều c&oacute; vốn g&oacute;p nước ngo&agrave;i: CVI l&agrave; c&ocirc;ng ty bảo hiểm c&oacute; 80% vốn đầu tư từ Việt Nam; Asia Insurance (Cambodia) c&oacute; vốn g&oacute;p giữa c&aacute;c li&ecirc;n doanh bảo hiểm của Hồng K&ocirc;ng, Th&aacute;i Lan, Indonesia; Campubank Longpac l&agrave; c&ocirc;ng ty của Malaysia; Forte c&oacute; vốn g&oacute;p của Singapore; Infinity Insurance l&agrave; c&ocirc;ng ty li&ecirc;n doanh của một tập đo&agrave;n kinh tế trong nước v&agrave; nước ngo&agrave;i. Điều n&agrave;y chứng tỏ thị trường bảo hiểm Campuchia kh&aacute; hấp dẫn c&aacute;c NĐT nước ngo&agrave;i.<br />\r\n	<br />\r\n	B&ecirc;n cạnh lĩnh vực bảo hiểm h&agrave;ng kh&ocirc;ng v&agrave; xe cơ giới, nh&oacute;m sản phẩm bảo hiểm t&agrave;i sản kỹ thuật cũng sẽ l&agrave; một lĩnh vực tiềm năng đối với c&aacute;c DN bảo hiểm khi gia nhập thị trường Campuchia.<br />\r\n	<br />\r\n	<em><strong>Theo &ocirc;ng, kh&oacute; khăn lớn nhất của c&aacute;c DN bảo hiểm Việt Nam khi th&acirc;m nhập thị trường n&agrave;y l&agrave; g&igrave;?<br />\r\n	</strong></em><br />\r\n	Thứ nhất, về năng lực bảo hiểm, đa phần DN bảo hiểm hiện đang hoạt động tại Campuchia đều c&oacute; vốn g&oacute;p của c&aacute;c c&ocirc;ng ty bảo hiểm nước ngo&agrave;i. Do đ&oacute;, họ nhận được sự hỗ trợ rất lớn từ c&ocirc;ng ty mẹ ở nước sở tại trong việc cung cấp năng lực bảo hiểm để chấp nhận c&aacute;c rủi ro tại thị trường Campuchia. B&ecirc;n cạnh đ&oacute;, c&aacute;c DN n&agrave;y cũng đ&atilde; c&oacute; thời gian hoạt động kh&aacute; l&acirc;u tại Campuchia, n&ecirc;n hiểu r&otilde; đặc th&ugrave; rủi ro của thị trường, từ đ&oacute; c&oacute; được c&aacute;c điều kiện điều khoản ph&ugrave; hợp hơn.<br />\r\n	<br />\r\n	Đối với c&aacute;c DN Việt Nam, năng lực bảo hiểm chủ yếu phụ thuộc v&agrave;o c&aacute;c c&ocirc;ng ty t&aacute;i bảo hiểm nước ngo&agrave;i, đặc điểm thị trường mới n&ecirc;n rất kh&oacute; để thu xếp được chương tr&igrave;nh bảo hiểm/t&aacute;i bảo hiểm đủ sức cạnh tranh với c&aacute;c DN cũ, đặc biệt l&agrave; đối với những dự &aacute;n lớn.<br />\r\n	<br />\r\n	Thứ hai, về việc thiết lập hệ thống mạng lưới kinh doanh. Tại Việt Nam, 1 c&ocirc;ng ty bảo hiểm c&oacute; thể c&oacute; nhiều chi nh&aacute;nh v&agrave; c&oacute; mạng lưới đại l&yacute; rộng khắp cả nước. Việc khai th&aacute;c c&aacute;c dịch vụ b&aacute;n lẻ như xe cơ giới, con người chủ yếu dựa v&agrave;o mạng lưới n&agrave;y. Nhưng ở Campuchia, để được hoạt động với tư c&aacute;ch l&agrave; đại l&yacute; bảo hiểm, c&aacute; nh&acirc;n hoặc tổ chức phải được Bộ Kinh tế T&agrave;i ch&iacute;nh nước n&agrave;y cấp giấy ph&eacute;p hoạt động, phải đăng k&yacute; m&atilde; số thuế v&agrave; phải k&yacute; quỹ 10.000 USD tại Ng&acirc;n h&agrave;ng Trung ương Campuchia trong suốt thời gian hoạt động. Điều n&agrave;y sẽ hạn chế việc mở rộng mạng lưới đại l&yacute; của c&aacute;c c&ocirc;ng ty bảo hiểm, ảnh hưởng đến nguồn doanh thu từ hoạt động đại l&yacute; cho c&aacute;c DN bảo hiểm n&oacute;i ri&ecirc;ng v&agrave; doanh thu của to&agrave;n thị trường bảo hiểm n&oacute;i chung.<br />\r\n	<br />\r\n	Thứ ba, sự kh&aacute;c biệt về ng&ocirc;n ngữ v&agrave; tập qu&aacute;n kinh doanh cũng l&agrave; một kh&oacute; khăn kh&aacute; lớn cho c&aacute;c DN Việt Nam khi bắt đầu kinh doanh tại thị trường Campuchia. Sự kh&aacute;c biệt giữa hệ thống luật ph&aacute;p v&agrave; tập qu&aacute;n kinh doanh cũng y&ecirc;u cầu c&aacute;c DN Việt Nam phải c&oacute; sự nghi&ecirc;n cứu kỹ c&aacute;c văn bản ph&aacute;p luật c&oacute; li&ecirc;n quan, cũng như c&oacute; sự linh hoạt trong hoạt động cho ph&ugrave; hợp với đặc th&ugrave; của thị trường.<br />\r\n	<br />\r\n	<em><strong>CVI được trợ gi&uacute;p rất nhiều từ c&aacute;c mối quan hệ kh&aacute;ch h&agrave;ng của BIDV. Nếu kh&ocirc;ng c&oacute; sự trợ gi&uacute;p n&agrave;y th&igrave; sao, thưa &ocirc;ng?<br />\r\n	</strong></em><br />\r\n	Kh&ocirc;ng thể phủ nhận để c&oacute; những bước đi bắt đầu vững chắc như ng&agrave;y h&ocirc;m nay của CVI l&agrave; nhờ rất lớn v&agrave;o sự hậu thuẫn của BIDV v&agrave; sự hỗ trợ đắc lực về mặt nghiệp vụ bảo hiểm của BIC.<br />\r\n	<br />\r\n	CVI đ&atilde; nhận được sự ủng hộ rất lớn từ c&aacute;c mối quan hệ của BIDV, đ&oacute; l&agrave; những thuận lợi gi&uacute;p CVI c&oacute; nền tảng v&agrave; điểm tựa mạnh trong giai đoạn khởi đầu. Nhưng t&ocirc;i cho rằng, để tồn tại v&agrave; trụ vững tr&ecirc;n thị trường, đặc biệt l&agrave; khi cuộc cạnh tranh đang thực sự bắt đầu, th&igrave; điểm tựa tốt kh&ocirc;ng phải l&agrave; tất cả.<br />\r\n	<br />\r\n	Những dự &aacute;n của c&aacute;c NĐT Việt Nam v&agrave;o thị trường Campuchia th&ocirc;ng qua sự hỗ trợ của ng&acirc;n h&agrave;ng cũng mới chỉ ở giai đoạn đầu, chưa ph&aacute;t sinh doanh thu cho bảo hiểm. CVI đ&atilde; phải tự vận động t&igrave;m kiếm những kh&aacute;ch h&agrave;ng lớn, c&oacute; quan hệ với c&aacute;c cổ đ&ocirc;ng g&oacute;p vốn kh&aacute;c như H&atilde;ng h&agrave;ng kh&ocirc;ng Quốc gia Campuchia, C&ocirc;ng ty Cavifoods, Ng&acirc;n h&agrave;ng Canadian Bank&hellip; C&aacute;c kh&aacute;ch h&agrave;ng n&agrave;y đ&atilde; lựa chọn CVI v&agrave; đang tiếp tục t&iacute;n nhiệm CVI cho c&aacute;c hợp đồng bảo hiểm tiếp sau đ&oacute;. Ch&uacute;ng t&ocirc;i đang trưởng th&agrave;nh v&agrave; tự cạnh tranh bằng ch&iacute;nh năng lực v&agrave; chất lượng dịch vụ bảo hiểm của m&igrave;nh.</p>', '2010-12-05 00:00:00', '2010-12-05 00:00:00', 1, 0, 0);
INSERT INTO `articles` VALUES (23, 10, 1, 0, 'Tôm VN tiếp tục bị áp thuế', 'tom-vn-tiep-tuc-bi-ap-thue', 'Bộ Thương mại Mỹ (DOC) vừa công bố tiếp tục áp thuế chống bán phá giá đối với tôm nước ấm đông lạnh từ VN.', 'files/news/20101206/20101206214249.jpg', '<div align="justify">\r\n	<p>\r\n		<span class="p_te8" id="UcNewsDetail1_lblNoiDung" style="display: inline-block; width: 500px;">Theo kết quả cuối c&ugrave;ng của đợt xem x&eacute;t c&ocirc;ng bố cuối th&aacute;ng 11 (gọi l&agrave; r&agrave; so&aacute;t ho&agrave;ng h&ocirc;n) vừa qua, c&aacute;c doanh nghiệp trong danh s&aacute;ch chịu mức thuế chống b&aacute;n ph&aacute; gi&aacute; thấp nhất l&agrave; 4,3-5,24%, doanh nghiệp c&ograve;n lại chịu mức thuế suất to&agrave;n quốc l&agrave; 25,76%.<br />\r\n		<br />\r\n		L&yacute; do DOC tiếp tục &aacute;p thuế chống b&aacute;n ph&aacute; gi&aacute; với t&ocirc;m VN l&agrave; v&igrave; việc gỡ bỏ thuế chống ph&aacute; gi&aacute; đối với t&ocirc;m đ&ocirc;ng lạnh của VN c&oacute; thể dẫn tới việc t&aacute;i diễn t&igrave;nh trạng b&aacute;n ph&aacute; gi&aacute;.<br />\r\n		<br />\r\n		Theo quy định của Tổ chức Thương mại thế giới, sau mỗi năm năm kể từ khi đ&aacute;nh thuế chống b&aacute;n ph&aacute; gi&aacute;, cơ quan thẩm quyền của nước nhập khẩu phải tiến h&agrave;nh r&agrave; so&aacute;t ho&agrave;ng h&ocirc;n, tức l&agrave; đ&aacute;nh gi&aacute; v&agrave; x&aacute;c định liệu việc hủy bỏ thuế chống b&aacute;n ph&aacute; gi&aacute; đối với sản phẩm nhập khẩu c&oacute; ảnh hưởng hoặc g&acirc;y thiệt hại đến ng&agrave;nh sản xuất trong nước hay kh&ocirc;ng.<br />\r\n		<br />\r\n		Nếu x&eacute;t thấy h&agrave;ng h&oacute;a nhập khẩu kh&ocirc;ng hoặc chỉ g&acirc;y thiệt hại kh&ocirc;ng đ&aacute;ng kể th&igrave; phải hủy bỏ việc &aacute;p dụng thuế chống b&aacute;n ph&aacute; gi&aacute; đối với sản phẩm nhập khẩu đ&oacute;.<br />\r\n		<br />\r\n		Với quyết định mới n&agrave;y của DOC, VN lại mất năm năm nữa mới c&oacute; cơ hội được hủy bỏ thuế chống b&aacute;n ph&aacute; gi&aacute;, đồng thời vẫn phải tiếp tục vụ kiện Mỹ tại Tổ chức Thương mại thế giới. </span></p>\r\n	<p style="text-align: right;">\r\n		<span class="p_te8" id="UcNewsDetail1_lblNoiDung" style="display: inline-block; width: 500px;">(Theo TT)</span></p>\r\n</div>', '2010-12-06 00:00:00', '2010-12-06 00:00:00', 1, 0, 0);
INSERT INTO `articles` VALUES (24, 10, 1, 0, 'Tranh nhau mua xăng, dầu Dung Quất', 'tranh-nhau-mua-xang-dau-dung-quat', 'Do tình hình ngoại tệ khan hiếm, các doanh nghiệp kinh doanh xăng dầu trong nước đang xếp hàng để mua sản phẩm từ Nhà máy Lọc dầu Dung Quất.', 'files/news/20101206/20101206215203.jpg', '<p>\r\n	<span class="p_te8" id="UcNewsDetail1_lblNoiDung" style="display: inline-block; width: 500px;">So với thời điểm tồn kho cực đỉnh 256.000 tấn, hiện xăng dầu tại Dung Quất chỉ c&ograve;n khoảng 100.000 tấn trong kho. Con số n&agrave;y được coi l&agrave; mức tồn tối ưu của nh&agrave; m&aacute;y. Tổng gi&aacute;m đốc C&ocirc;ng ty B&igrave;nh Sơn - đơn vị tiếp nhận khai th&aacute;c v&agrave; kinh doanh dự &aacute;n Dung Quất - Nguyễn Ho&agrave;i Giang cho biết: &quot;Ch&uacute;ng t&ocirc;i đang vận h&agrave;nh nh&agrave; m&aacute;y với c&ocirc;ng suất 100%-105% mới đ&aacute;p ứng được nhu cầu ti&ecirc;u thụ trong nước&quot;.<br />\r\n	<br />\r\n	&Ocirc;ng Giang cho biết so với thời điểm sản phẩm tồn trong kho ở mức cao nhất, xăng dầu sản xuất tại Nh&agrave; m&aacute;y Dung Quất đ&atilde; ti&ecirc;u thụ được một nửa. Do vấn đề cung ứng ngoại tệ gặp kh&oacute; khăn n&ecirc;n rất nhiều doanh nghiệp đang xếp h&agrave;ng đặt mua xăng tại nh&agrave; m&aacute;y. Trong đ&oacute;, 3 đối t&aacute;c lớn nhất l&agrave; Petrolimex, PV Oil v&agrave; Petec.<br />\r\n	<br />\r\n	Theo &ocirc;ng Giang, việc khan hiếm ngoại tệ c&ugrave;ng với biến động tỷ gi&aacute; khiến nhu cầu ti&ecirc;u thụ xăng dầu từ Dung Quất sẽ tăng đột biến trong thời gian tới, thậm ch&iacute; c&oacute; thể dẫn tới căng thẳng nguồn cung.<br />\r\n	<br />\r\n	Ri&ecirc;ng xăng m&aacute;y bay ZA1, ph&iacute;a Dung Quất đang x&uacute;c tiến c&aacute;c thủ tục về ti&ecirc;u chuẩn để cung cấp sản phẩm cho nh&agrave; cung ứng xăng dầu h&agrave;ng kh&ocirc;ng theo đ&uacute;ng thỏa thuận đ&atilde; k&yacute;. Gi&aacute;m đốc C&ocirc;ng ty Xăng dầu H&agrave;ng kh&ocirc;ng Vinapco Trần Hữu Ph&uacute;c cho biết: &quot;Ch&uacute;ng t&ocirc;i lu&ocirc;n sẵn s&agrave;ng ti&ecirc;u thụ sản phẩm xăng dầu trong nước. Hợp đồng k&yacute; kết giữa Vinapco v&agrave; Dung Quất đ&atilde; k&yacute;, ch&uacute;ng t&ocirc;i chỉ chờ mỗi giấy chứng nhận hợp chuẩn&quot;.<br />\r\n	<br />\r\n	Theo &ocirc;ng Ph&uacute;c, trong bối cảnh căng thẳng ngoại tệ như hiện nay, việc mua sản phẩm trong nước l&agrave; lựa chọn tối ưu. Vấn đề biến động tỷ gi&aacute; theo đ&oacute; cũng sẽ được giải quyết.<br />\r\n	<br />\r\n	Hồi th&aacute;ng 10, dư luận bắt đầu ầm ĩ chuyện Nh&agrave; m&aacute;y Lọc dầu Dung Quất tồn h&agrave;ng trăm ngh&igrave;n tấn xăng dầu c&aacute;c loại, d&ugrave; rằng trong nước c&oacute; tới 11 nh&agrave; nhập khẩu v&agrave; ph&acirc;n phối. Tại thời điểm ấy, Bộ C&ocirc;ng Thương đ&atilde; phải nhiều lần tổ chức họp c&aacute;c b&ecirc;n để vận động doanh nghiệp trong nước chung tay ti&ecirc;u thụ sản phẩm xăng dầu nội địa. Đến đầu th&aacute;ng 11, bản thỏa thuận giữa Dung Quất với 4 nh&agrave; ti&ecirc;u thụ lớn gồm Petrolimex, Petec, PV Oil v&agrave; Vinapco được k&yacute; kết.<br />\r\n	<br />\r\n	Theo thỏa thuận k&yacute; kết, ph&iacute;a Petrolimex sẽ ti&ecirc;u thụ khoảng 2 triệu m3 sản phẩm cho Nh&agrave; m&aacute;y Lọc dầu Dung Quất, PV Oil 1,5 triệu m3, Petec 1 triệu m3, Vinapco 200.000 m3. Như vậy, tổng khối lượng m&agrave; 4 đơn vị n&agrave;y nhận ti&ecirc;u thụ l&agrave; 4,7 triệu m3 sản phẩm c&aacute;c loại, gồm xăng A92, A95, dầu diezel, xăng m&aacute;y bay Z1 v&agrave; dầu nhi&ecirc;n liệu FO. </span></p>', '2010-12-06 00:00:00', '2010-12-06 00:00:00', 1, 0, 0);
INSERT INTO `articles` VALUES (25, 10, 1, 0, 'Da giày với mục tiêu xuất khẩu 9,1 tỷ USD năm 2015', 'da-giay-voi-muc-tieu-xuat-khau-91-ty-usd-nam-2015', 'Năm 2010, ngành da giày Việt Nam đã có tốc độ tăng trưởng khá mạnh với kim ngạch xuất khẩu 10 tháng đạt 4,06 tỷ USD, gần bằng với kim ngạch cả năm 2009 và đạt mức tăng trưởng tới 24,8%, xếp hạng...', 'files/news/20101206/20101206215259.jpg', '<p>\r\n	<span class="p_te8" id="UcNewsDetail1_lblNoiDung" style="display: inline-block; width: 500px;">Chiến lược ph&aacute;t triển ng&agrave;nh gi&agrave;y Việt Nam vừa được Ban chấp h&agrave;nh Hiệp hội Da gi&agrave;y Việt Nam (Lefaso) x&acirc;y dựng nổi bật 2 nội dung lớn.<br />\r\n	<br />\r\n	Một l&agrave; chuyển từ thế chỉ sản xuất cho xuất khẩu sang thế c&acirc;n bằng giữa xuất khẩu v&agrave; ti&ecirc;u thụ nội địa. &Ocirc;ng Diệp Th&agrave;nh Kiệt, Ph&oacute; chủ tịch Hiệp hội ph&acirc;n t&iacute;ch, nh&igrave;n v&agrave;o bảng số liệu thống k&ecirc; 12 nước sản xuất gi&agrave;y trong khu vực ch&acirc;u &Aacute; năm 2009 của Tổ chức Satra, phần lớn c&aacute;c nước c&oacute; sự c&acirc;n bằng giữa sản xuất v&agrave; ti&ecirc;u thụ trong nước.<br />\r\n	<br />\r\n	Trong khi đ&oacute;, t&iacute;nh đến thời điểm hiện nay, tỷ lệ lượng gi&agrave;y d&eacute;p xuất khẩu so với tổng số sản xuất của Việt Nam l&agrave; 92,3%. Ngay cả những nước c&oacute; ho&agrave;n cảnh gần giống Việt Nam th&igrave; tỷ lệ xuất khẩu/ti&ecirc;u thụ nội địa thấp hơn rất nhiều, chẳng hạn như của Th&aacute;i Lan l&agrave; 42,5%, Indonesia 40,5%, Malaysia 37,9%, c&aacute; biệt c&oacute; Phillippines 5,4%, Pakistan 7,3%.<br />\r\n	<br />\r\n	Nội dung lớn thứ hai ch&iacute;nh l&agrave; phải nhanh ch&oacute;ng giảm tỷ lệ gia c&ocirc;ng đối với gi&agrave;y d&eacute;p xuất khẩu. Tỷ lệ n&agrave;y hiện đang được c&aacute;c chuy&ecirc;n gia đ&aacute;nh gi&aacute; l&agrave; rất cao, cụ thể l&agrave; tr&ecirc;n 50% trong cả ng&agrave;nh v&agrave; l&ecirc;n đến 70% đối với c&aacute;c doanh nghiệp c&oacute; vốn trong nước.<br />\r\n	<br />\r\n	Cũng theo Hiệp hội, ng&agrave;nh da gi&agrave;y Việt Nam trong qu&aacute; khứ đ&atilde; cho thấy lu&ocirc;n c&oacute; một sức bật rất tốt, con số giảm kim ngạch xuất khẩu 14,7% (đạt 4,07 tỷ USD năm 2009) vẫn được xem l&agrave; con số lạc quan so với dự b&aacute;o của nhiều chuy&ecirc;n gia l&agrave; phải giảm hơn 20%, khi bị c&aacute;c đ&ograve;n gi&aacute;ng từ việc t&aacute;i &aacute;p thuế chống b&aacute;n ph&aacute; gi&aacute; đối với gi&agrave;y mũ da v&agrave; việc b&atilde;i bỏ ch&iacute;nh s&aacute;ch ưu đ&atilde;i thuế quan (GSP) từ EU, vốn l&agrave; thị trường của gần 60% năng lực xuất khẩu qua nhiều năm.<br />\r\n	<br />\r\n	Tuy nhi&ecirc;n, từ 6 th&aacute;ng cuối năm 2009 đến nay, nhiều l&ocirc; h&agrave;ng gi&agrave;y d&eacute;p xuất khẩu từ Việt Nam đ&atilde; được chuyển hướng sang thị trường Hoa Kỳ, v&agrave; việc xuất khẩu v&agrave;o EU tuy c&oacute; giảm, nhưng Việt Nam vẫn l&agrave; nước sản xuất lớn thứ 3 ở ch&acirc;u &Aacute; v&agrave; thứ 4 tr&ecirc;n to&agrave;n thế giới (sau Brazil).<br />\r\n	<br />\r\n	C&ograve;n về xuất khẩu, kể cả tại ch&acirc;u &Aacute; v&agrave; tr&ecirc;n thế giới, ng&agrave;nh gi&agrave;y Việt Nam chỉ đứng sau Trung Quốc v&agrave; với sản lượng gấp 2,5 lần nước xuất khẩu thứ 3 (Italia). Hiện nay, cứ 100 đ&ocirc;i gi&agrave;y được sản xuất tr&ecirc;n thế giới th&igrave; c&oacute; 4,14 đ&ocirc;i mang nh&atilde;n &ldquo;Made in Vietnam&rdquo;.<br />\r\n	<br />\r\n	Ng&agrave;y 25/11, Bộ trưởng Bộ C&ocirc;ng Thương đ&atilde; k&yacute; Quyết định số 6209/QĐ-BCT ph&ecirc; duyệt Quy hoạch tổng thể ph&aacute;t triển ng&agrave;nh da - gi&agrave;y Việt Nam đến năm 2020, tầm nh&igrave;n đến năm 2025. Mục ti&ecirc;u chung ph&aacute;t triển đến năm 2020 l&agrave; x&acirc;y dựng ng&agrave;nh da gi&agrave;y trở th&agrave;nh một ng&agrave;nh c&ocirc;ng nghiệp xuất khẩu mũi nhọn quan trọng của nền kinh tế, tiếp tục giữ vị tr&iacute; trong nh&oacute;m c&aacute;c nước sản xuất v&agrave; xuất khẩu c&aacute;c sản phẩm da gi&agrave;y h&agrave;ng đầu thế giới v&agrave; tạo th&ecirc;m nhiều việc l&agrave;m cho x&atilde; hội tr&ecirc;n cơ sở thu nhập của người lao động ng&agrave;y c&agrave;ng được n&acirc;ng cao, thực hiện tr&aacute;ch nhiệm x&atilde; hội ng&agrave;y c&agrave;ng tốt, số lượng lao động được qua đ&agrave;o tạo ng&agrave;y c&agrave;ng tăng.<br />\r\n	<br />\r\n	Với tốc độ tăng trưởng dự kiến, kim ngạch xuất khẩu của to&agrave;n ng&agrave;nh năm 2015 l&agrave; 9,1 tỷ USD, năm 2020 l&agrave; 14,5 tỷ USD v&agrave; năm 2025 đạt 21 tỷ USD. Đồng thời, n&acirc;ng dần tỷ lệ nội địa ho&aacute; c&aacute;c loại sản phẩm l&agrave; một trong những vấn đề được quan t&acirc;m đặc biệt trong qu&aacute; tr&igrave;nh x&acirc;y dựng Quy hoạch trong giai đoạn 2020, tầm nh&igrave;n 2025, trong đ&oacute; phấn đấu năm 2015 tỷ lệ nội địa ho&aacute; đạt 60 - 65%, năm 2020 đạt 75 - 80 % v&agrave; năm 2025 đạt 80 - 85%.<br />\r\n	<br />\r\n	Trong Quy hoạch, gi&agrave;y d&eacute;p vẫn l&agrave; sản phẩm chủ lực của ng&agrave;nh, song sẽ quan t&acirc;m đến việc sản xuất giầy d&eacute;p da thời trang v&agrave; cặp - t&uacute;i - v&iacute; chất lượng cao phục vụ thị trường mới, thị trường cao cấp v&agrave; thị trường nội địa. Đối với sản phẩm da thuộc, ng&agrave;nh sẽ tập trung sản xuất da thuộc với c&ocirc;ng nghệ ti&ecirc;n tiến, th&acirc;n thiện với m&ocirc;i trường. Đầu tư sản xuất da thuộc được gắn liền với việc ph&aacute;t triển đ&agrave;n gia s&uacute;c g&oacute;p phần giảm nhập si&ecirc;u v&agrave; chủ động trong sản xuất.<br />\r\n	<br />\r\n	Theo quyết định tr&ecirc;n, đến năm 2020, tầm nh&igrave;n đến năm 2025, ng&agrave;nh da giầy Việt Nam sẽ ph&aacute;t triển tr&ecirc;n cơ sở ph&ugrave; hợp c&aacute;c quy định hiện h&agrave;nh về c&ocirc;ng t&aacute;c quy hoạch. To&agrave;n ng&agrave;nh vẫn duy tr&igrave; định hướng chủ động phục vụ xuất khẩu v&agrave; chiếm lĩnh dần thị trường nội địa, ph&aacute;t triển mạnh sản xuất nguy&ecirc;n phụ liệu v&agrave; c&ocirc;ng nghiệp hỗ trợ, tham gia s&acirc;u v&agrave;o chuỗi gi&aacute; trị gia tăng của thị trường sản phẩm da giầy thế giới. Ph&aacute;t triển ng&agrave;nh da gi&agrave;y Việt Nam nhằm tạo ra năng suất lao động v&agrave; hiệu quả kinh tế cao, chủ động hội nhập kinh tế với khu vực v&agrave; thế giới.<br />\r\n	<br />\r\n	Điểm mới của quy hoạch giai đoạn đến năm 2020, tầm nh&igrave;n đến năm 2025 l&agrave; việc quan t&acirc;m đến việc năng cao khả năng thiết kế mẫu m&atilde; sản phẩm v&agrave; ph&aacute;t triển nguồn nh&acirc;n lực trong lĩnh vực da giầy n&oacute;i chung v&agrave; thời trang n&oacute;i ri&ecirc;ng. Một số khu, cụm c&ocirc;ng nghiệp sản xuất da giầy sẽ được x&acirc;y dựng để sản xuất nguy&ecirc;n phụ liệu v&agrave; xử l&yacute; m&ocirc;i trường tập trung tr&ecirc;n cơ sở lợi thế về hạ tầng v&agrave; lao động để chủ động cung cấp nguy&ecirc;n phụ liệu, n&acirc;ng cao năng lực cạnh tranh cho ng&agrave;nh v&agrave; x&acirc;y dựng mới v&agrave; ph&aacute;t triển c&aacute;c cơ sở đ&agrave;o tạo, c&aacute;c cơ sở nghi&ecirc;n cứu khoa học c&ocirc;ng nghệ, c&aacute;c trung t&acirc;m kiểm định, dịch vụ ng&agrave;nh v&agrave; c&aacute;c trung t&acirc;m x&uacute;c tiến thương mại, trung t&acirc;m thời trang ở trong nước v&agrave; nước ngo&agrave;i l&agrave; những định hướng c&oacute; t&iacute;nh l&acirc;u d&agrave;i nhằm ph&aacute;t triển ng&agrave;nh theo hướng ổn định v&agrave; bền vững.<br />\r\n	<br />\r\n	Với quy hoạch theo v&ugrave;ng l&atilde;nh thổ, bố tr&iacute; sản xuất v&agrave; đầu tư của ng&agrave;nh da gi&agrave;y tr&ecirc;n to&agrave;n quốc được x&aacute;c định th&agrave;nh 4 v&ugrave;ng chủ yếu gồm v&ugrave;ng Đồng bằng s&ocirc;ng Hồng, v&ugrave;ng Đ&ocirc;ng Nam Bộ, v&ugrave;ng Bắc Trung Bộ v&agrave; Duy&ecirc;n hải miền Trung, v&ugrave;ng đồng bằng S&ocirc;ng Cửu Long.</span></p>', '2010-12-06 00:00:00', '2010-12-06 00:00:00', 1, 0, 0);
INSERT INTO `articles` VALUES (26, 10, 1, 0, 'Giảm mạnh thuế nhập khẩu xăng dầu', 'giam-manh-thue-nhap-khau-xang-dau', 'Ngày 1/12, Bộ Tài chính ban hành Thông tư số 190/2010/TT-BTC hướng dẫn thực hiện mức thuế suất thuế nhập khẩu ưu đãi đối với một số mặt hàng xăng dầu thuộc nhóm 2710 tại Biểu thuế nhập khẩu ưu đãi.', 'files/news/20101206/20101206215350.jpg', '<p>\r\n	Theo đ&oacute;, mức thuế suất thuế nhập khẩu ưu đ&atilde;i đối với một số mặt h&agrave;ng xăng, dầu thuộc nh&oacute;m 2710 thực hiện theo quy định tại th&ocirc;ng tư n&agrave;y, b&atilde;i bỏ quy định về mức thuế suất thuế nhập khẩu ưu đ&atilde;i đối với một số mặt h&agrave;ng xăng, dầu thuộc nh&oacute;m 2710 quy định tại Th&ocirc;ng tư 184/2010/TT-BTC ng&agrave;y 15/11/2010 của Bộ T&agrave;i ch&iacute;nh quy định mức thuế suất của Biểu thuế xuất khẩu, Biểu thuế nhập khẩu ưu đ&atilde;i theo danh mục mặt h&agrave;ng chịu thuế.<br />\r\n	<br />\r\n	Mức thuế nhập khẩu c&aacute;c mặt h&agrave;ng xăng theo th&ocirc;ng tư mới được giảm từ 17% xuống c&ograve;n 12%; nhiều mặt h&agrave;ng dầu v&agrave; diesel giảm từ 10% xuống c&ograve;n 5%.<br />\r\n	<br />\r\n	Th&ocirc;ng tư n&agrave;y c&oacute; hiệu lực thi h&agrave;nh kể từ ng&agrave;y 1/12/2010 v&agrave; thay thế Th&ocirc;ng tư số 59/2010/TT-BTC ng&agrave;y 19/4/2010 của Bộ T&agrave;i ch&iacute;nh hướng dẫn thực hiện mức thuế suất thuế nhập khẩu ưu đ&atilde;i đối với một số mặt h&agrave;ng thuộc nh&oacute;m 2710 tại Biểu thuế nhập khẩu ưu đ&atilde;i.<br />\r\n	<br />\r\n	Kể từ ng&agrave;y 1/1/2011, mức thuế suất thuế nhập khẩu ưu đ&atilde;i đối với một số mặt h&agrave;ng xăng, dầu thuộc nh&oacute;m 2710 tiếp tục thực hiện theo quy định tại th&ocirc;ng tư n&agrave;y.</p>\r\n<p style="text-align: right;">\r\n	(Theo vneconomy</p>', '2010-12-06 00:00:00', '2010-12-06 00:00:00', 1, 0, 0);
INSERT INTO `articles` VALUES (27, 10, 1, 0, 'Có thể ''''thả'''' giá điện, than trong năm 2011', 'co-the-tha-gia-dien-than-trong-nam-2011', 'Lộ trình điều chỉnh giá điện, than, khí, nước sạch... theo cơ chế thị trường có thể được thực hiện vào năm 2011, theo đề án điều hành giá đang được Bộ Tài chính xây dựng.', 'files/news/20101206/20101206215438.jpg', '<div align="justify">\r\n	<p style="text-align: justify;">\r\n		<span class="p_te8" id="UcNewsDetail1_lblNoiDung" style="display: inline-block; width: 500px;">Theo Bộ T&agrave;i ch&iacute;nh, năm 2011 c&oacute; rất nhiều yếu tố kh&aacute;ch quan t&aacute;c động tới mặt bằng gi&aacute; như khủng hoảng nợ c&ocirc;ng của khối EU, tỷ gi&aacute;, l&atilde;i suất biến động, thương mại to&agrave;n cầu thấp, thi&ecirc;n tai dịch bệnh diễn biến phức tạp...<br />\r\n		<br />\r\n		Tại thị trường trong nước, kinh tế Việt Nam phục hồi nhanh khiến nhu cầu nguy&ecirc;n vật liệu cao để phục vụ tăng trưởng. B&ecirc;n cạnh đ&oacute;, những yếu k&eacute;m vốn c&oacute; của nền kinh tế chưa được khắc phục triệt để như chất lượng tăng trưởng, hiệu quả sử dụng vốn... Điều n&agrave;y tiềm ẩn những yếu tố g&acirc;y lạm ph&aacute;t cao v&agrave; bất ổn kinh tế vĩ m&ocirc;.<br />\r\n		<br />\r\n		Do vậy, Bộ T&agrave;i ch&iacute;nh đề xuất 5 giải ph&aacute;p điều h&agrave;nh gi&aacute; cả năm 2011 trong đ&oacute; c&oacute; việc thực hiện lộ tr&igrave;nh gi&aacute; thị trường đối với một số h&agrave;ng h&oacute;a dịch vụ m&agrave; nh&agrave; nước c&ograve;n định gi&aacute; như điện, than, nước sinh hoạt, gi&aacute; thuốc... Đối với mặt h&agrave;ng xăng dầu, cơ quan n&agrave;y sẽ &aacute;p dụng c&aacute;c c&ocirc;ng cụ linh hoạt để điều h&agrave;nh như sử dụng thuế, quỹ b&igrave;nh ổn v&agrave; ph&iacute;...<br />\r\n		<br />\r\n		Trong phương &aacute;n điều h&agrave;nh gi&aacute; của m&igrave;nh, Bộ T&agrave;i ch&iacute;nh đề xuất cần chủ động thực hiện lộ tr&igrave;nh điều chỉnh gi&aacute; một số mặt h&agrave;ng thiết yếu như than, điện v&agrave;o thời điểm hợp l&yacute;. Lộ tr&igrave;nh n&agrave;y phải được gắn với hệ thống cung ứng h&agrave;ng h&oacute;a, dịch vụ ho&agrave;n chỉnh, giảm chi ph&iacute; sản xuất v&agrave; gi&aacute; th&agrave;nh. Việc &quot;thả&quot; gi&aacute; theo thị trường n&agrave;y phải gắn với cơ chế trợ gi&uacute;p hợp l&yacute; đối với c&aacute;c hộ ti&ecirc;u d&ugrave;ng c&oacute; điều kiện kh&oacute; khăn, đối tượng ch&iacute;nh s&aacute;ch...<br />\r\n		<br />\r\n		Cục trưởng Cục Quản l&yacute; Gi&aacute; - Nguyễn Tiến Thỏa khẳng định từ nay đến cuối năm sẽ tiếp tục giữ ổn định gi&aacute; nhiều nh&oacute;m mặt h&agrave;ng quan trọng, trong đ&oacute; c&oacute; than, điện. Tuy nhi&ecirc;n, năm 2011, gi&aacute; những mặt h&agrave;ng như điện, than... sẽ c&oacute; sự điều chỉnh ph&ugrave; hợp. Thời gian n&agrave;o, c&aacute;c mức điều chỉnh ra sao sẽ được c&aacute;c cơ quan chức năng t&iacute;nh to&aacute;n kỹ để t&aacute;c động thấp nhất đến người ti&ecirc;u d&ugrave;ng. B&ecirc;n cạnh đ&oacute;, Bộ cũng thực hiện một loạt c&aacute;c biện ph&aacute;p kiểm so&aacute;t yếu tố h&igrave;nh th&agrave;nh gi&aacute; v&agrave; y&ecirc;u cầu ni&ecirc;m yết c&ocirc;ng khai gi&aacute; nhiều nh&oacute;m h&agrave;ng h&oacute;a.<br />\r\n		<br />\r\n		Hiện nay, Tập đo&agrave;n Than v&agrave; Kho&aacute;ng sản VN (Vinacomin) đ&atilde; c&oacute; văn bản đề xuất tăng gi&aacute; b&aacute;n than cho 4 hộ ti&ecirc;u thụ lớn như giấy, ph&acirc;n b&oacute;n, điện v&agrave; xi măng từ năm 2011.<br />\r\n		<br />\r\n		Ri&ecirc;ng đối với gi&aacute; than b&aacute;n cho điện, tập đo&agrave;n n&agrave;y đề nghị được điều chỉnh theo 2 bước để t&aacute;c động thấp nhất đến việc tăng gi&aacute; điện. Vinacomin ước t&iacute;nh phương &aacute;n tăng gi&aacute; than sẽ t&aacute;c động tới gi&aacute; điện khoảng 2,4-2,6%.<br />\r\n		<br />\r\n		Kh&ocirc;ng chỉ gi&aacute; than, Tập đo&agrave;n Điện lực VN (EVN) cũng đang x&acirc;y dựng gi&aacute; b&aacute;n điện mới dự kiến &aacute;p dụng theo lộ tr&igrave;nh điện canh tranh năm 2011. Tuy nhi&ecirc;n, theo &ocirc;ng Thỏa thời điểm n&agrave;y rất nhạy cảm n&ecirc;n bất cứ phương &aacute;n đề xuất điều chỉnh gi&aacute; mặt h&agrave;ng n&agrave;o cũng phải được c&acirc;n nhắc thận trọng v&agrave; phải được t&iacute;nh to&aacute;n đầy đủ c&aacute;c yếu tố t&aacute;c động c&oacute; thể xảy ra.</span></p>\r\n	<p style="text-align: justify;">\r\n		<span class="p_te8" id="UcNewsDetail1_lblNoiDung" style="display: inline-block; width: 500px;">&nbsp;</span></p>\r\n	<p style="text-align: right;">\r\n		<span class="p_te8" id="UcNewsDetail1_lblNoiDung" style="display: inline-block; width: 500px;">(Theo VNE)</span></p>\r\n</div>', '2010-12-06 00:00:00', '2010-12-06 00:00:00', 1, 1, 0);
INSERT INTO `articles` VALUES (28, 10, 1, 0, 'Mô hình tăng trưởng mới cho VN', 'mo-hinh-tang-truong-moi-cho-vn', 'Lần đầu tiên một bản báo cáo năng lực cạnh tranh quốc gia của Việt Nam được thực hiện và công bố, dưới sự giúp đỡ của GS Michael E.Porter, cha đẻ của thuyết chiến lược cạnh tranh.', 'files/news/20101206/20101206215601.jpg', '<p>\r\n	B&aacute;o c&aacute;o cho thấy bộ mặt to&agrave;n diện của nền kinh tế VN sau 20 năm với những lợi thế, hạn chế tồn tại nhằm x&acirc;y dựng m&ocirc; h&igrave;nh tăng trưởng mới dựa tr&ecirc;n sức mạnh cạnh tranh, định vị thương hiệu quốc gia, ph&aacute;t triển một c&aacute;ch bền vững hơn.<br />\r\n	<br />\r\n	<strong> Nh&acirc;n c&ocirc;ng rẻ kh&ocirc;ng c&ograve;n l&agrave; lợi thế</strong><br />\r\n	<br />\r\n	Gi&aacute;o sư Porter đ&aacute;nh gi&aacute;, hơn 2 thập ni&ecirc;n mở cửa nền kinh tế theo thị trường, VN đạt được sức tăng trưởng ấn tượng nhưng cũng đang bộc lộ r&otilde; những hạn chế. Cụ thể, d&ugrave; tăng trưởng ở mức cao nhưng mức độ thịnh vượng so với c&aacute;c nước trong khu vực như Ấn Độ, Trung Quốc&hellip; c&ograve;n thấp. Nền kinh tế vốn chỉ dựa v&agrave;o nh&acirc;n c&ocirc;ng đ&ocirc;ng đảo, chi ph&iacute; rẻ tạo năng suất kh&ocirc;ng cao. Xuất khẩu được chọn l&agrave;m lĩnh vực mũi nhọn nhưng do cơ cấu thiếu hợp l&yacute;, d&agrave;n trải v&agrave; chủ yếu gia c&ocirc;ng n&ecirc;n gi&aacute; trị gia tăng &iacute;t. Thị phần xuất khẩu lớn của Việt Nam tập trung trong c&aacute;c ng&agrave;nh sử dụng nhiều lao động v&agrave; t&agrave;i nguy&ecirc;n thi&ecirc;n nhi&ecirc;n. Li&ecirc;n kết trực tiếp giữa c&aacute;c ng&agrave;nh xuất khẩu n&agrave;y hầu như kh&ocirc;ng c&oacute;. Việt Nam vốn được c&aacute;c nh&agrave; đầu tư lựa chọn chủ yếu do chi ph&iacute; nh&acirc;n c&ocirc;ng thấp, nay đ&atilde; kh&ocirc;ng c&ograve;n hấp dẫn.<br />\r\n	<br />\r\n	Ngo&agrave;i ra, tăng trưởng được dựa tr&ecirc;n nền tảng của việc thu h&uacute;t FDI nhằm thay đổi chuyển dịch cơ cấu nền kinh tế. Lượng vốn FDI năm 1988 chỉ v&agrave;i chục triệu USD, hiện tại l&ecirc;n tới h&agrave;ng chục tỉ USD, đỉnh cao l&agrave; năm 2008 vốn đăng k&yacute; hơn 60 tỉ USD. Tuy nhi&ecirc;n, theo GS Porter hiện vẫn đang c&oacute; khoảng c&aacute;ch lớn giữa FDI c&ocirc;ng bố v&agrave; con số thực tế. Khoảng c&aacute;ch n&agrave;y đang ng&agrave;y c&agrave;ng rộng ra khiến việc chuyển dịch cơ cấu kh&ocirc;ng hiệu quả. Nh&igrave;n từ thực tiễn những năm qua, &ocirc;ng b&agrave;y tỏ lo ngại khi Việt Nam đổ qu&aacute; nhiều vốn v&agrave;o bất động sản m&agrave; chưa ch&uacute; trọng tới việc n&acirc;ng cao chất lượng c&aacute;c ng&agrave;nh mũi nhọn, chủ lực.<br />\r\n	<br />\r\n	B&ecirc;n cạnh đ&oacute;, c&aacute;c d&ograve;ng vốn lớn đổ v&agrave;o thị trường trong những năm qua đ&atilde; k&iacute;ch th&iacute;ch tăng cầu nội địa. C&ugrave;ng ch&iacute;nh s&aacute;ch kh&aacute; mở của tiền tệ v&agrave; t&agrave;i kh&oacute;a khiến lạm ph&aacute;t lu&ocirc;n ở mức cao trong khu vực. Số liệu b&aacute;o c&aacute;o cho thấy, so với Th&aacute;i Lan,&nbsp;&nbsp;&nbsp; Indonesia, Malaysia, Trung Quốc kể từ năm 2000 đến nay mức lạm ph&aacute;t của Việt Nam lu&ocirc;n đứng ở mức rất cao, đặc biệt trong 2008, l&ecirc;n tới hơn 20%.<br />\r\n	<br />\r\n	Những hạn chế tr&ecirc;n của nền kinh tế khiến chất lượng tăng trưởng kh&ocirc;ng theo c&ugrave;ng tốc độ, mức độ thịnh vượng chung trong khu vực c&ograve;n thấp, tham nhũng vẫn ở mức cao. Cụ thể, theo thống k&ecirc; tiền lương trung b&igrave;nh của VN chỉ ở mức 48,72 USD/th&aacute;ng, trong khi Nhật Bản gần 2.000 USD/th&aacute;ng, Singapore hơn 1.000 USD/th&aacute;ng, Th&aacute;i Lan 156 USD/th&aacute;ng&hellip;<br />\r\n	<br />\r\n	Theo GS Porter, Việt Nam cần c&oacute; một bước đột ph&aacute; mạnh mẽ, đột ph&aacute; từ năng lực cạnh tranh, tạo ra một m&ocirc; h&igrave;nh tăng trưởng mới chắc chắn, ổn định, bền vững. Cần tạo dựng được c&aacute;c lợi thế mới, đặc trưng thay v&igrave; chỉ đơn thuần sử dụng t&agrave;i nguy&ecirc;n sẵn c&oacute;.<br />\r\n	<br />\r\n	<strong> Th&agrave;nh lập Hội đồng năng lực cạnh tranh quốc gia</strong><br />\r\n	<br />\r\n	Theo GS Porter, để c&oacute; một m&ocirc; h&igrave;nh tăng trưởng mới, Việt Nam cần th&agrave;nh lập Hội đồng năng lực cạnh tranh quốc gia để thực thi chiến lược kinh tế mới. Cụ thể, Việt Nam c&oacute; thể học hỏi m&ocirc; h&igrave;nh của H&agrave;n Quốc, Colombia v&agrave; c&aacute;c quốc gia kh&aacute;c để th&agrave;nh lập hội đồng bao gồm những th&agrave;nh vi&ecirc;n trực thuộc bộ, ng&agrave;nh của Ch&iacute;nh phủ, do người đứng đầu Ch&iacute;nh phủ trực tiếp l&agrave;m chủ tịch hội đồng. C&aacute;c th&agrave;nh vi&ecirc;n l&agrave; những người c&oacute; nhiệt huyết, lu&ocirc;n nghĩ tới lợi &iacute;ch của quốc gia. Th&agrave;nh vi&ecirc;n hội đồng n&ecirc;n c&oacute; đại diện của khu vực kinh tế tư nh&acirc;n - đ&oacute;ng vai tr&ograve; l&agrave;m đầu t&agrave;u đưa nền kinh tế tăng trưởng, ph&aacute;t triển.<br />\r\n	<br />\r\n	Theo GS Porter, để theo đuổi m&ocirc; h&igrave;nh tăng trưởng mới dựa tr&ecirc;n năng suất, chất lượng cao Việt Nam cần phải hy sinh những lợi &iacute;ch ngắn hạn trước mắt. Đơn cử như loại bỏ c&aacute;c lĩnh vực, ng&agrave;nh lương thấp v&igrave; lương thấp cũng đồng nghĩa năng suất thấp, chất lượng kh&ocirc;ng cao. Ngo&agrave;i ra trong xuất khẩu phải tạo được sự gắn kết chặt chẽ giữa c&aacute;c ng&agrave;nh c&oacute; thể bổ trợ cho nhau gia tăng gi&aacute; trị xuất khẩu. Cần thiết th&iacute; điểm th&agrave;nh lập những cụm ng&agrave;nh c&ocirc;ng nghiệp như cụm ng&agrave;nh điện tử v&agrave; cơ kh&iacute; ở H&agrave; Nội, c&aacute;c tỉnh l&acirc;n cận; cụm ng&agrave;nh dệt may, Logistics ở khu vực TP.HCM v&agrave; l&acirc;n cận hay chế biến n&ocirc;ng sản ở đồng bằng s&ocirc;ng Cửu Long...<br />\r\n	<br />\r\n	Đ&aacute;nh gi&aacute; về m&ocirc; h&igrave;nh tăng trưởng mới, Bộ trưởng - Chủ nhiệm Văn ph&ograve;ng Ch&iacute;nh phủ Nguyễn Xu&acirc;n Ph&uacute;c cho rằng, m&ocirc; h&igrave;nh tr&ecirc;n đ&atilde; bổ sung, hỗ trợ cho chiến lược kinh tế của Việt Nam; Cụ thể h&oacute;a c&aacute;c kiến nghị về ch&iacute;nh s&aacute;ch ưu ti&ecirc;n của quốc gia. Ngo&agrave;i ra, cũng đưa ra được một thứ tự thực hiện nhiệm vụ ưu ti&ecirc;n rất r&otilde; r&agrave;ng về kinh tế vĩ m&ocirc;, về mục ti&ecirc;u ngắn hạn, d&agrave;i hạn, về năng lực vi m&ocirc;... &Ocirc;ng cũng cho rằng cần thiết ủng hộ m&ocirc; h&igrave;nh Hội đồng năng lực cạnh tranh quốc gia v&igrave; đ&acirc;y l&agrave; cơ quan điều phối c&aacute;c dự &aacute;n, đ&aacute;nh gi&aacute; cơ chế triển khai về năng lực cạnh tranh nền kinh tế.</p>\r\n<p style="text-align: right;">\r\n	(Theo TN)</p>', '2010-12-06 00:00:00', '2010-12-06 00:00:00', 1, 1, 0);
INSERT INTO `articles` VALUES (29, 10, 1, 0, 'Doanh nghiệp khát vốn cuối năm', 'doanh-nghiep-khat-von-cuoi-nam', 'Tỷ giá đôla biến động, lãi suất cho vay tăng cao khiến doanh nghiệp đứng ngồi không yên vì thiếu vốn. Nhiều doanh nghiệp kiến nghị, ngân hàng cần mở rộng hầu bao và đưa ra ra các chính sách hỗ trợ.', 'files/news/20101206/20101206215715.jpg', '<div align="justify">\r\n	<p style="text-align: justify;">\r\n		<span class="p_te8" id="UcNewsDetail1_lblNoiDung" style="display: inline-block; width: 500px;">Tại cuộc họp giao ban về t&igrave;nh h&igrave;nh xuất nhập khẩu do Bộ C&ocirc;ng Thương tổ chức chiều 29/11, đại diện của một doanh nghiệp xăng dầu cho biết, c&ocirc;ng ty &ocirc;ng đang phải đối mặt với nguy cơ lỗ nặng. Doanh nghiệp kinh doanh xăng dầu n&oacute;i chung đang chịu rất nhiều kh&oacute; khăn.<br />\r\n		<br />\r\n		Theo b&aacute;o c&aacute;o ng&agrave;y 26/11 gửi Bộ T&agrave;i ch&iacute;nh của c&ocirc;ng ty n&agrave;y, xăng lỗ 1.685 đồng mỗi l&iacute;t, diezel lỗ 1.500 đồng, dầu hỏa lỗ 1.740 đồng mỗi l&iacute;t... Mỗi ng&agrave;y doanh nghiệp xăng dầu lỗ đến h&agrave;ng chục tỷ đồng v&agrave; con số n&agrave;y l&ecirc;n đến hơn một ngh&igrave;n tỷ đồng mỗi th&aacute;ng.<br />\r\n		<br />\r\n		&ldquo;Ch&uacute;ng t&ocirc;i đ&atilde; li&ecirc;n tục c&oacute; văn bản gửi tới c&aacute;c bộ ng&agrave;nh từ 10/9 đến nay để xin hướng giải quyết. Đ&acirc;y l&agrave; những nguy cơ thực sự chứ kh&ocirc;ng phải nguy cơ dự b&aacute;o&rdquo;, vị ph&oacute; tổng gi&aacute;m đốc lo ngại.<br />\r\n		<br />\r\n		Mặt h&agrave;ng thủy sản xuất khẩu cũng đang gặp kh&oacute; khăn về nguy&ecirc;n liệu đầu v&agrave;o v&agrave; h&agrave;ng r&agrave;o kỹ thuật từ thị trường nhập khẩu. T&ocirc;m vẫn l&agrave; mặt h&agrave;ng xuất khẩu chủ lực chiếm tỷ trọng lớn nhất với hơn 41% trong danh mục c&aacute;c sản phẩm thủy sản xuất khẩu ch&iacute;nh của Việt Nam 11 th&aacute;ng năm 2010 nhưng đang bị cạnh tranh khốc liệt ở thị trường Nhật Bản. C&aacute; tra đứng thứ 2 nhưng xuất khẩu trong 11 chỉ tăng nhẹ so với năm ngo&aacute;i.<br />\r\n		<br />\r\n		&Ocirc;ng Nguyễn Ho&agrave;i Nam, Ph&oacute; Tổng thư k&yacute; Hiệp hội Chế biến v&agrave; Xuất khẩu thủy sản Việt Nam (VASEP) cho biết, xuất khẩu c&aacute; tra đang c&oacute; dấu hiệu chững lại do nhiều chủ nu&ocirc;i c&aacute; ở đồng bằng s&ocirc;ng Cửu Long đang gặp kh&oacute; khăn về vốn. Theo &ocirc;ng Nam, tỷ gi&aacute; đ&ocirc;la biến động v&agrave; gi&aacute; nguy&ecirc;n liệu đầu v&agrave;o tăng cao khiến nhiều doanh nghiệp đ&oacute;i vốn buộc phải &ldquo;bỏ ao&rdquo;.<br />\r\n		<br />\r\n		Nguồn cung c&agrave; ph&ecirc; thế giới đang thiếu hụt khiến gi&aacute; c&agrave; ph&ecirc; xuất khẩu c&oacute; xu hướng tăng. Gi&aacute; tăng cao đ&atilde; khuyến kh&iacute;ch b&agrave; con n&ocirc;ng d&acirc;n mạnh dạn vay vốn ng&acirc;n h&agrave;ng, song kh&ocirc;ng phải l&uacute;c n&agrave;o ng&acirc;n h&agrave;ng cũng mở rộng hầu bao. &Ocirc;ng Đo&agrave;n Triệu Nhạn, Ph&oacute; chủ tịch Hiệp hội C&agrave; ph&ecirc; Ca cao Việt Nam kiến nghị, ng&acirc;n h&agrave;ng cần cởi mở hơn để ưu đ&atilde;i cho n&ocirc;ng d&acirc;n vay vốn mua trang thiết bị nhằm cải thiện t&igrave;nh h&igrave;nh trồng trọt. Ngo&agrave;i ra doanh nghiệp c&oacute; thể dự trữ h&agrave;ng để đảm bảo b&igrave;nh ổn gi&aacute; cả khi gi&aacute; c&agrave; ph&ecirc; biến động.<br />\r\n		<br />\r\n		Kh&ocirc;ng chỉ lo thiếu vốn, nhiều c&ocirc;ng ty trong nước c&ograve;n phải đau đầu đối mặt với sự cạnh tranh khốc liệt từ doanh nghiệp ngoại. &Ocirc;ng Nguyễn Tiến Nghi, Ph&oacute; chủ tịch Hiệp hội Th&eacute;p Việt Nam phấn khởi đưa con số xuất khẩu ước t&iacute;nh trong năm 2010 đạt kỷ lục 1,1 triệu tấn, tăng xấp xỉ 300% so với năm 2009.<br />\r\n		<br />\r\n		Chưa kịp vui mừng, Hiệp hội Th&eacute;p đ&atilde; phải giật m&igrave;nh lo đối ph&oacute; với th&eacute;p ngoại đang gi&agrave;nh giật thị phần trong nước. Theo &ocirc;ng Nghi, trong 10 th&aacute;ng đầu năm, lượng th&eacute;p ASEAN v&agrave;o Việt Nam đ&atilde; l&ecirc;n tới 43.000 tấn tăng 6.000 tấn so với c&ugrave;ng kỳ năm ngo&aacute;i. &ldquo;Th&eacute;p ASEAN chịu thuế suất 0% đang ồ ạt v&agrave;o Việt Nam v&agrave; cạnh tranh mạnh với doanh nghiệp nội&rdquo;, &ocirc;ng Nghi lo ngại.<br />\r\n		<br />\r\n		B&agrave; Trần Thị Hồng Hạnh, Ph&oacute; vụ trưởng Vụ t&iacute;n dụng Ng&acirc;n h&agrave;ng Nh&agrave; nước cho biết, hiện nay ng&acirc;n h&agrave;ng ở khu vực Đồng Nai chưa từ chối một trường hợp cho vay vốn để mua m&aacute;y m&oacute;c thiết bị n&agrave;o. C&agrave; ph&ecirc; kh&ocirc;ng phải l&agrave; mặt h&agrave;ng đặc biệt để được hưởng ưu ti&ecirc;n n&ecirc;n quy tr&igrave;nh cho vay vốn vẫn phải theo quy định.<br />\r\n		<br />\r\n		Ri&ecirc;ng đối với mặt h&agrave;ng c&aacute; tra, theo b&agrave; Hạnh nhiều doanh nghiệp kh&ocirc;ng chứng minh được hiệu quả sản xuất kinh doanh. B&agrave; Hạnh đưa ra minh họa, một số doanh nghiệp nu&ocirc;i c&aacute; qu&aacute; b&eacute;o n&ecirc;n kh&ocirc;ng thể xuất khẩu được.<br />\r\n		<br />\r\n		&ldquo;Ng&acirc;n h&agrave;ng lu&ocirc;n đảm bảo đủ vốn cho doanh nghiệp, nhưng đối với mặt h&agrave;ng kh&ocirc;ng được hưởng ch&iacute;nh s&aacute;ch ưu đ&atilde;i th&igrave; qu&aacute; tr&igrave;nh vay vốn phải theo chu tr&igrave;nh, quy định&rdquo;, b&agrave; Hạnh nhấn mạnh.<br />\r\n		<br />\r\n		Thứ trưởng Bộ C&ocirc;ng Thương Nguyễn Th&agrave;nh Bi&ecirc;n cho rằng, cần c&oacute; sự phối hợp chặt chẽ giữa c&aacute;c bộ ban ng&agrave;nh cũng như Hiệp hội v&agrave; c&aacute;c doanh nghiệp. Bộ C&ocirc;ng Thương lắng nghe v&agrave; sẽ tiếp thu &yacute; kiến đ&oacute;ng g&oacute;p của c&aacute;c doanh nghiệp để tr&igrave;nh l&ecirc;n Ch&iacute;nh phủ.</span></p>\r\n	<p style="text-align: justify;">\r\n		<span class="p_te8" id="UcNewsDetail1_lblNoiDung" style="display: inline-block; width: 500px;">&nbsp;</span></p>\r\n	<p style="text-align: right;">\r\n		<span class="p_te8" id="UcNewsDetail1_lblNoiDung" style="display: inline-block; width: 500px;">(Theo VNE)</span></p>\r\n</div>', '2010-12-06 00:00:00', '2010-12-06 00:00:00', 1, 1, 0);
INSERT INTO `articles` VALUES (30, 10, 1, 0, 'Xuất khẩu 2010: Nhiều khả năng vượt 70 tỷ USD', 'xuat-khau-2010-nhieu-kha-nang-vuot-70-ty-usd', 'Chỉ còn một tháng nữa năm 2010 sẽ khép lại và cơ sở để nhận định kim ngạch xuất khẩu thu về trong năm nay sẽ đạt trên 70 tỷ USD ngày càng hiện rõ.', 'files/news/20101206/20101206215852.jpg', '<div align="justify">\r\n	<p style="text-align: justify;">\r\n		<span class="p_te8" id="UcNewsDetail1_lblNoiDung" style="display: inline-block; width: 500px;"><strong>Lạc quan với con số 70,8 tỷ USD</strong><br />\r\n		<br />\r\n		B&aacute;o c&aacute;o do Thứ trưởng Bộ C&ocirc;ng Thương Nguyễn Th&agrave;nh Bi&ecirc;n tr&igrave;nh b&agrave;y tại buổi giao ban về xuất nhập khẩu ng&agrave;y 29/11 n&ecirc;u r&otilde;, th&aacute;ng 11 t&igrave;nh h&igrave;nh xuất khẩu tiếp tục diễn biến theo chiều hướng t&iacute;ch cực với c&aacute;c yếu tố thuận lợi về gi&aacute;, đơn h&agrave;ng, thị trường...<br />\r\n		<br />\r\n		Gi&aacute; xuất khẩu của hầu hết c&aacute;c mặt h&agrave;ng n&ocirc;ng sản đều tăng mạnh như sắn tăng tới 83,8%, cao su tăng khoảng 81,2%, hạt điều tăng l&agrave; 21,2%... Tiếp đến l&agrave; gi&aacute; dầu th&ocirc; tăng 33,9% v&agrave; than đ&aacute; tăng l&agrave; 53,2%...<br />\r\n		<br />\r\n		Nhờ vậy, kim ngạch xuất khẩu th&aacute;ng 11/2010 tăng tới 24,5% so với c&ugrave;ng kỳ năm 2009. T&iacute;nh cả 11 th&aacute;ng, b&igrave;nh qu&acirc;n mỗi th&aacute;ng xuất khẩu đạt 5,86 tỷ USD, cao hơn mức b&igrave;nh qu&acirc;n đ&atilde; đề ra (kế hoạch năm 2010 kim ngạch xuất khẩu l&agrave; 60,5 tỷ USD, b&igrave;nh qu&acirc;n 5,04 tỷ USD/th&aacute;ng).<br />\r\n		<br />\r\n		Về cơ cấu h&agrave;ng h&oacute;a xuất khẩu, tỷ trọng nh&oacute;m h&agrave;ng c&ocirc;ng nghiệp, chế tạo đ&atilde; tăng mạnh so với c&ugrave;ng kỳ năm trước, cụ thể l&agrave; từ 62,8% l&ecirc;n 68,1%. Trong khi nh&oacute;m h&agrave;ng nhi&ecirc;n liệu v&agrave; kho&aacute;ng sản lại giảm từ 15,2% xuống 11%.<br />\r\n		<br />\r\n		Như vậy, cơ cấu h&agrave;ng h&oacute;a xuất khẩu đ&atilde; v&agrave; đang c&oacute; những chuyển dịch theo hướng t&iacute;ch cực, tăng dần tỷ trọng nh&oacute;m h&agrave;ng c&ocirc;ng nghiệp, chế tạo, nh&oacute;m h&agrave;ng c&oacute; h&agrave;m lượng c&ocirc;ng nghệ v&agrave; chất x&aacute;m cao, giảm dần xuất khẩu h&agrave;ng th&ocirc;, c&oacute; gi&aacute; trị gia tăng thấp.<br />\r\n		<br />\r\n		Tr&ecirc;n cơ sở của những t&iacute;n hiệu lạc quan n&agrave;y, Bộ C&ocirc;ng Thương dự b&aacute;o, th&aacute;ng cuối năm xuất khẩu tiếp tục vẫn diễn biến theo chiều hướng thuận lợi. Cả năm xuất khẩu c&oacute; khả năng đạt được 70,8 tỷ USD, tăng 24% so với năm 2009, v&agrave; tăng 16,5% so với kế hoạch năm.<br />\r\n		<br />\r\n		&ldquo;Với những thuận lợi như đ&atilde; ph&acirc;n t&iacute;ch ở tr&ecirc;n, khả năng đạt được mức tăng trưởng n&agrave;y l&agrave; ho&agrave;n to&agrave;n c&oacute; thể&rdquo;, Thứ trưởng Bi&ecirc;n nhận định.&nbsp;<br />\r\n		<br />\r\n		<strong>Nhập si&ecirc;u vẫn dưới ngưỡng</strong><br />\r\n		<br />\r\n		Cũng theo b&aacute;o c&aacute;o của cơ quan giữ vai tr&ograve; trong điều h&agrave;nh xuất nhập khẩu n&agrave;y, tổng nhập si&ecirc;u 11 th&aacute;ng năm 2010 ước l&agrave; 10,65 tỷ USD, bằng 16,58% kim ngạch xuất khẩu. Tỷ lệ nhập si&ecirc;u 11 th&aacute;ng vẫn nằm dưới ngưỡng 20% m&agrave; Ch&iacute;nh phủ đặt ra.<br />\r\n		<br />\r\n		Tuy vậy, nếu x&eacute;t về cơ cấu mặt h&agrave;ng nhập khẩu so với c&ugrave;ng kỳ, kim ngạch nhập khẩu nh&oacute;m h&agrave;ng cần nhập khẩu v&agrave; nh&oacute;m h&agrave;ng cần hạn chế chỉ tăng lần lượt l&agrave; 18,5% v&agrave; 16,1%, th&igrave; nh&oacute;m h&agrave;ng cần kiểm so&aacute;t lại tăng tới 33,1%.<br />\r\n		<br />\r\n		Điều n&agrave;y được l&yacute; giải chủ yếu l&agrave; do sự tăng mạnh của gi&aacute; v&agrave;ng trong thời gian qua. Để b&igrave;nh ổn gi&aacute; v&agrave;ng trong nước, Ng&acirc;n h&agrave;ng Nh&agrave; nước đ&atilde; quyết định cấp ph&eacute;p nhập khẩu v&agrave;ng miếng 4 lần.<br />\r\n		<br />\r\n		Mặc d&ugrave; theo chu kỳ h&agrave;ng năm, nhập khẩu th&aacute;ng cuối c&ugrave;ng của năm thường tăng cao hơn so với những th&aacute;ng trước. Song với c&aacute;c biện ph&aacute;p kiềm chế nhập si&ecirc;u đồng bộ đang t&iacute;ch cực được triển khai, dự b&aacute;o nhập khẩu năm 2010 sẽ chỉ khoảng 82,8 tỷ USD, tăng 18,4% so với năm 2009. Theo đ&oacute;, nhập si&ecirc;u cả năm 2010 sẽ trong khoảng 12 tỷ USD,&nbsp; tỷ lệ nhập si&ecirc;u/xuất khẩu l&agrave; 17,02%, vẫn đạt được chỉ ti&ecirc;u của Ch&iacute;nh phủ đặt ra.<br />\r\n		<br />\r\n		Tuy kh&aacute; lạc quan với c&aacute;c con số sẽ đạt được trong năm 2010, nhưng Thứ trưởng Bi&ecirc;n cũng như đại diện cho c&aacute;c hiệp hội, ng&agrave;nh h&agrave;ng vẫn kh&aacute; &ldquo;d&egrave; dặt&rdquo; trước chỉ ti&ecirc;u trong năm 2011 l&agrave; tăng trưởng xuất khẩu h&agrave;ng h&oacute;a đạt 10% so với thực hiện năm 2010, nhập si&ecirc;u h&agrave;ng ho&aacute; năm 2011 dưới 18% so với tổng kim ngạch xuất khẩu h&agrave;ng h&oacute;a vừa được Quốc hội th&ocirc;ng qua.<br />\r\n		<br />\r\n		Thứ trưởng Nguyễn Th&agrave;nh Bi&ecirc;n cho rằng điều n&agrave;y l&agrave; kh&aacute; kh&oacute; v&igrave; trong năm tới, c&aacute;c mặt h&agrave;ng xuất khẩu chủ lực của Việt Nam sẽ phải đối mặt với nhiều kh&oacute; khăn như c&aacute;c r&agrave;o cản thương mại ng&agrave;y c&agrave;ng tăng l&ecirc;n... Th&ecirc;m v&agrave;o đ&oacute;, gi&aacute; của của nhiều mặt h&agrave;ng sẽ kh&oacute; c&oacute; thể được như hiện nay do đ&acirc;y đang l&agrave; giai đoạn đầu của thời kỳ phục hồi kinh tế sau thời gian suy tho&aacute;i.</span></p>\r\n	<p style="text-align: justify;">\r\n		<span class="p_te8" id="UcNewsDetail1_lblNoiDung" style="display: inline-block; width: 500px;">&nbsp;</span></p>\r\n	<p style="text-align: right;">\r\n		<span class="p_te8" id="UcNewsDetail1_lblNoiDung" style="display: inline-block; width: 500px;">(Theo VnEconomy)</span></p>\r\n</div>', '2010-12-06 00:00:00', '2010-12-06 00:00:00', 1, 1, 0);
INSERT INTO `articles` VALUES (31, 10, 1, 0, 'Giá thép tăng lần thứ hai trong tháng 11', 'gia-thep-tang-lan-thu-hai-trong-thang-11', 'Lãi suất ngân hàng quá cao cùng với tỷ giá đôla biến động mạnh khiến giá thép tăng khoảng 300.000 đồng mỗi tấn. Đây là lần thứ hai trong tháng, giá thép biến động mạnh.', 'files/news/20101206/20101206215945.jpg', '<div align="justify">\r\n	<p style="text-align: justify;">\r\n		<span class="p_te8" id="UcNewsDetail1_lblNoiDung" style="display: inline-block; width: 500px;">Ph&oacute; chủ tịch Hiệp hội Th&eacute;p Việt Nam Nguyễn Tiến Nghi cho biết, gi&aacute; th&eacute;p đ&atilde; tăng từ 100.000-300.000 đồng mỗi tấn so với những ng&agrave;y đầu th&aacute;ng. Tại nhiều nh&agrave; m&aacute;y, th&eacute;p đang được b&aacute;n quanh mức 13,4 triệu-14 triệu đồng mỗi tấn (chưa t&iacute;nh VAT, chưa trừ chiết khấu).<br />\r\n		<br />\r\n		Theo &ocirc;ng Nghi, gi&aacute; th&eacute;p tăng do chịu t&aacute;c động mạnh từ biến động tỷ gi&aacute; USD v&agrave; l&atilde;i suất ng&acirc;n h&agrave;ng. Trong khi 40% ph&ocirc;i v&agrave; 70% th&eacute;p phế phải nhập khẩu, gi&aacute; đ&ocirc;la tăng khiến chi ph&iacute; đầu v&agrave;o cao hơn buộc c&aacute;c nh&agrave; sản xuất phải tăng gi&aacute; b&aacute;n. Ngo&agrave;i ra, ng&acirc;n h&agrave;ng c&ocirc;ng bố l&atilde;i suất cho vay với doanh nghiệp th&eacute;p phổ biến l&agrave; 19% một năm, nhưng kh&ocirc;ng &iacute;t trường hợp phải vay cao hơn gi&aacute; ni&ecirc;m yết.<br />\r\n		<br />\r\n		&Ocirc;ng Nghi nhấn mạnh, với mức tăng khoảng 300.000 đồng mỗi tấn, doanh nghiệp vẫn phải chịu lỗ. Sự tăng gi&aacute; mạnh của đ&ocirc;la trong thời gian qua khiến gi&aacute; ph&ocirc;i th&eacute;p tăng 1 triệu đồng mỗi tấn, ngo&agrave;i ra c&ograve;n phải cộng th&ecirc;m 1,5-1,7 triệu đồng chi ph&iacute; gia c&ocirc;ng.<br />\r\n		<br />\r\n		&quot;L&atilde;i suất ng&acirc;n h&agrave;ng qu&aacute; cao c&ugrave;ng tỷ gi&aacute; USD biến động mạnh đang g&acirc;y sức &eacute;p tiếp tục tăng gi&aacute; cho c&aacute;c doanh nghiệp th&eacute;p&quot;, &ocirc;ng Nghi dự b&aacute;o.<br />\r\n		<br />\r\n		Hiệp hội Th&eacute;p Việt Nam cho biết lượng th&eacute;p ti&ecirc;u thụ trong th&aacute;ng 12 sẽ v&agrave;o khoảng 400.000-450.000 tấn, bằng mức ti&ecirc;u thụ của th&aacute;ng 11. Do t&acirc;m l&yacute; cuối năm, c&aacute;c nh&agrave; đầu tư phải đẩy nhanh tiến độ c&ocirc;ng tr&igrave;nh, ho&agrave;n th&agrave;nh kế hoạch năm n&ecirc;n lượng ti&ecirc;u thụ th&eacute;p sẽ tăng cao.<br />\r\n		<br />\r\n		&Ocirc;ng Nghi cho hay, với lượng ph&ocirc;i dự trữ khoảng 560.000 tấn, th&eacute;p th&agrave;nh phẩm tồn kho khoảng 280.000 tấn, nguồn cung th&eacute;p sẽ kh&ocirc;ng thiếu. Đại diện của Hiệp hội Th&eacute;p cũng đưa ra dự b&aacute;o, trong thời gian tới, c&oacute; thể gi&aacute; c&ograve;n tiếp tục tăng.<br />\r\n		<br />\r\n		Trước đợt tăng gi&aacute; n&agrave;y, gi&aacute; th&eacute;p cũng đ&atilde; tăng một lần v&agrave;o đầu th&aacute;ng 11 với mức từ 150.000-300.000 đồng mỗi tấn.</span></p>\r\n	<p style="text-align: justify;">\r\n		<span class="p_te8" id="UcNewsDetail1_lblNoiDung" style="display: inline-block; width: 500px;">&nbsp;</span></p>\r\n	<p style="text-align: right;">\r\n		<span class="p_te8" id="UcNewsDetail1_lblNoiDung" style="display: inline-block; width: 500px;">(Theo VNE)</span></p>\r\n</div>', '2010-12-06 00:00:00', '2010-12-06 00:00:00', 1, 1, 0);
INSERT INTO `articles` VALUES (32, 4, 1, 0, 'DCC: triển khai 02 gói thầu mới với giá trị 28 tỷ đồng', 'dcc-trien-khai-02-goi-thau-moi-voi-gia-tri-28-ty-dong', '', 'files/news/20101206/20101206220832.jpg', '<p>\r\n	<span class="p_te8" id="UcNewsDetail1_lblNoiDung" style="display: inline-block; width: 500px;"><strong>1/ Khởi c&ocirc;ng c&ocirc;ng tr&igrave;nh &ldquo;Nh&agrave; m&aacute;y C&ocirc;ng ty TNHH Sato-Sangyo VN&rdquo;</strong></span></p>\r\n<p>\r\n	<br />\r\n	<span class="p_te8" id="UcNewsDetail1_lblNoiDung" style="display: inline-block; width: 500px;">Ng&agrave;y 13/09/2010 tại Khu c&ocirc;ng nghiệp Mỹ Phước 3, tỉnh B&igrave;nh Dương, C&ocirc;ng ty DESCON đ&atilde; tổ chức lễ khởi c&ocirc;ng c&ocirc;ng tr&igrave;nh &ldquo;Nh&agrave; M&aacute;y C&ocirc;ng ty TNHH SATO-SANGYO (Việt Nam).<br />\r\n	<br />\r\n	Đ&acirc;y l&agrave; c&ocirc;ng tr&igrave;nh do C&ocirc;ng ty TNHH SATO-SANGYO (Việt Nam) &ndash; 100% vốn của Nhật Bản &ndash; l&agrave;m Chủ đầu tư, chuy&ecirc;n sản xuất c&aacute;c sản phẩm về gỗ.<br />\r\n	<br />\r\n	C&ocirc;ng tr&igrave;nh c&oacute; gi&aacute; trị hợp đồng tr&ecirc;n 16 tỷ đồng v&agrave; do X&iacute; nghiệp 3 (DESCON 3) thi c&ocirc;ng.<br />\r\n	&nbsp;<br />\r\n	<strong>2/&nbsp; Triển khai c&aacute;c hạng mục điện, nước v&agrave; ho&agrave;n thiện c&ocirc;ng tr&igrave;nh &ldquo;Co.opMart Đ&ocirc;ng H&agrave;&rdquo;</strong><br />\r\n	<br />\r\n	Sau khi thi c&ocirc;ng xong phần th&ocirc;, DESCON tr&uacute;ng thầu tiếp c&aacute;c hạng mục: ho&agrave;n thiện - hệ thống điện, hệ thống cấp tho&aacute;t nước, hệ thống PCCC v&agrave; chống s&eacute;t c&ocirc;ng tr&igrave;nh Co.opMart Đ&ocirc;ng H&agrave; với gi&aacute; trị hợp đồng ~12 tỷ đồng. C&ocirc;ng tr&igrave;nh do X&iacute; nghiệp C&amp;ME thi c&ocirc;ng.<br />\r\n	<br />\r\n	Chủ đầu tư c&ocirc;ng tr&igrave;nh l&agrave; C&ocirc;ng ty TNHH Thương mại Dịch vụ S&agrave;i G&ograve;n - Đ&ocirc;ng H&agrave;.<br />\r\n	<br />\r\n	C&ocirc;ng tr&igrave;nh được x&acirc;y dựng tại Thị x&atilde; Đ&ocirc;ng H&agrave;, Tỉnh Quảng Trị .</span></p>', '2010-12-06 00:00:00', NULL, 1, 0, 0);
INSERT INTO `articles` VALUES (33, 2, 1, 0, 'Giá trị giao dịch sàn TP HCM cao nhất trong 6 tháng', 'gia-tri-giao-dich-san-tp-hcm-cao-nhat-trong-6-thang', 'Lượng cổ phiếu, trái phiếu chuyển nhượng tại HOSE trong phiên sáng nay đạt gần 2.400 tỷ đồng. Thanh khoản sàn Hà Nội cũng tăng mạnh cho dù các Index chưa vượt thành công các ngưỡng cản quan trọng.', 'files/news/20101206/20101206222541.jpg', '<p>\r\n	2 s&agrave;n chứng kho&aacute;n khởi động tuần mới tương đối hứng khởi với đ&agrave; tăng ngắn hạn đ&atilde; được x&aacute;c lập trước đ&oacute;. Trong những ng&agrave;y gần đ&acirc;y, khối lượng giao dịch cổ phiếu tr&ecirc;n s&agrave;n TP HCM c&oacute; xu hướng tăng dần ngay trong đợt khớp lệnh mở cửa. Phi&ecirc;n s&aacute;ng nay cũng kh&ocirc;ng phải ngoại lệ với gần 6 triệu chứng kho&aacute;n (130,72 tỷ đồng) được sang tay.</p>\r\n<p>\r\n	<img alt="" src="/files/upload/images/chung-khoan-0.jpg" style="width: 490px; height: 320px;" /></p>\r\n<p class="Normal">\r\n	Vn-Index cũng tăng gần 3 điểm, l&ecirc;n 467,3 điểm khi hầu hết c&aacute;c m&atilde; blue-chip vẫn chứng tỏ được vai tr&ograve; dẫn dắt thị trường. Nh&oacute;m ng&agrave;nh chứng kho&aacute;n v&agrave; bất động sản vẫn được giao dịch nhiều nhất. Điển h&igrave;nh l&agrave; SSI với gần 2,2 triệu cổ phiếu được chuyển nhượng tại thời điểm n&agrave;y. Tiếp theo l&agrave; LCG, VIP, GTT, REE&hellip;</p>\r\n<p class="Normal">\r\n	Đ&agrave; tăng gi&aacute; cổ phiếu c&oacute; dấu hiệu đuối sức trong những ph&uacute;t đầu đợt 2 khi một số m&atilde; lớn như DRC, FPT, HDC, VIS&hellip; bắt đầu giảm điểm. Index c&oacute; l&uacute;c đ&atilde; hiện chỉ b&aacute;o đỏ nhưng cuối c&ugrave;ng vẫn duy tr&igrave; được đ&agrave; tăng nhẹ v&agrave;o cuối phi&ecirc;n nhờ lực đẩy của SSI, REE, STB, ITA, OGC, QCG, VNE&hellip;</p>\r\n<p class="Normal">\r\n	S&agrave;n TP HCM kết phi&ecirc;n ở 465,56 điểm, chỉ tăng 1,21 điểm. Tuy vậy, khối lượng v&agrave; gi&aacute; trị giao dịch vọt l&ecirc;n gần 96,78 triệu cổ phiếu v&agrave; 2.392,65 tỷ đồng. Đ&acirc;y l&agrave; gi&aacute; trị giao dịch lớn nhất tại HOSE trong v&ograve;ng 6 th&aacute;ng gần đ&acirc;y. Bảng điện tử HOSE ghi nhận gần 20 cổ phiếu c&oacute; giao dịch tr&ecirc;n một triệu chứng kho&aacute;n trong đ&oacute; SSI chuyển nhượng được tới hơn 7 triệu cổ phiếu.</p>\r\n<p class="Normal">\r\n	Tương tự s&agrave;n TP HCM, lượng giao dịch tại HNX cũng tăng mạnh l&ecirc;n 87,76 triệu cổ phiếu, tương đương 1.761,07 tỷ đồng. Tuy nhi&ecirc;n <strong><font color="#4f4f4f">HNX-Index </font></strong>tỏ ra đuối sức cuối phi&ecirc;n, chỉ tăng nhẹ 0,41 điểm, l&ecirc;n 117,07 điểm. Như vậy, cả 2 Index đều thất bại trong nỗ lực vượt ngưỡng cản 470 điểm v&agrave; 120 điểm trong s&aacute;ng nay.</p>\r\n<p class="Normal">\r\n	Kết th&uacute;c giao dịch buổi s&aacute;ng, <strong><font color="#4f4f4f">UPCoM-Index</font></strong> tăng 0,34 điểm, l&ecirc;n 43,3 điểm. Lượng giao dịch, tuy vậy, chỉ đạt 261.500 cổ phiếu, tương đương 2,79 tỷ đồng</p>', '2010-12-06 00:00:00', NULL, 1, 1, 0);
INSERT INTO `articles` VALUES (34, 7, 1, 0, 'Thị trường tài chính căng thẳng', 'thi-truong-tai-chinh-cang-thang', 'Giao tranh giữa hai miền Nam - Bắc Triều Tiên đã khiến thị trường tài chính khắp nơi càng thêm căng thẳng. Tại VN, giá vàng có thêm động thái tăng trở lại trong khi nhà đầu tư chứng khoán nhấp...', 'files/news/20101207/20101207193810.jpg', '<p>\r\n	<strong>Gi&aacute; v&agrave;ng, USD tăng</strong><br />\r\n	<br />\r\n	Gi&aacute; v&agrave;ng thế giới h&ocirc;m qua đ&atilde; tăng 14 USD/ounce so với ng&agrave;y 23.11, l&ecirc;n&nbsp; 1.377 USD/ounce (trong đ&ecirc;m 23.11, gi&aacute; v&agrave;ng thế giới đ&atilde; l&ecirc;n 1.382 USD/ounce). Điều n&agrave;y k&eacute;o theo gi&aacute; v&agrave;ng trong nước tăng th&ecirc;m khoảng 300.000 - 400.000 đồng/lượng so với ng&agrave;y 23.11. Đồng thời lực mua v&agrave;ng xuất hiện khiến gi&aacute; v&agrave;ng được nhanh ch&oacute;ng đẩy l&ecirc;n cao c&ugrave;ng với gi&aacute; thế giới.<br />\r\n	<br />\r\n	B&agrave; Nguyễn Thị C&uacute;c - Ph&oacute; tổng gi&aacute;m đốc C&ocirc;ng ty cổ phần v&agrave;ng bạc đ&aacute; qu&yacute; Ph&uacute; Nhuận (PNJ) cho biết trong ng&agrave;y h&ocirc;m qua, c&ocirc;ng ty đ&atilde; b&aacute;n ra tr&ecirc;n 2.000 lượng v&agrave;ng v&agrave; chỉ mua v&agrave;o 872 lượng. Lực mua v&agrave;ng chủ yếu xuất ph&aacute;t từ người d&acirc;n.<br />\r\n	<br />\r\n	Chiều h&ocirc;m qua, NHNN ch&iacute;nh thức cấp th&ecirc;m quota cho một số đơn vị nhập khẩu v&agrave;ng với thời gian k&eacute;o d&agrave;i đến hết năm 2010. Th&ocirc;ng tin n&agrave;y đ&atilde; l&agrave;m gi&aacute; v&agrave;ng trong nước đứng khựng lại tại mức 35,85 triệu - 35,95 triệu đồng/lượng d&ugrave; gi&aacute; thế giới tăng l&ecirc;n 1.380 USD/ounce. Khoảng c&aacute;ch ch&ecirc;nh lệch gi&aacute; v&agrave;ng trong v&agrave; ngo&agrave;i nước được k&eacute;o lại, c&ograve;n khoảng 500.000 đồng/lượng (chưa t&iacute;nh ph&iacute;) thay v&igrave; mức ch&ecirc;nh lệch từ 1 triệu - 1,2 triệu đồng/lượng trong c&aacute;c ng&agrave;y trước. Đồng thời gi&aacute; USD tr&ecirc;n thị trường cũng tăng mạnh 150 - 170 đồng/USD, l&ecirc;n 21.200 đồng/USD.<br />\r\n	<br />\r\n	Đại diện C&ocirc;ng ty v&agrave;ng bạc đ&aacute; qu&yacute; Sacombank - SBJ nhận x&eacute;t, căng thẳng giữa CHDCND Triều Ti&ecirc;n - H&agrave;n Quốc đ&atilde; khiến gi&aacute; v&agrave;ng tho&aacute;t khỏi k&ecirc;nh giảm gi&aacute; v&agrave; hiện đang &ldquo;nương&rdquo; theo diễn biến n&agrave;y. Đồng thời, việc c&aacute;c quan chức Cục Dự trữ li&ecirc;n bang Mỹ (FED) hạ dự b&aacute;o tăng trưởng của nước n&agrave;y trong năm 2010 - 2011, nhận định phải mất hơn 6 năm nữa thất nghiệp, tăng trưởng v&agrave; lạm ph&aacute;t mới c&oacute; thể trở về c&aacute;c mức b&igrave;nh thường hay nỗi lo về nợ c&ocirc;ng của Ireland vẫn c&ograve;n... cũng đang hỗ trợ cho đ&agrave; tăng gi&aacute; của v&agrave;ng.<br />\r\n	<br />\r\n	<strong>Chứng kho&aacute;n hụt hơi<br />\r\n	</strong><br />\r\n	Trong phi&ecirc;n giao dịch ng&agrave;y 23.11, thị trường chứng kho&aacute;n (TTCK) nhiều nơi đ&atilde; phản ứng ngay tức th&igrave;. Kết quả TTCK H&agrave;n Quốc đ&atilde; giảm 0,8%, TTCK Hồng K&ocirc;ng v&agrave; Trung Quốc lần lượt giảm 2,67% v&agrave; 1,94%. Đặc biệt gi&aacute; tr&aacute;i phiếu tương lai của H&agrave;n Quốc v&agrave; đồng won giảm 2%. Sự lo &acirc;u cũng k&eacute;o d&agrave;i sang c&aacute;c ch&acirc;u lục kh&aacute;c khi chỉ số Dow Jones của TTCK Mỹ đ&oacute;ng cửa rạng s&aacute;ng ng&agrave;y 24.11 (giờ VN) mất đi 1,27%, chỉ số S&amp;P giảm 1,43%...<br />\r\n	<br />\r\n	Tuy nhi&ecirc;n bước sang phi&ecirc;n giao dịch h&ocirc;m qua 24.11, dường như c&aacute;c nh&agrave; đầu tư&nbsp; (NĐT) đ&atilde; trở n&ecirc;n b&igrave;nh tĩnh hơn khiến nhiều TTCK ch&acirc;u &Aacute; tiếp tục giảm nhưng tốc độ nhẹ hơn, như chỉ số Nikkei 225 của Nhật giảm 0,84%, chỉ số KOSPI giảm 0,15%,&hellip; trong khi chỉ số Hang Seng của Hồng K&ocirc;ng v&agrave; Shanghai của Trung Quốc lần lượt tăng nhẹ 0,56% v&agrave; 1,12%...<br />\r\n	<br />\r\n	Thạc sĩ L&ecirc; Đạt Ch&iacute;, Trưởng bộ m&ocirc;n Đầu tư t&agrave;i ch&iacute;nh (trường ĐH Kinh tế TP.HCM), nhận x&eacute;t t&igrave;nh h&igrave;nh của b&aacute;n đảo Triều Ti&ecirc;n khiến cho hoạt động thương mại v&agrave; đầu tư giảm xuống. Tuy nhi&ecirc;n mức độ t&aacute;c động như thế n&agrave;o đến VN th&igrave; chưa c&oacute; cơ sở để x&aacute;c định cụ thể. Chỉ c&oacute; thể dự b&aacute;o rằng d&ograve;ng vốn đầu tư nước ngo&agrave;i tại H&agrave;n Quốc sẽ bị dịch chuyển sang c&aacute;c thị trường kh&aacute;c nếu như bất ổn vẫn c&ograve;n k&eacute;o d&agrave;i. &ldquo;Hơn nữa t&aacute;c động đến thị trường t&agrave;i ch&iacute;nh VN n&oacute;i chung v&agrave; TTCK n&oacute;i ri&ecirc;ng sẽ kh&ocirc;ng nhiều bởi mức độ hội nhập của c&aacute;c thị trường n&agrave;y với thế giới chưa cao&rdquo;, &ocirc;ng Ch&iacute; n&oacute;i.</p>\r\n<p style=\\"\\">\r\n	(Theo TN)</p>', '2010-12-07 19:37:33', '2010-12-07 19:38:34', 1, 0, 0);
INSERT INTO `articles` VALUES (35, 7, 1, 0, 'Nhiều doanh nghiệp niêm yết hạ chỉ tiêu kinh doanh', 'nhieu-doanh-nghiep-niem-yet-ha-chi-tieu-kinh-doanh', 'Nhiều doanh nghiệp quyết định giảm chỉ tiêu doanh thu, lợi nhuận xuống 20-50% ngay trong những tháng cuối năm, do không lường trước biến động lớn giá nguyên liệu đầu vào, chi phí tăng cao.', 'files/news/20101207/20101207194518.jpg', '<div align=\\"justify\\">\r\n	<p>\r\n		<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">Chứng kho&aacute;n An Ph&aacute;t điều chỉnh kế hoạch kinh doanh 2010 hồi đầu th&aacute;ng n&agrave;y. Tổng doanh thu 85,071 tỷ đồng sẽ r&uacute;t c&ograve;n 42,5 tỷ đồng, bằng 49,96% so với kế hoạch. Lợi nhuận trước thuế 37 tỷ đồng, c&ograve;n 16,644 tỷ đồng, giảm 55,02%. Thu nhập sau thuế 28,55 tỷ đồng do vậy cũng điều chỉnh xuống 12,983 tỷ đồng (giảm 54,53%).<br />\r\n		<br />\r\n		Thị trường &egrave;o uột trong suốt thời gian d&agrave;i khiến hoạt động kinh doanh ở nhiều c&ocirc;ng ty chứng kho&aacute;n gặp kh&oacute;. L&atilde;nh đạo một c&ocirc;ng ty chứng kho&aacute;n ở quận 1, TP <b>HCM</b> than thở: &quot;Duy tr&igrave; hoạt động đ&atilde; l&agrave; việc kh&oacute; trong bối cảnh hiện nay chứ chưa t&iacute;nh tới mở rộng kinh doanh hay huy động vốn, bởi phải co chỗ n&agrave;y, k&eacute;o chỗ nọ mới đắp đủ chi ph&iacute; ph&aacute;t sinh h&agrave;ng th&aacute;ng&quot;. Trong qu&yacute; III, nhiều c&ocirc;ng ty chứng kho&aacute;n thua lỗ, giảm lợi nhuận cũng như kh&oacute; c&oacute; thể ho&agrave;n th&agrave;nh kế hoạch năm do doanh thu từ tự doanh v&agrave; m&ocirc;i giới giảm mạnh.<br />\r\n		<br />\r\n		Với những doanh nghiệp hoạt động ở những lĩnh vực kh&aacute;c, gi&aacute; nguy&ecirc;n liệu đầu v&agrave;o, tỷ gi&aacute;, chi ph&iacute; tăng mạnh... l&agrave; những nguy&ecirc;n nh&acirc;n trọng yếu khiến việc điều chỉnh phải diễn ra ngay trong thời điểm gần hết năm.<br />\r\n		chứng kho&aacute;n<br />\r\n		<br />\r\n		Theo Tổng gi&aacute;m đốc một c&ocirc;ng ty sản xuất tại TP <b>HCM</b>, nếu kh&ocirc;ng ho&agrave;n th&agrave;nh kế hoạch, người lao động kh&ocirc;ng được thưởng cuối năm, cổ đ&ocirc;ng mất niềm tin, trong khi gi&aacute; nguy&ecirc;n liệu tăng vượt dự kiến, ti&ecirc;u thụ kh&oacute; khăn l&agrave; những nguy&ecirc;n nh&acirc;n kh&aacute;ch quan kh&oacute; lường trước được. Do vậy, c&aacute;c chỉ ti&ecirc;u kinh doanh phải thay đổi để ph&ugrave; hợp với hoạt động của năm nay. Cổ đ&ocirc;ng nhận th&ocirc;ng tin kịp thời, sẽ dễ chấp nhận hơn l&agrave; phải nghe trần t&igrave;nh của l&atilde;nh đạo ở đại hội năm sau.<br />\r\n		<br />\r\n		C&ocirc;ng ty cổ phần chế biến gỗ Đức Th&agrave;nh (<b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=ana+zIpZX8Y=\\" target=\\"_blank\\">GDT</a></b>) hồi cuối th&aacute;ng 10 quyết nghị giảm doanh thu xuất khẩu 162 tỷ xuống 147 tỷ đồng. Doanh thu chung từ 195 tỷ c&ograve;n 180 tỷ đồng. L&atilde;nh đạo c&ocirc;ng ty cho biết do gi&aacute; nguy&ecirc;n liệu tăng mạnh n&ecirc;n rất rủi ro nếu nhận th&ecirc;m đơn h&agrave;ng. Bởi nhiều khả năng gi&aacute; gỗ sẽ tiếp tục tăng nữa, thậm ch&iacute; kh&ocirc;ng c&oacute; h&agrave;ng để mua, khi đ&oacute; sẽ phải đền hợp đồng. Ch&iacute;nh v&igrave; vậy, doanh nghiệp đ&atilde; điều chỉnh giảm doanh thu một ch&uacute;t, nhận đơn h&agrave;ng &iacute;t lại, nhưng an to&agrave;n hơn l&agrave; vẫn giữ mức kế hoạch ban đầu nhưng rủi ro tăng cao. Tuy nhi&ecirc;n, lợi nhuận trước thuế đến hết năm dự b&aacute;o đạt 42 tỷ, vượt 7% so với kế hoạch.<br />\r\n		<br />\r\n		Trước đ&oacute;, C&ocirc;ng ty cổ phần thế giới số Trần Anh (<b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=FAVIHhmSPuI=\\" target=\\"_blank\\">TAG</a></b>) v&agrave; cổ đ&ocirc;ng đ&atilde; đồng thuận chỉ ti&ecirc;u mới: 1.320 tỷ đồng doanh thu thuần về b&aacute;n h&agrave;ng v&agrave; cung cấp dịch vụ (mức cũ 1.517 tỷ đồng), lợi nhuận sau thuế 40,1 tỷ đồng xuống c&ograve;n 35,2 tỷ đồng.<br />\r\n		<br />\r\n		Ho&atilde;n kế hoạch tăng vốn điều lệ từ 500 l&ecirc;n 700 tỷ đồng, C&ocirc;ng ty cổ phần bất động sản t&agrave;i ch&iacute;nh dầu kh&iacute; Việt Nam (<b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=H8HHCjgiuu0=\\" target=\\"_blank\\">PFL</a></b>) cũng đ&atilde; ho&agrave;n tất c&ocirc;ng đoạn lấy &yacute; kiến cổ đ&ocirc;ng bằng văn bản việc điều chỉnh kế hoạch kinh doanh. Cụ thể, tổng doanh thu từ 543,1 tỷ đồng xuống 325,83 tỷ đồng, lợi nhuận sau thuế 119,7 tỷ đồng c&ograve;n 92,25 tỷ đồng.<br />\r\n		<br />\r\n		Tiến sĩ L&ecirc; Thẩm Dương, Trưởng khoa quản trị kinh doanh, Đại học Ng&acirc;n h&agrave;ng TP <b>HCM</b> cho rằng việc điều chỉnh khi năm t&agrave;i ch&iacute;nh sắp kết th&uacute;c kh&ocirc;ng c&oacute; g&igrave; bất thường đối với những doanh nghiệp thực sự kh&oacute; khăn do c&aacute;c yếu tố kh&aacute;ch quan chi phối (l&atilde;i suất, tỷ gi&aacute;, nguy&ecirc;n liệu...). Tuy nhi&ecirc;n, c&oacute; doanh nghiệp lấy cớ kh&oacute; khăn n&agrave;y nọ để điều chỉnh giảm, &eacute;m lợi nhuận để d&agrave;nh cho năm sau (khi dự kiến qu&yacute; I năm sau cũng kh&oacute; khăn kh&ocirc;ng k&eacute;m) hoặc v&igrave; những nguy&ecirc;n do kh&aacute;c (giảm thuế, t&aacute;c động v&agrave;o gi&aacute; cổ phiếu...). Song, nh&agrave; đầu tư sẽ kh&oacute; nhận ra những thủ thuật n&agrave;y.<br />\r\n		<br />\r\n		Do vậy, đối với những c&ocirc;ng ty điều chỉnh kế hoạch, theo &ocirc;ng Dương, nh&agrave; đầu tư phải nghi&ecirc;n cứu kỹ phần giải tr&igrave;nh xem c&oacute; đủ căn cứ thuyết phục bằng c&aacute;c dẫn chứng định lượng, kh&ocirc;ng n&ecirc;n tin v&agrave;o c&aacute;c giải th&iacute;ch định t&iacute;nh chung chung. Bởi c&oacute; trường hợp c&ocirc;ng ty hoan hỉ b&aacute;o doanh thu tăng nhưng thực ra đ&oacute; l&agrave; do gi&aacute; cả tăng chứ sản lượng ti&ecirc;u thụ kh&ocirc;ng hề đột ph&aacute; so với c&ugrave;ng kỳ.</span></p>\r\n	<p style=\\"text-align: right;\\">\r\n		<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">(Theo Vnexpress)</span></p>\r\n</div>', '2010-12-07 19:44:18', NULL, 1, 0, 0);
INSERT INTO `articles` VALUES (36, 7, 1, 0, 'Cổ phiếu nhỏ lại “nóng”', 'co-phieu-nho-lai-nong', 'Vai trò giữ nhịp của nhóm cổ phiếu lớn trong hai phiên gần đây không còn được chú ý nhiều. Thay vào đó sự trở lại của hàng loạt cổ phiếu nhỏ, “hàng nóng”, báo hiệu nhu cầu tìm kiếm lợi nhuận của...', 'files/news/20101207/20101207194555.jpg', '<div align=\\"justify\\">\r\n	<p style=\\"text-align: justify;\\">\r\n		<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\"><strong>&ldquo;H&agrave;ng n&oacute;ng&rdquo; lại sốt</strong><br />\r\n		<br />\r\n		H&agrave;ng loạt cổ phiếu n&oacute;ng lao dốc thời gian qua bắt đầu quay trở lại quỹ đạo tăng trần quen thuộc. <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=Mysi6/Lf+io=\\" target=\\"_blank\\">AAA</a></b> l&agrave; gương mặt s&aacute;ng gi&aacute; nhất v&igrave; l&agrave; cổ phiếu &ldquo;đ&igrave;nh đ&aacute;m&rdquo; được ch&uacute; &yacute; trong suốt hai th&aacute;ng qua. T&iacute;nh từ đỉnh cao nhất ng&agrave;y 15/9 đến đ&aacute;y ng&agrave;y 16/11 vừa qua, <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=Mysi6/Lf+io=\\" target=\\"_blank\\">AAA</a></b> đ&atilde; sụt giảm khoảng 74%, xuống mức gi&aacute; thấp nhất trong lịch sử của cổ phiếu n&agrave;y: 23.900 đồng/cổ phiếu.<br />\r\n		<br />\r\n		Sau v&agrave;i lần đỡ đ&aacute;y hụt, d&ograve;ng vốn đầu cơ đ&atilde; chặn được đ&agrave; giảm v&agrave; bắt đầu tiến h&agrave;nh đẩy gi&aacute; l&ecirc;n bằng khối lượng chặn mua trần &ldquo;khủng&rdquo;. N&oacute;i chung chiến thuật với c&aacute;c &ldquo;h&agrave;ng n&oacute;ng&rdquo; dạng n&agrave;y vẫn kh&ocirc;ng thay đổi: Chặn mua lớn, chuẩn bị vốn để đ&oacute;n h&agrave;ng cắt lỗ, sẵn s&agrave;ng rung lắc tr&ecirc;n đường.<br />\r\n		<br />\r\n		<b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=Mysi6/Lf+io=\\" target=\\"_blank\\">AAA</a></b> c&oacute; độ bật nhanh hơn mặt bằng chung kh&aacute; nhiều. HNX-Index cũng chạm đ&aacute;y ng&agrave;y 17/11 nhưng 7 phi&ecirc;n vừa qua vẫn l&igrave;nh x&igrave;nh v&agrave; mới tăng được 4,4%. Trong khi đ&oacute; <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=Mysi6/Lf+io=\\" target=\\"_blank\\">AAA</a></b> đ&atilde; đạt tỉ suất lợi nhuận tối đa 32,5% - một mức tăng ch&oacute;ng mặt trong giai đoạn n&agrave;y.<br />\r\n		<br />\r\n		Tuy nhi&ecirc;n <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=Mysi6/Lf+io=\\" target=\\"_blank\\">AAA</a></b> vẫn xếp sau &ldquo;qu&aacute;n qu&acirc;n&rdquo; <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=PkIOW5dv+tM=\\" target=\\"_blank\\">VE9</a></b>. Cổ phiếu n&agrave;y tuy kh&ocirc;ng &ldquo;nổi tiếng&rdquo; như <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=Mysi6/Lf+io=\\" target=\\"_blank\\">AAA</a></b> nhưng cũng thuộc loại giảm mạnh nhất tr&ecirc;n cả hai s&agrave;n. T&iacute;nh từ đỉnh 25/6/2010 đến đ&aacute;y ng&agrave;y 3/11, <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=PkIOW5dv+tM=\\" target=\\"_blank\\">VE9</a></b> cũng đ&atilde; giảm khoảng 72%. Trong 7 phi&ecirc;n vừa qua, <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=PkIOW5dv+tM=\\" target=\\"_blank\\">VE9</a></b> đ&atilde; đem lại tỉ suất lợi nhuận gần 41%.<br />\r\n		<br />\r\n		Nh&oacute;m cổ phiếu c&oacute; truyền thống s&oacute;ng lớn do bị đầu cơ qu&aacute; mức từ đầu tuần đến nay lại g&acirc;y ch&uacute; &yacute; khi khối lượng giao dịch tăng cao, đặc biệt l&agrave; khối lượng chặn mua gi&aacute; trần. Kh&ocirc;ng giống như l&yacute; thuyết &ldquo;cổ phiếu giảm c&agrave;ng &iacute;t th&igrave; tăng c&agrave;ng mạnh&rdquo;, tr&ecirc;n thị trường Việt Nam, cổ phiếu giảm c&agrave;ng s&acirc;u th&igrave; tăng c&agrave;ng ch&oacute;ng mặt. D&ograve;ng vốn đầu cơ c&oacute; lẽ rất quen thuộc với c&aacute;c m&atilde; như vậy n&ecirc;n chỉ cần &ldquo;c&oacute; động&rdquo;, h&agrave;ng loạt cổ phiếu vốn rơi thảm v&agrave;i h&ocirc;m trước đột ngột &ldquo;lột x&aacute;c&rdquo;. Người chậm ch&acirc;n c&oacute; lẽ kh&ocirc;ng thể mua được v&igrave; lượng cầu qu&aacute; lớn.<br />\r\n		<br />\r\n		<strong> Penny sẽ &ldquo;l&ecirc;n hương&rdquo;?</strong><br />\r\n		<br />\r\n		Ngo&agrave;i c&aacute;c cổ phiếu &ldquo;n&oacute;ng&rdquo;, những m&atilde; tăng trần li&ecirc;n tục v&agrave;i phi&ecirc;n gần đ&acirc;y phần lớn l&agrave; cổ phiếu c&oacute; vốn h&oacute;a thấp, đặc biệt l&agrave; tr&ecirc;n s&agrave;n HNX.<br />\r\n		<br />\r\n		Thống k&ecirc; cho thấy, khoảng 30 m&atilde; tại HNX trong 7 phi&ecirc;n vừa qua c&oacute; mức tăng tr&ecirc;n 15%. Tại HOSE &ndash; d&ugrave; bi&ecirc;n độ nhỏ hơn &ndash; cũng c&oacute; 11 m&atilde; tăng tr&ecirc;n 15%, c&ograve;n số tăng tr&ecirc;n 10% l&ecirc;n tới 34 m&atilde;.<br />\r\n		<br />\r\n		Thực tế chỉ số chung của nh&oacute;m cổ phiếu c&oacute; vốn h&oacute;a dưới 500 tỷ đồng tại HOSE trong 7 phi&ecirc;n gần đ&acirc;y c&oacute; xu hướng tăng mạnh hơn VN-Index. Ngay trong c&aacute;c phi&ecirc;n giảm chung, nh&oacute;m n&agrave;y vẫn c&oacute; diễn biến tốt hơn thị trường. Cả s&agrave;n HOSE c&oacute; 34 m&atilde; tăng tr&ecirc;n 10% th&igrave; ri&ecirc;ng nh&oacute;m vốn h&oacute;a dưới 500 tỷ đồng đ&atilde; chiếm 22 m&atilde;. Tại HNX cũng tương tự: 26/30 cổ phiếu c&oacute; mức tăng từ 15% trở l&ecirc;n nằm trong nh&oacute;m vốn h&oacute;a dưới 500 tỷ đồng.<br />\r\n		<br />\r\n		Một phần kh&ocirc;ng nhỏ những penny vốn h&oacute;a thấp l&agrave; những doanh nghiệp l&agrave;m ăn kh&ocirc;ng tốt, thậm ch&iacute; lỗ trong v&agrave;i qu&yacute; gần đ&acirc;y. Đơn cử như <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=271kMyYEKfY=\\" target=\\"_blank\\">BAS</a></b> lỗ liền qu&yacute; 2 v&agrave; qu&yacute; 3/2010 khoảng 7,6 tỷ đồng v&agrave; lỗ lũy kế từ đầu năm khoảng 6,2 tỷ đồng nhưng vẫn c&oacute; v&agrave;i phi&ecirc;n tăng trần.<br />\r\n		<br />\r\n		<b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=1/Vwl3RQcUQ=\\" target=\\"_blank\\">TRI</a></b> qu&yacute; 2 nhờ b&aacute;n bớt t&agrave;i sản (cổ phần g&oacute;p vốn) lấy về tiền mặt mới ghi nhận l&atilde;i (41,9 tỷ đồng) nhưng thực tế d&ograve;ng tiền từ hoạt động sản xuất kinh doanh vẫn &acirc;m. Qu&yacute; 3 vừa qua <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=1/Vwl3RQcUQ=\\" target=\\"_blank\\">TRI</a></b> lại lỗ tiếp 4,7 tỷ đồng. Tuy nhi&ecirc;n điều đ&oacute; cũng kh&ocirc;ng hề g&igrave; v&igrave; <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=1/Vwl3RQcUQ=\\" target=\\"_blank\\">TRI</a></b> vẫn cho tỉ suất lợi nhuận khoảng 15% trong 7 phi&ecirc;n vừa qua v&agrave; hai phi&ecirc;n gần đ&acirc;y li&ecirc;n tục &ldquo;căng&rdquo; trần.<br />\r\n		<br />\r\n		Một điểm kh&aacute; th&uacute; vị l&agrave; số cổ phiếu đem lại tỉ suất lợi nhuận cao ở tr&ecirc;n phần lớn lại c&oacute; thị gi&aacute; rất thấp. Một phần ba số cổ phiếu đem lại lợi nhuận tr&ecirc;n 15% tại HNX c&oacute; gi&aacute; đang giao dịch nhỏ hơn 15.000 đồng/cổ phiếu. C&ugrave;ng khung gi&aacute; n&agrave;y, tại HOSE c&oacute; 18 m&atilde; đạt lợi nhuận tr&ecirc;n 10%.<br />\r\n		<br />\r\n		Loại trừ c&aacute;c cổ phiếu c&oacute; điều chỉnh gi&aacute; kỹ thuật, thị gi&aacute; thấp thường l&agrave; biểu hiện của những m&atilde; c&oacute; mức sụt giảm gi&aacute; mạnh từ đầu năm đến nay. Thị trường định gi&aacute; lại những m&atilde; n&agrave;y dưới t&aacute;c động của kết quả kinh doanh. Tuy nhi&ecirc;n từ việc những cổ phiếu như vậy phục hồi mạnh trở lại, c&oacute; thể đặt c&acirc;u hỏi liệu mức gi&aacute; đ&oacute; đ&atilde; phản &aacute;nh hết c&aacute;c rắc rối đ&oacute; hay chưa. Kh&ocirc;ng &iacute;t cổ phiếu rơi về mức gi&aacute; thấp đến kh&oacute; tin (v&agrave;i ng&agrave;n đồng) v&agrave; kh&ocirc;ng thể giảm th&ecirc;m nữa kể cả trong điều kiện thị trường rất xấu do kh&ocirc;ng c&ograve;n ai muốn b&aacute;n ra nữa. Một số m&atilde; thậm ch&iacute; c&ograve;n đ&oacute;ng băng giao dịch.<br />\r\n		<br />\r\n		N&oacute;i chung c&aacute;c cổ phiếu c&oacute; vốn h&oacute;a thấp, thị gi&aacute; nhỏ thường c&oacute; t&iacute;nh đầu cơ cao do chi ph&iacute; vốn để đẩy gi&aacute; l&ecirc;n kh&ocirc;ng cần nhiều. Một số cổ phiếu vốn h&oacute;a thấp đến mức chỉ cần một v&agrave;i nh&agrave; đầu tư lớn tham gia mua cũng c&oacute; thể khống chế hết lượng cổ phiếu tr&ocirc;i nổi. Đ&acirc;y ch&iacute;nh l&agrave; nguy&ecirc;n nh&acirc;n khiến c&aacute;c cổ phiếu n&agrave;y thường c&oacute; biến động rất mạnh.</span></p>\r\n	<p style=\\"text-align: justify;\\">\r\n		<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">&nbsp;</span></p>\r\n	<p style=\\"text-align: right;\\">\r\n		<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">(Theo VnEconomy)</span></p>\r\n</div>', '2010-12-07 19:44:18', NULL, 1, 0, 0);
INSERT INTO `articles` VALUES (37, 7, 1, 0, 'Cổ phiếu mía đường có “ngọt” như dự báo?', 'co-phieu-mia-duong-co-ngot-nhu-du-bao', 'Niên vụ cao điểm của ngành mía đường thường bắt đầu cuối quý III hàng năm. Và theo lẽ thông thường thì giá đường sẽ giảm do nguồn cung dồi dào. Tuy nhiên, giá đường năm 2010 lại đi ngược với quy...', 'files/news/20101207/20101207194650.jpg', '<div align=\\"justify\\">\r\n	<p>\r\n		<span id=\\"UcNewsDetail1_lblNoiDung\\">Ni&ecirc;n vụ cao điểm của ng&agrave;nh m&iacute;a đường thường bắt đầu cuối qu&yacute; III h&agrave;ng năm. V&agrave; theo lẽ th&ocirc;ng thường th&igrave; gi&aacute; đường sẽ giảm do nguồn cung dồi d&agrave;o. Tuy nhi&ecirc;n, gi&aacute; đường năm 2010 lại đi ngược với quy luật tr&ecirc;n khi li&ecirc;n tục tăng. T&iacute;nh từ đầu năm đến nay, gi&aacute; b&aacute;n lẻ đường trắng tr&ecirc;n thị trường đ&atilde; tăng từ mức 18.000 đồng/kg l&ecirc;n 22.000 đồng/kg. Cũng v&igrave; lẽ đ&oacute;, h&agrave;ng loạt b&aacute;o c&aacute;o ph&acirc;n t&iacute;ch về nh&oacute;m cổ phiếu ng&agrave;nh m&iacute;a đường được nhiều CTCK tung ra với những nhận định rất t&iacute;ch cực. C&aacute;c CTCK cũng đưa ra khuyến nghị nh&agrave; đầu tư c&oacute; thể xem x&eacute;t đầu tư trung v&agrave; d&agrave;i hạn nh&oacute;m cổ phiếu ng&agrave;nh m&iacute;a đường. Nhưng cổ phiếu ng&agrave;nh n&agrave;y c&oacute; được &quot;ngọt&quot; như dự b&aacute;o?<br />\r\n		<br />\r\n		Hiện nay, tr&ecirc;n TTCK tập trung c&oacute; 5 cổ phiếu ng&agrave;nh m&iacute;a đường v&agrave; đều giao dịch tại HOSE, đ&oacute; l&agrave; CTCP M&iacute;a đường Lam Sơn (<strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=uUaeI6n3VLA=\\" target=\\"_blank\\">LSS</a></strong>), CTCP Đường Bi&ecirc;n H&ograve;a (<strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=asymlEJiraU=\\" target=\\"_blank\\">BHS</a></strong>), CTCP Bourbon T&acirc;y Ninh (<strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=H0Df65MRXxo=\\" target=\\"_blank\\">SBT</a></strong>), CTCP M&iacute;a đường - Nhiệt điện Gia Lai (<strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=1Y+8wljF/1o=\\" target=\\"_blank\\">SEC</a></strong>) v&agrave; CTCP Đường Ninh H&ograve;a (<strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=httqK6Mrngw=\\" target=\\"_blank\\">NHS</a></strong>).<br />\r\n		<br />\r\n		Theo CTCK Ng&acirc;n h&agrave;ng Li&ecirc;n Việt (LVS), ng&agrave;nh m&iacute;a đường trong nước hiện c&oacute; rất nhiều yếu tố thuận lợi để ph&aacute;t triển như: gi&aacute; đường thế giới c&oacute; diễn biến thuận lợi v&agrave; dự b&aacute;o tiếp tục duy tr&igrave; được mặt bằng gi&aacute; cao. C&ograve;n trong nước, sản lượng ti&ecirc;u thụ sẽ tăng mạnh v&agrave;o cuối năm, do cung tăng do v&agrave;o vụ m&ugrave;a khai th&aacute;c của ng&agrave;nh v&agrave; cầu tăng khi đ&uacute;ng v&agrave;o dịp lễ tết&hellip;<br />\r\n		<br />\r\n		&Ocirc;ng V&otilde; Văn Minh, Gi&aacute;m đốc ph&acirc;n t&iacute;ch LVS cho biết, nh&oacute;m cổ phiếu m&iacute;a đường trong thời gian qua giữ gi&aacute; kh&aacute; tốt so với thị trường chung, điển h&igrave;nh như <strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=asymlEJiraU=\\" target=\\"_blank\\">BHS</a></strong>, <strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=uUaeI6n3VLA=\\" target=\\"_blank\\">LSS</a></strong> v&agrave; <strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=H0Df65MRXxo=\\" target=\\"_blank\\">SBT</a></strong> c&oacute; mức giảm &iacute;t hơn so với VN-Index, trong khi c&aacute;c chỉ số P/E, P/B của nh&oacute;m cổ phiếu n&agrave;y lại thấp hơn so với chỉ số chung. Hơn nữa, c&aacute;c doanh nghiệp n&agrave;y c&oacute; nguồn dự trữ đường từ nay đến cuối năm kh&aacute; tốt, do vậy, khả năng sẽ c&oacute; kết quả tốt trong qu&yacute; IV/2010 v&agrave; qu&yacute; I/2011. Điểm qua c&aacute;c đơn vị c&oacute; thể thấy, t&iacute;nh đến hết 9 th&aacute;ng đầu năm, một số doanh nghiệp m&iacute;a đường đ&atilde; ho&agrave;n th&agrave;nh vượt mức kế hoạch năm (<strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=asymlEJiraU=\\" target=\\"_blank\\">BHS</a></strong> ho&agrave;n th&agrave;nh 125%, <strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=uUaeI6n3VLA=\\" target=\\"_blank\\">LSS</a></strong> ho&agrave;n th&agrave;nh 108,66%...).<br />\r\n		<br />\r\n		CTCK Sacombank-SBS cũng vừa c&oacute; b&aacute;o c&aacute;o đ&aacute;nh gi&aacute; về cơ hội đầu tư v&agrave;o ng&agrave;nh đường trong giai đoạn hiện nay. Theo SBS, c&aacute;c doanh nghiệp ng&agrave;nh m&iacute;a đường đang c&oacute; kết quả kinh doanh ở mức kh&aacute; tốt so với đa số c&aacute;c ng&agrave;nh kh&aacute;c. Với t&igrave;nh h&igrave;nh gi&aacute; đường đang ổn định ở mức cao như hiện nay sẽ đảm bảo một khoản lợi nhuận tốt cho c&aacute;c c&ocirc;ng ty trong ng&agrave;nh. Tuy vậy, nhiều nh&agrave; đầu tư d&agrave;i hạn vẫn e ngại v&igrave; cho rằng, đ&acirc;y đang l&agrave; thời kỳ đỉnh cao của ng&agrave;nh v&agrave; c&oacute; khả năng đi xuống trong năm tới.</span></p>\r\n	<p>\r\n		<span id=\\"UcNewsDetail1_lblNoiDung\\"><strong>Kết quả kinh doanh 9 th&aacute;ng đầu năm 2010 của c&aacute;c DN ng&agrave;nh m&iacute;a đường (VT: Tỷ đồng)</strong><br />\r\n		Normal 0 false false false MicrosoftInternetExplorer4</span></p>\r\n	<p>\r\n		&nbsp;</p>\r\n	<table align=\\"center\\" border=\\"0\\" cellpadding=\\"0\\" cellspacing=\\"0\\" width=\\"79%\\">\r\n		<tbody>\r\n			<tr>\r\n				<td style=\\"width: 68px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\"><strong>M&atilde; CK</strong></span></p>\r\n				</td>\r\n				<td style=\\"width: 128px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\"><strong>Lợi nhuận sau thuế 9 th&aacute;ng đầu năm 2010</strong></span></p>\r\n				</td>\r\n				<td style=\\"width: 95px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\"><strong>Lợi nhuận kế hoạch năm 2010</strong></span></p>\r\n				</td>\r\n				<td style=\\"width: 104px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\"><strong>Ho&agrave;n th&agrave;nh so với kế hoạch (%)</strong></span></p>\r\n				</td>\r\n				<td style=\\"width: 113px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\"><strong>Tăng trưởng so với c&ugrave;ng kỳ (%)</strong></span></p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\\"width: 68px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\"><strong><strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=uUaeI6n3VLA=\\" target=\\"_blank\\">LSS</a></strong></strong></span></p>\r\n				</td>\r\n				<td style=\\"width: 128px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\">203,74</span></p>\r\n				</td>\r\n				<td style=\\"width: 95px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\">187,5</span></p>\r\n				</td>\r\n				<td style=\\"width: 104px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\">108,66</span></p>\r\n				</td>\r\n				<td style=\\"width: 113px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\">84,15</span></p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\\"width: 68px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\"><strong><strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=asymlEJiraU=\\" target=\\"_blank\\">BHS</a></strong></strong></span></p>\r\n				</td>\r\n				<td style=\\"width: 128px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\">100</span></p>\r\n				</td>\r\n				<td style=\\"width: 95px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\">80</span></p>\r\n				</td>\r\n				<td style=\\"width: 104px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\">233,53</span></p>\r\n				</td>\r\n				<td style=\\"width: 113px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\">22,65</span></p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\\"width: 68px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\"><strong><strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=H0Df65MRXxo=\\" target=\\"_blank\\">SBT</a></strong></strong></span></p>\r\n				</td>\r\n				<td style=\\"width: 128px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\">262</span></p>\r\n				</td>\r\n				<td style=\\"width: 95px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\">260</span></p>\r\n				</td>\r\n				<td style=\\"width: 104px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\">100,77</span></p>\r\n				</td>\r\n				<td style=\\"width: 113px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\">124,18</span></p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\\"width: 68px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\"><strong><strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=1Y+8wljF/1o=\\" target=\\"_blank\\">SEC</a></strong></strong></span></p>\r\n				</td>\r\n				<td style=\\"width: 128px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\">55,08</span></p>\r\n				</td>\r\n				<td style=\\"width: 95px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\">55</span></p>\r\n				</td>\r\n				<td style=\\"width: 104px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\">100,15</span></p>\r\n				</td>\r\n				<td style=\\"width: 113px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\">51,74</span></p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\\"width: 68px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\"><strong><strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=httqK6Mrngw=\\" target=\\"_blank\\">NHS</a></strong></strong></span></p>\r\n				</td>\r\n				<td style=\\"width: 128px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\">79,4</span></p>\r\n				</td>\r\n				<td style=\\"width: 95px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\">34</span></p>\r\n				</td>\r\n				<td style=\\"width: 104px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\">233,53</span></p>\r\n				</td>\r\n				<td style=\\"width: 113px;\\">\r\n					<p>\r\n						<span id=\\"UcNewsDetail1_lblNoiDung\\">75,55</span></p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	<p>\r\n		<span id=\\"UcNewsDetail1_lblNoiDung\\"><em>(Nguồn: B&aacute;o c&aacute;o kết quả kinh doanh của c&aacute;c DN)</em></span></p>\r\n	<p>\r\n		<span id=\\"UcNewsDetail1_lblNoiDung\\">Trao đổi với b&aacute;o ch&iacute;, &ocirc;ng L&ecirc; Văn Tam, Chủ tịch HĐQT <strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=uUaeI6n3VLA=\\" target=\\"_blank\\">LSS</a></strong> cho biết, kết quả kinh doanh 2 qu&yacute; cuối năm của C&ocirc;ng ty thường cao hơn 2 qu&yacute; đầu năm. Qu&yacute; III/2010, <strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=uUaeI6n3VLA=\\" target=\\"_blank\\">LSS</a></strong> đạt 52,51 tỷ đồng lợi nhuận sau thuế, tăng 68,3% so với c&ugrave;ng kỳ năm 2009. &Ocirc;ng Tam cũng khẳng định, lợi nhuận qu&yacute; IV của <strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=uUaeI6n3VLA=\\" target=\\"_blank\\">LSS</a></strong> cũng sẽ ngang bằng mức lợi nhuận&nbsp; qu&yacute; III. Lợi thế của <strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=uUaeI6n3VLA=\\" target=\\"_blank\\">LSS</a></strong> l&agrave; C&ocirc;ng ty lu&ocirc;n chủ động được nguồn nguy&ecirc;n liệu đầu v&agrave;o với gi&aacute; vốn rẻ, cộng với việc lường trước được t&igrave;nh h&igrave;nh biến động gi&aacute; cả n&ecirc;n <strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=uUaeI6n3VLA=\\" target=\\"_blank\\">LSS</a></strong> đ&atilde; chuẩn bị tốt nguy&ecirc;n liệu, đồng thời t&iacute;ch cực đầu tư n&acirc;ng cao năng suất trồng m&iacute;a. Theo &ocirc;ng Tam, gi&aacute; đường trong nước thời gian qua tăng mạnh xuất ph&aacute;t từ nhiều yếu tố, trước hết l&agrave; do t&igrave;nh h&igrave;nh gi&aacute; tăng chung của thị trường thế giới, ngo&agrave;i ra, gi&aacute; m&iacute;a hiện nay so với đầu năm đ&atilde; tăng l&ecirc;n 30%... Do vậy, c&aacute;c DN phải tăng gi&aacute; b&aacute;n đầu ra.<br />\r\n		<br />\r\n		C&ograve;n theo đại diện <strong><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=asymlEJiraU=\\" target=\\"_blank\\">BHS</a></strong>, sản lượng ti&ecirc;u thụ đường trong qu&yacute; IV/2010 của C&ocirc;ng ty dự kiến sẽ cao hơn so với qu&yacute; III, trong đ&oacute; chủ yếu từ lượng đường tồn kho cuối qu&yacute; trước. Tuy nhi&ecirc;n, do lượng tồn kho n&agrave;y c&oacute; mặt bằng gi&aacute; vốn cũng kh&aacute; cao n&ecirc;n lợi nhuận gộp dự kiến sẽ tương đương qu&yacute; trước, khoảng 28 - 32 tỷ đồng.<br />\r\n		<br />\r\n		Theo &yacute; kiến của một số chuy&ecirc;n gia, gi&aacute; đường tr&ecirc;n thế giới v&agrave; Việt Nam c&oacute; thể giảm nhẹ sau qu&yacute; I/2011. Hơn nữa, mặt bằng gi&aacute; m&iacute;a năm nay cao hơn năm ngo&aacute;i, do vậy, lợi nhuận năm 2011 của c&aacute;c DN sản xuất - kinh doanh ng&agrave;nh n&agrave;y c&oacute; thể kh&ocirc;ng bằng năm 2010.<br />\r\n		<br />\r\n		&Ocirc;ng Phạm Kinh Lu&acirc;n, chuy&ecirc;n gia chứng kho&aacute;n cho biết, DN sản xuất - kinh doanh trong ng&agrave;nh m&iacute;a đường thường c&oacute; t&iacute;nh thời vụ v&agrave; m&ugrave;a cao điểm thường tập trung v&agrave;o cuối qu&yacute; III, đầu qu&yacute; IV, do vậy để đầu tư v&agrave;o nh&oacute;m cổ phiếu ng&agrave;y th&igrave; nh&agrave; đầu tư phải xem x&eacute;t đến t&iacute;nh ổn đ&igrave;nh, bền vững. Theo &ocirc;ng Lu&acirc;n, gi&aacute; đường biến động tăng trong thời gian qua chỉ l&agrave; nhất thời, vấn đề l&agrave; c&oacute; chủ động, kiểm so&aacute;t được nguồn nguy&ecirc;n liệu ổn định để đảm bảo duy tr&igrave; sản lượng v&agrave; hiệu quả kinh doanh.</span></p>\r\n	<p>\r\n		<span id=\\"UcNewsDetail1_lblNoiDung\\">Theo ĐTCK</span></p>\r\n</div>', '2010-12-07 19:44:18', NULL, 1, 0, 0);
INSERT INTO `articles` VALUES (46, 2, 1, 0, 'PET: LNTT 11 tháng, vượt 39,3% kế hoạch cả năm', 'pet-lntt-11-thang-vuot-393-ke-hoach-ca-nam', 'Tổng Công ty Cổ phần Dịch vụ Tổng hợp Dầu khí (mã PET, HoSE), công bố lợi nhuận 11 tháng đầu 2010.', 'files/news/20101207/20101207220432.jpg', '<p>\r\n	<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">Theo đ&oacute;, c&ocirc;ng ty đạt 9.302 tỷ đ&ocirc;̀ng tổng doanh thu, vượt 20,8% kế hoạch cả năm. LNTT lũy kế 11 th&aacute;ng đầu năm 2010 của <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=59ag+wPr0K8=\\" target=\\"_blank\\">PET</a></b> đạt 209 tỷ đ&ocirc;̀ng vượt 39,3% kế hoạch cả năm.<br />\r\n	<br />\r\n	Theo <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=59ag+wPr0K8=\\" target=\\"_blank\\">PET</a></b>, trong năm 2010, mặc d&ugrave; gặp nhiều kh&oacute; khăn do sự biến động của l&atilde;i suất, tỷ gi&aacute; ngoại tệ&hellip; nhưng các mảng kinh doanh ch&iacute;nh của Tổng C&ocirc;ng ty như ph&acirc;n ph&ocirc;́i đi&ecirc;̣n thoại di đ&ocirc;̣ng, laptop và cung c&acirc;́p thi&ecirc;́t bị trong ngành D&acirc;̀u khí đã đạt được sự tăng trưởng t&ocirc;́t so với cùng kỳ năm 2009.<br />\r\n	<br />\r\n	Ngoài ra, năm 2010 Tổng C&ocirc;ng ty triển khai kinh doanh một số mặt h&agrave;ng mới như ph&acirc;n phối hạt nhựa PP, kinh doanh LPG v&agrave; bước đầu đ&atilde; đạt được những th&agrave;nh c&ocirc;ng.<br />\r\n	<br />\r\n	C&aacute;c mảng kinh doanh truyền thống của Tổng C&ocirc;ng ty như dịch vụ Catering, ăn uống, kh&aacute;ch sạn cũng đạt kết quả khả quan, góp ph&acirc;̀n vào k&ecirc;́t quả chung của toàn T&ocirc;̉ng C&ocirc;ng ty.</span></p>', '2010-12-07 22:03:45', NULL, 1, 0, 0);
INSERT INTO `articles` VALUES (39, 7, 1, 0, 'Chứng khoán: Cơ hội với áp lực lãi suất', 'chung-khoan-co-hoi-voi-ap-luc-lai-suat', 'Thị trường chứng khoán VN đang rẻ là cơ hội rất tốt để thu hút nguồn vốn ngoại vào thị trường. Tuy nhiên, ông Nguyễn Hồ Nam - chủ tịch HĐQT Công ty CP chứng khoán Sài Gòn Thương Tín (SBS) - nhận...', 'files/news/20101207/20101207195349.jpg', '<div align=\\"justify\\">\r\n	<p style=\\"text-align: justify;\\">\r\n		<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">- Việc Mỹ tiếp tục đưa ra g&oacute;i cứu trợ 600 tỉ USD đ&atilde; tạo một lượng tiền rất lớn ra thị trường. Trong khi nền kinh tế Mỹ chưa đi v&agrave;o chu kỳ thật sự hồi phục n&ecirc;n d&ograve;ng tiền n&agrave;y t&igrave;m cơ hội đầu tư ở c&aacute;c nước kh&aacute;c c&oacute; khả năng sinh lợi cao hơn. Một phần của d&ograve;ng tiền n&agrave;y chạy v&agrave;o c&aacute;c thị trường mới nổi như Th&aacute;i Lan, Indonesia, Singapore... đang c&oacute; những t&aacute;c động t&iacute;ch cực gi&uacute;p nền kinh tế ở c&aacute;c nước n&agrave;y tăng trưởng.<br />\r\n		<br />\r\n		Trước đ&acirc;y, khủng hoảng đ&atilde; t&aacute;c động trực tiếp đến k&ecirc;nh huy động vốn của c&aacute;c doanh nghiệp ở những thị trường n&agrave;y. Đến nay d&ograve;ng vốn đưa v&agrave;o thị trường gi&uacute;p c&aacute;c thị trường n&agrave;y hồi phục. Mặc d&ugrave; chưa quay lại mức tăng trưởng đỉnh điểm trước đ&acirc;y nhưng n&oacute; đ&atilde; tạo ra một t&aacute;c động rất lớn, gi&uacute;p những đợt IPO mới của c&aacute;c doanh nghiệp ở c&aacute;c nước n&agrave;y rất th&agrave;nh c&ocirc;ng.<br />\r\n		<br />\r\n		<strong><em>* Nhưng thị trường chứng kho&aacute;n nước ta lại eo s&egrave;o?</em></strong><br />\r\n		<br />\r\n		- Nếu đưa ra những chỉ số ph&acirc;n t&iacute;ch cơ bản th&igrave; c&aacute;c chuy&ecirc;n gia, c&aacute;c định chế t&agrave;i ch&iacute;nh trong nước lẫn quốc tế đều c&ocirc;ng nhận hiện thị trường chứng kho&aacute;n VN rẻ khoảng 30-40% so với thị trường chứng kho&aacute;n trong khu vực. Về nguy&ecirc;n tắc, rẻ th&igrave; d&ograve;ng vốn phải chảy v&agrave;o VN nhưng thực tế thời gian vừa qua c&oacute; thể thấy d&ograve;ng vốn nước ngo&agrave;i chưa chạy v&agrave;o thị trường chứng kho&aacute;n VN.<br />\r\n		<br />\r\n		Trong khi ở thị trường chứng kho&aacute;n VN, do ch&iacute;nh s&aacute;ch kiềm chế lạm ph&aacute;t n&ecirc;n d&ograve;ng vốn nội ng&agrave;y c&agrave;ng giảm, đồng thời với thanh khoản của thị trường chứng kho&aacute;n ng&agrave;y c&agrave;ng đi xuống n&ecirc;n niềm tin của nh&agrave; đầu tư trong nước cũng bị x&oacute;i m&ograve;n, l&agrave;m d&ograve;ng vốn c&agrave;ng bị r&uacute;t ra khỏi thị trường. V&igrave; vậy, trong thời gian vừa qua c&aacute;c doanh nghiệp VN kh&ocirc;ng c&oacute; khả năng huy động vốn tr&ecirc;n thị trường chứng kho&aacute;n, khiến nhiều kế hoạch ph&aacute;t h&agrave;nh cổ phiếu tăng vốn điều lệ bị ph&aacute; sản, khả năng th&agrave;nh c&ocirc;ng của c&aacute;c IPO rất &iacute;t.<br />\r\n		<br />\r\n		C&aacute;c doanh nghiệp VN r&otilde; r&agrave;ng đang đứng trong bối cảnh rất kh&oacute; khăn, một đằng huy động vốn vay từ c&aacute;c ng&acirc;n h&agrave;ng thương mại c&agrave;ng kh&oacute; khăn hơn do l&atilde;i suất tăng cao đột biến, một đằng lựa chọn huy động vốn từ thị trường chứng kho&aacute;n kh&ocirc;ng thực hiện được.<br />\r\n		<br />\r\n		<strong><em>* Theo &ocirc;ng, tại sao d&ograve;ng vốn ngoại n&agrave;y chưa chảy v&agrave;o VN?</em></strong><br />\r\n		<br />\r\n		- Nguy&ecirc;n nh&acirc;n c&oacute; thể thấy r&otilde; nhất l&agrave; kinh tế vĩ m&ocirc; chưa ổn định v&agrave; chưa tạo sự an l&ograve;ng cho c&aacute;c nh&agrave; đầu tư. Yếu tố vĩ m&ocirc; t&aacute;c động trực tiếp, trực diện đến d&ograve;ng vốn ngoại l&agrave; tỉ gi&aacute; giữa VND v&agrave; USD chưa ổn định. Khi tiền đổ v&agrave;o đầu tư m&agrave; kh&ocirc;ng t&iacute;nh to&aacute;n được tỉ gi&aacute; biến động như thế n&agrave;o th&igrave; r&otilde; r&agrave;ng c&aacute;c nh&agrave; đầu tư nước ngo&agrave;i sẽ kh&ocirc;ng d&aacute;m đ&aacute;nh cược. Đ&oacute; l&agrave; r&agrave;o cản lớn nhất l&agrave;m d&ograve;ng vốn nước ngo&agrave;i hiện tại chưa đổ v&agrave;o.<br />\r\n		<br />\r\n		<strong><em> * Nhưng với động th&aacute;i mua r&ograve;ng của c&aacute;c nh&agrave; đầu tư ngoại thời gian qua, liệu c&oacute; phải khả năng hỗ trợ bởi d&ograve;ng tiền của lượng nh&agrave; đầu tư nước ngo&agrave;i mới?</em></strong><br />\r\n		<br />\r\n		- C&aacute;c quỹ đầu tư nước ngo&agrave;i tại VN hiện c&oacute; nguồn vốn lớn v&agrave; d&agrave;i hạn hơn nh&agrave; đầu tư nội địa n&ecirc;n họ c&oacute; thể trường vốn trong đầu tư hơn. Khi thị trường chứng kho&aacute;n trở n&ecirc;n rẻ hơn, c&aacute;c nh&agrave; đầu tư tổ chức nước ngo&agrave;i đang c&oacute; mặt ở VN cũng thấy được điều n&agrave;y v&agrave; đ&oacute; l&agrave; l&yacute; do để họ giải ng&acirc;n. V&igrave; thực tế tiền của họ cũng đang nằm ở VN, rủi ro về tỉ gi&aacute; đối với họ nếu c&oacute; th&igrave; đ&atilde; xảy ra rồi n&ecirc;n họ vận dụng cơ hội đầu tư trong thị trường hiện tại. Tuy nhi&ecirc;n, khối lượng quỹ đầu tư ở VN quy m&ocirc; đang nhỏ so với nhu cầu vốn của c&aacute;c doanh nghiệp VN trong giai đoạn hiện tại, cũng như chưa đủ sức để gi&uacute;p phục hồi thị trường chứng kho&aacute;n.<br />\r\n		<br />\r\n		<strong><em>* Vậy theo &ocirc;ng, cần c&oacute; những giải ph&aacute;p g&igrave; để thu h&uacute;t d&ograve;ng vốn ngoại?</em></strong><br />\r\n		<br />\r\n		- Thị trường chứng kho&aacute;n VN đang rẻ l&agrave; cơ hội rất tốt để thu h&uacute;t nguồn vốn v&agrave;o thị trường, đặc biệt l&agrave; nguồn vốn nước ngo&agrave;i. Tuy nhi&ecirc;n, để thu h&uacute;t được d&ograve;ng vốn ngoại trước ti&ecirc;n phải ổn định kinh tế vĩ m&ocirc;, nhất l&agrave; ổn định được tỉ gi&aacute;.<br />\r\n		<br />\r\n		Ch&iacute;nh phủ n&ecirc;n mạnh dạn bỏ ra một khoản tiền nhất định để ổn định tỉ gi&aacute;. Coi đ&acirc;y như l&agrave; chi ph&iacute; đầu tư, khi tỉ gi&aacute; ổn định th&igrave; nguồn vốn ngoại v&agrave;o thị trường sẽ l&agrave;m tăng dự trữ VN, gi&uacute;p c&acirc;n bằng c&aacute;n c&acirc;n vĩ m&ocirc;. &Iacute;t nhiều g&igrave; một phần của lượng vốn ngoại n&agrave;y sẽ đi thẳng v&agrave;o sản xuất kinh doanh của doanh nghiệp, t&aacute;c động t&iacute;ch cực qu&aacute; tr&igrave;nh hồi phục kinh tế v&agrave; c&acirc;n bằng c&aacute;n c&acirc;n thanh to&aacute;n quốc gia.<br />\r\n		<br />\r\n		Trong giai đoạn n&agrave;y l&agrave; cơ hội rất lớn để thu h&uacute;t lượng vốn từ đầu tư nước ngo&agrave;i so với c&aacute;c nước trong khu vực. Ch&uacute;ng ta đ&atilde; lỡ chuyến t&agrave;u đầu, nếu kh&ocirc;ng sớm c&oacute; những giải ph&aacute;p kịp thời để h&uacute;t vốn th&igrave; sẽ trễ chuyến t&agrave;u cuối, tức d&ograve;ng vốn ngoại vẫn đang tiếp tục đổ v&agrave;o c&aacute;c thị trường kh&aacute;c theo nguy&ecirc;n tắc &ldquo;nước chảy chỗ trũng&rdquo;.</span></p>\r\n	<p style=\\"text-align: justify;\\">\r\n		<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">&nbsp;</span></p>\r\n	<p style=\\"text-align: right;\\">\r\n		<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">(Theo tT)</span></p>\r\n</div>', '2010-12-07 19:44:18', NULL, 1, 0, 0);
INSERT INTO `articles` VALUES (40, 7, 1, 0, 'Cổ phiếu chứng khoán “bốc đầu”', 'co-phieu-chung-khoan-boc-dau', 'Lượng cầu “khủng” đẩy giá những cổ phiếu chứng khoán hàng đầu bật tăng trong phiên ngày 30/11 đem lại kỳ vọng về một sự khởi sắc dài hơi.', 'files/news/20101207/20101207195436.jpg', '<p>\r\n	Nh&oacute;m cổ phiếu chứng kho&aacute;n trước đ&acirc;y vẫn được xem l&agrave; chỉ b&aacute;o của d&ograve;ng vốn đầu cơ. Tuy nhi&ecirc;n kể từ đầu qu&yacute; 3 đến nay, nh&oacute;m cổ phiếu n&agrave;y thực sự g&acirc;y thất vọng với kết quả kinh doanh tồi tệ c&ugrave;ng với diễn biến &egrave;o uột của thị trường. Liệu những chuyển biến bất ngờ gần đ&acirc;y c&oacute; l&agrave;m thay đổi xu hướng?<br />\r\n	<br />\r\n	Phi&ecirc;n giao dịch cuối c&ugrave;ng của th&aacute;ng 11 chứng kiến diễn biến thường thấy trong một chu kỳ tăng d&agrave;i với lượng cầu chặn mua gi&aacute; trần cực lớn ở những m&atilde; như <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=aydOcrXmqUA=\\" target=\\"_blank\\">SSI</a></b>, <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=YuNIMSsyRXk=\\" target=\\"_blank\\">KLS</a></b>, <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=kSSopH/yTOU=\\" target=\\"_blank\\">BVS</a></b>, <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=5ZPXQHjvgFY=\\" target=\\"_blank\\">HCM</a></b>. Gần như to&agrave;n bộ nh&oacute;m cổ phiếu chứng kho&aacute;n trong phi&ecirc;n h&ocirc;m nay đều tăng gi&aacute;, nhưng t&acirc;m điểm ch&uacute; &yacute; thường tập trung v&agrave;o c&aacute;c m&atilde; dẫn dắt.<br />\r\n	<br />\r\n	<b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=aydOcrXmqUA=\\" target=\\"_blank\\">SSI</a></b> c&oacute; phi&ecirc;n tăng trần thứ hai li&ecirc;n tiếp với khối lượng khớp lệnh đột biến: gần 4,02 triệu cổ phiếu. Sổ lệnh khớp với <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=aydOcrXmqUA=\\" target=\\"_blank\\">SSI</a></b> cho thấy chỉ c&oacute; 3 mức gi&aacute; khớp, từ 24.500 đồng/cổ phiếu tới 24.700 đồng cổ phiếu. Tại gi&aacute; trần, <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=aydOcrXmqUA=\\" target=\\"_blank\\">SSI</a></b> giao dịch tới 3,15 triệu đơn vị, chiếm 78,5% tổng thanh khoản. Cuối ng&agrave;y <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=aydOcrXmqUA=\\" target=\\"_blank\\">SSI</a></b> vẫn dư mua trần tr&ecirc;n 178.000 đơn vị, chưa kể lượng mua ATC.<br />\r\n	<br />\r\n	<b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=YuNIMSsyRXk=\\" target=\\"_blank\\">KLS</a></b> c&oacute; một phi&ecirc;n giao dịch &ldquo;khủng&rdquo; với kịch bản quen thuộc: gi&aacute; được &ldquo;đ&oacute;ng đinh&rdquo; mức trần do lượng cầu chặn ngay từ l&uacute;c mở cửa. To&agrave;n bộ 1,85 triệu <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=YuNIMSsyRXk=\\" target=\\"_blank\\">KLS</a></b> được khớp h&ocirc;m nay l&agrave; b&ecirc;n b&aacute;n tho&aacute;t h&agrave;ng. Thanh khoản sụt giảm khoảng 59% so với phi&ecirc;n trước. C&oacute; lẽ cung kh&ocirc;ng &ldquo;d&aacute;m&rdquo; b&aacute;n mạnh khi b&ecirc;n mua biểu dương lực lượng h&ugrave;ng hậu như vậy.<br />\r\n	<br />\r\n	Kịch bản tương tự cũng đến với <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=kSSopH/yTOU=\\" target=\\"_blank\\">BVS</a></b> v&agrave; cổ phiếu n&agrave;y c&ugrave;ng với <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=YuNIMSsyRXk=\\" target=\\"_blank\\">KLS</a></b> hợp th&agrave;nh bộ đ&ocirc;i &ldquo;đ&igrave;nh đ&aacute;m&rdquo; tr&ecirc;n HNX: Cầu chặn mua l&uacute;c đ&oacute;ng cửa vẫn c&ograve;n khoảng 615.000 đơn vị ở gi&aacute; trần. Tuy nhi&ecirc;n lượng giao dịch của <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=kSSopH/yTOU=\\" target=\\"_blank\\">BVS</a></b> kh&aacute; thấp với hơn 705.000 đơn vị v&agrave; chỉ c&oacute; một mức gi&aacute; khớp tại trần.<br />\r\n	<br />\r\n	<b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=5ZPXQHjvgFY=\\" target=\\"_blank\\">HCM</a></b> &ldquo;đuối&rdquo; nhất trong nh&oacute;m.&nbsp; <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=5ZPXQHjvgFY=\\" target=\\"_blank\\">HCM</a></b> l&agrave; một trong những m&atilde; blue-chip của HOSE bị xả trần kh&aacute; sớm v&agrave; bị b&aacute;n &eacute;p về gi&aacute; 29.700 đồng/cổ phiếu, thấp hơn tới 5 bước gi&aacute; so với mức cao nhất.<br />\r\n	<br />\r\n	Đặc điểm chung của đa số cổ phiếu nh&oacute;m chứng kho&aacute;n hai phi&ecirc;n gần đ&acirc;y l&agrave; đều xuất hiện khối lượng giao dịch đột biến. <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=aydOcrXmqUA=\\" target=\\"_blank\\">SSI</a></b> h&ocirc;m nay thanh khoản kỷ lục trong 6 th&aacute;ng gần đ&acirc;y. <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=YuNIMSsyRXk=\\" target=\\"_blank\\">KLS</a></b> h&ocirc;m qua cũng nỗ lực ti&ecirc;u h&oacute;a lượng b&aacute;n ra mạnh nhất trong v&ograve;ng 2 th&aacute;ng. <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=kSSopH/yTOU=\\" target=\\"_blank\\">BVS</a></b> đạt mức giao dịch cao nhất từ ng&agrave;y 19/10/2010.<br />\r\n	<br />\r\n	Dĩ nhi&ecirc;n kh&ocirc;ng phải tất cả c&aacute;c cổ phiếu thuộc ng&agrave;nh n&agrave;y c&ugrave;ng tăng như nhau. <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=Acw/RFGmO3M=\\" target=\\"_blank\\">SBS</a></b> l&agrave; v&iacute; dụ về sự lạc l&otilde;ng khi chỉ khớp loanh quanh tham chiếu h&ocirc;m nay. Số kh&aacute;c tăng yếu như <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=k75Jx5JNNHQ=\\" target=\\"_blank\\">APG</a></b>, <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=JKopfW7tvkk=\\" target=\\"_blank\\">GBS</a></b>, <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=N7hBnuX+q9w=\\" target=\\"_blank\\">PHS</a></b>, <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=W2HEQsD4Fws=\\" target=\\"_blank\\">VDS</a></b>... Tuy nhi&ecirc;n những cổ phiếu của c&aacute;c c&ocirc;ng ty chứng kho&aacute;n h&agrave;ng đầu hay những m&atilde; c&oacute; t&iacute;nh đầu cơ cao lu&ocirc;n xảy ra đột biến từ rất sớm. Sự lan tỏa từ những m&atilde; n&agrave;y ảnh hưởng mạnh đến c&aacute;c cổ phiếu kh&aacute;c trong ng&agrave;nh. <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=c0H6px98bHo=\\" target=\\"_blank\\">VIX</a></b> &ndash; cổ phiếu của C&ocirc;ng ty Chứng kho&aacute;n Vincom từng bị b&aacute;n đổ b&aacute;n th&aacute;o với tin thu hẹp hoạt động, giải t&aacute;n chi nh&aacute;nh H&agrave; Nội h&ocirc;m nay cũng kịch trần.<br />\r\n	<br />\r\n	Nh&oacute;m chung 25 cổ phiếu chứng kho&aacute;n đang ni&ecirc;m yết tr&ecirc;n cả hai s&agrave;n lại th&agrave;nh một chỉ số chung th&igrave; diễn biến trong 7 phi&ecirc;n gần đ&acirc;y kh&aacute; tốt: tăng trưởng đạt 13,3%. Căn cứ theo chỉ số n&agrave;y th&igrave; nh&oacute;m cổ phiếu chứng kho&aacute;n đ&atilde; giảm rất mạnh kể từ th&aacute;ng 7 đến nay. Đợt giảm mạnh nhất l&agrave; qu&yacute; 3 vừa qua, ph&ugrave; hợp với kết quả kinh doanh kh&ocirc;ng mấy khả quan.<br />\r\n	<br />\r\n	<strong> Vậy nguy&ecirc;n nh&acirc;n của s&oacute;ng tăng gi&aacute; đang diễn ra l&agrave; g&igrave;?</strong><br />\r\n	<br />\r\n	Mức độ giảm gi&aacute; của c&aacute;c cổ phiếu chứng kho&aacute;n đ&atilde; kh&aacute; mạnh v&agrave; rất c&oacute; thể đ&atilde; phản &aacute;nh hết những g&igrave; xấu nhất của qu&yacute; 3 vừa qua. T&iacute;nh từ đầu th&aacute;ng 9/2010 đến thời điểm VN-Index chạm đ&aacute;y ng&agrave;y 22/11, h&agrave;ng loạt cổ phiếu chứng kho&aacute;n giảm tương đối mạnh: <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=aydOcrXmqUA=\\" target=\\"_blank\\">SSI</a></b> giảm 23,5% về gi&aacute; thấp nhất 21.800 đồng/cổ phiếu; <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=kSSopH/yTOU=\\" target=\\"_blank\\">BVS</a></b> giảm 38,4%, gi&aacute; thấp nhất 13.400 đồng/cổ phiếu; <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=5ZPXQHjvgFY=\\" target=\\"_blank\\">HCM</a></b> giảm 24,6%, gi&aacute; thấp nhất 26.100 đồng/cổ phiếu...<br />\r\n	<br />\r\n	Với nh&oacute;m cổ phiếu của c&ocirc;ng ty chứng kho&aacute;n nhỏ hơn, mức giảm gi&aacute; c&ograve;n lớn hơn, khoảng 20-30% so với thời điểm đầu qu&yacute; 3. Ngay đến thời điểm n&agrave;y, cũng c&oacute; khoảng 19 cổ phiếu trong nh&oacute;m đang giao dịch loanh quanh mệnh gi&aacute;. Mức gi&aacute; thấp như vậy c&oacute; thể tạo ấn tượng &ldquo;rẻ&rdquo; v&agrave; kh&oacute; c&oacute; thể giảm s&acirc;u hơn.<br />\r\n	<br />\r\n	Triển vọng kinh doanh của c&aacute;c c&ocirc;ng ty chứng kho&aacute;n qu&yacute; 4 vẫn l&agrave; ẩn số. Thanh khoản thời gian qua giảm, mảng dịch vụ t&agrave;i ch&iacute;nh kh&oacute; &ldquo;kiếm&rdquo; nhiều như trước, gi&aacute; đa số cổ phiếu chưa phục hồi được bao nhi&ecirc;u. Khả năng ho&agrave;n nhập dự ph&ograve;ng cũng chưa r&otilde; r&agrave;ng. Một số c&ocirc;ng ty ghi nhận lượng tiền mặt lớn cuối qu&yacute; 3 c&oacute; thể nắm được lợi thế nhưng cũng chưa hẳn sẽ tối đa h&oacute;a được lợi nhuận từ lượng tiền mặt đ&oacute;.<br />\r\n	<br />\r\n	Tuy nhi&ecirc;n, thị trường c&oacute; thể suy luận xa hơn: Liệu đ&aacute;y xấu nhất của thị trường năm nay đ&atilde; x&aacute;c lập chưa? Hiện tại ngưỡng 420 điểm của VN-index được xem l&agrave; trụ vững ngay cả những t&igrave;nh huống bi quan nhất. Khả năng phục hồi đến đ&acirc;u chưa r&otilde;, nhưng nếu kh&ocirc;ng thể xấu hơn th&igrave; rất c&oacute; thể c&aacute;c c&ocirc;ng ty chứng kho&aacute;n đ&atilde; phải g&aacute;nh chịu những t&aacute;c động bất lợi nhất v&agrave; gi&aacute; c&oacute; thể đ&atilde; chạm đ&aacute;y.<br />\r\n	<br />\r\n	Khối lượng giao dịch tăng vọt ở đa số cổ phiếu chứng kho&aacute;n hai phi&ecirc;n gần đ&acirc;y cho thấy d&ograve;ng vốn đầu cơ bắt đầu chảy mạnh. Kh&ocirc;ng phải ngẫu nhi&ecirc;n d&ograve;ng vốn n&agrave;y chấp nhận mua v&agrave;o khối lượng lớn như vậy. Khi gi&aacute; đ&atilde; giảm s&acirc;u th&igrave; chi ph&iacute; vốn để giải quyết khối lượng cổ phiếu sẵn s&agrave;ng cắt lỗ cũng kh&ocirc;ng qu&aacute; lớn. <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=aydOcrXmqUA=\\" target=\\"_blank\\">SSI</a></b> l&agrave; v&iacute; dụ: h&ocirc;m nay xả h&agrave;ng rất mạnh, gi&aacute; thậm ch&iacute; l&ugrave;i nhẹ khỏi mức s&agrave;n nhưng rốt cuộc vẫn được đẩy l&ecirc;n vững chắc. Th&ocirc;ng thường d&ograve;ng vốn lớn chủ động v&agrave;o trước v&agrave; ph&aacute;t t&iacute;n hiệu r&otilde; r&agrave;ng th&igrave; số đ&ocirc;ng c&ograve;n lại mới c&ugrave;ng tham gia. C&aacute;c m&atilde; cổ phiếu chứng kho&aacute;n &ldquo;n&oacute;ng&rdquo; h&ocirc;m nay đều thanh khoản cả mua v&agrave; b&aacute;n dễ d&agrave;ng chỉ c&aacute;ch đ&oacute; v&agrave;i phi&ecirc;n.<br />\r\n	<br />\r\n	D&ograve;ng vốn đầu cơ chảy mạnh sẽ tạo ra sức đẩy. Khi cơ hội lợi nhuận nhiều th&aacute;ng nay kh&aacute; hiếm hoi th&igrave; nh&agrave; đầu cơ kh&oacute; c&oacute; thể bỏ qua.</p>\r\n<p style=\\"text-align: right;\\">\r\n	(Theo Vneconomy</p>', '2010-12-07 19:44:18', NULL, 1, 1, 0);
INSERT INTO `articles` VALUES (41, 7, 1, 0, 'Chứng khoán hết trầm lắng?', 'chung-khoan-het-tram-lang', 'Kết thúc tháng 11, thị trường chứng khoán có phiên giao dịch sôi động làm cho nhà đầu tư có thêm chút niềm vui sau quãng thời gian dài thua lỗ nặng. Trên sàn TPHCM, chỉ số VN-Index tiếp tục tăng...', 'files/news/20101207/20101207195518.jpg', '<p>\r\n	C&ograve;n tr&ecirc;n s&agrave;n H&agrave; Nội, giữa 5 phi&ecirc;n tăng kh&aacute; mạnh chỉ c&oacute; 1 phi&ecirc;n điều chỉnh giảm nhẹ. Nh&agrave; đầu tư hy vọng đ&acirc;y l&agrave; dấu hiệu cho thấy những ng&agrave;y c&ograve;n lại của năm chứng kho&aacute;n sẽ hết trầm lắng.<br />\r\n	<strong>&nbsp;<br />\r\n	Đỉnh lạm ph&aacute;t l&agrave; đ&aacute;y chứng kho&aacute;n</strong><br />\r\n	&nbsp;<br />\r\n	Nhiều người cho rằng v&igrave; kinh tế vĩ m&ocirc; đang trong thời kỳ bất ổn như: lạm ph&aacute;t tăng rất cao (CPI th&aacute;ng 11 l&ecirc;n tới 1,86%, được cho l&agrave; cao nhất của năm nay), l&atilde;i suất t&iacute;n dụng đang bước v&agrave;o thời kỳ cạnh tranh huy động, tỉ gi&aacute; USD v&agrave; gi&aacute; v&agrave;ng đang biến động kh&oacute; lường... n&ecirc;n chứng kho&aacute;n chưa thể phục hồi trở lại...<br />\r\n	&nbsp;<br />\r\n	Tuy nhi&ecirc;n, trong mấy ng&agrave;y qua, giao dịch tr&ecirc;n thị trường chứng kho&aacute;n trở n&ecirc;n s&ocirc;i động, gi&aacute; trị giao dịch ng&agrave;y c&agrave;ng tăng l&ecirc;n, số m&atilde; cổ phiếu tăng gi&aacute; chiếm ưu thế tr&ecirc;n bảng điện tử, sự biến thi&ecirc;n gi&aacute; v&agrave; lệnh cạnh tranh mua đ&atilde; xuất hiện với tần suất mạnh hơn.<br />\r\n	&nbsp;<br />\r\n	Nhiều nh&agrave; đầu tư cho rằng đỉnh cao của lạm ph&aacute;t l&agrave; đ&aacute;y của chứng kho&aacute;n v&agrave; chứng kho&aacute;n thường đi l&ecirc;n trước sự ổn định của kinh tế vĩ m&ocirc; từ 3 &ndash; 4 th&aacute;ng, v&igrave; vậy nhiều người đ&atilde; &ldquo;liều lĩnh&rdquo; đổ tiền v&agrave;o bắt đ&aacute;y, mua được cổ phiếu với gi&aacute; &ldquo;b&egrave;o&rdquo;.<br />\r\n	&nbsp;<br />\r\n	Sự can đảm của những nh&agrave; đầu tư ti&ecirc;n phong đ&atilde; được đền đ&aacute;p với mức l&atilde;i cổ phiếu đạt từ 10% - 15%. Khi nh&agrave; đầu tư c&aacute; nh&acirc;n h&acirc;m n&oacute;ng thị trường, nhiều tổ chức v&agrave; c&aacute; nh&acirc;n lớn nhảy tiếp v&agrave;o tham gia l&agrave;m cho thị trường mấy ng&agrave;y qua ấm trở lại.<br />\r\n	<br />\r\n	Đặc biệt, trong ng&agrave;y 30-11, giao dịch rất s&ocirc;i động, với h&agrave;ng loạt m&atilde; tăng trần, khối lượng v&agrave; gi&aacute; trị l&ecirc;n cao đột biến. T&iacute;nh chung cả hai s&agrave;n đ&atilde; c&oacute; 2.408 tỉ đồng giao dịch th&agrave;nh c&ocirc;ng, với 122 triệu cổ phiếu trao tay, nhiều gấp 2 lần trung b&igrave;nh ng&agrave;y của tuần trước.<br />\r\n	&nbsp;<br />\r\n	&Ocirc;ng Nguyễn Quang, một nh&agrave; đầu tư l&acirc;u năm tr&ecirc;n s&agrave;n SSI, n&oacute;i rằng: Mặc d&ugrave; d&ograve;ng tiền đổ v&agrave;o c&ograve;n khi&ecirc;m tốn, sự nghi ngờ về đợt s&oacute;ng n&agrave;y chưa tan hết trong t&acirc;m l&yacute; cộng đồng nh&agrave; đầu tư nhưng nếu chờ đến khi &ldquo;mọi việc r&otilde; r&agrave;ng&rdquo; mới giải ng&acirc;n th&igrave; e rằng... đ&atilde; muộn.<br />\r\n	<strong>&nbsp;<br />\r\n	Bị n&eacute;n l&acirc;u ng&agrave;y</strong><br />\r\n	&nbsp;<br />\r\n	Sự b&ugrave;ng nổ về giao dịch trong phi&ecirc;n cuối th&aacute;ng 11 thể hiện thị trường bị n&eacute;n l&acirc;u ng&agrave;y, nay mới c&oacute; dịp bung ra. &Ocirc;ng Huỳnh Anh Tuấn, Tổng Gi&aacute;m đốc C&ocirc;ng ty Chứng kho&aacute;n SJC, cho biết: &ldquo;Nhiều nh&agrave; đầu tư l&acirc;u nay &ocirc;m h&agrave;ng tỉ đồng m&agrave; kh&ocirc;ng d&aacute;m giải ng&acirc;n bởi thấy thị trường &egrave;o uột qu&aacute;.<br />\r\n	<br />\r\n	Đại đa số để tiền trong t&agrave;i khoản hoặc chuyển sang gửi tiết kiệm kỳ hạn cực ngắn để hưởng ch&uacute;t l&atilde;i c&ograve;m v&agrave; chờ đợi thời cơ. Tại C&ocirc;ng ty SJC, số nh&agrave; đầu tư c&aacute; nh&acirc;n &ocirc;m mỗi người từ 3 &ndash; 5 tỉ đồng chờ giải ng&acirc;n l&agrave; kh&aacute; nhiều&rdquo;.<br />\r\n	&nbsp;<br />\r\n	Như vậy, giờ đ&acirc;y, khi thấy thị trường đ&atilde; chạm đ&aacute;y v&agrave; xu hướng tăng đ&atilde; h&igrave;nh th&agrave;nh n&ecirc;n d&ograve;ng tiền l&acirc;u nay nằm chờ đang được chuyển dần sang mua cổ phiếu, v&igrave; vậy trong 2 ng&agrave;y qua, thị trường chứng kho&aacute;n trở n&ecirc;n s&ocirc;i động.<br />\r\n	&nbsp;<br />\r\n	Hiện d&ograve;ng tiền đang đổ v&agrave;o những cổ phiếu c&oacute; lịch sử biến động mạnh, gi&aacute; đ&atilde; bị giảm xuống s&acirc;u trong thời gian qua. Đại đa số nh&oacute;m n&agrave;y tập trung ở những m&atilde; penny.<br />\r\n	&nbsp;<br />\r\n	Khi những m&atilde; n&agrave;y gi&aacute; đ&atilde; l&ecirc;n cao, những người tho&aacute;i vốn xong sẽ lại đầu tư tiếp v&agrave;o những cổ phiếu c&oacute; chỉ số cơ bản tốt nhưng gi&aacute; chưa tăng. Nếu t&igrave;nh h&igrave;nh kinh tế vĩ m&ocirc; kh&ocirc;ng xấu th&ecirc;m th&igrave; giao dịch chứng kho&aacute;n sẽ tho&aacute;t cảnh chợ chiều.</p>\r\n<p style=\\"text-align: right;\\">\r\n	(Theo NLĐ)</p>', '2010-12-07 19:44:18', NULL, 1, 1, 0);
INSERT INTO `articles` VALUES (42, 7, 1, 0, 'Cổ phiếu sốt bí ẩn', 'co-phieu-sot-bi-an', 'Bất chấp đà giảm của chứng khoán thế giới cùng những tin tức không mấy tích cực về kinh tế vĩ mô trong nước, Vn-Index có 6 phiên tăng điểm liên tiếp. Nhiều cổ phiếu nhỏ tăng giá trở lại.', 'files/news/20101207/20101207195610.jpg', '<div align=\\"justify\\">\r\n	<p style=\\"text-align: justify;\\">\r\n		<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">Tr&ecirc;n s&agrave;n H&agrave; Nội, cổ phiếu <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=PkIOW5dv+tM=\\" target=\\"_blank\\">VE9</a></b> sốt trở lại trong nhiều ng&agrave;y qua. Từ ng&agrave;y 15-30/11, m&atilde; n&agrave;y tăng 57,3%, trong khi HNX-Index mới t&iacute;ch lũy 6,85% c&ugrave;ng thời gian. C&ugrave;ng thời điểm, <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=Mysi6/Lf+io=\\" target=\\"_blank\\">AAA</a></b> vọt từ 24.700 đồng l&ecirc;n 34.700 đồng, <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=fdl0tqhEhso=\\" target=\\"_blank\\">STP</a></b> từ 23.200 l&ecirc;n 28.700 đồng, <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=Z72q1Cz4dlE=\\" target=\\"_blank\\">SHN</a></b> từ 13.800 l&ecirc;n 19.000 đồng...<br />\r\n		<br />\r\n		Tại s&agrave;n TP HCM, từ ng&agrave;y 25/11, <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=7vyE+L9gyC4=\\" target=\\"_blank\\">KSS</a></b> qua mỗi phi&ecirc;n đều tăng gần hết bi&ecirc;n độ 5%, từ mức 20.600 đồng h&ocirc;m 22/11, cổ phiếu đ&oacute;ng cửa s&aacute;ng 30/11 tại 25.600 đồng. Tương tự, VNE từ 9.200 đồng l&ecirc;n 11.200 đồng, <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=vNBaHCcAw0M=\\" target=\\"_blank\\">MCG</a></b> từ 14.100 l&ecirc;n 17.000 đồng... cũng trong c&ugrave;ng thời gian.<br />\r\n		<br />\r\n		Đặc biệt trong phi&ecirc;n giao dịch ng&agrave;y 30/11, c&ugrave;ng với đ&agrave; tăng điểm của c&aacute;c chỉ số, gi&aacute; trị giao dịch của mỗi s&agrave;n (H&agrave; Nội v&agrave; TP HCM) đều vọt l&ecirc;n tr&ecirc;n 1.000 tỷ đồng - mức cao nhất trong 2 th&aacute;ng gần đ&acirc;y. Vn-Index vượt ngưỡng 450 điểm, đạt 451,59 điểm.<br />\r\n		<br />\r\n		Gi&aacute;m đốc chi nh&aacute;nh một c&ocirc;ng ty chứng kho&aacute;n ở TP HCM cho biết, trong 2 ng&agrave;y gần đ&acirc;y, &ocirc;ng nhận được h&agrave;ng chục thắc mắc với một c&acirc;u hỏi chung: &quot;V&igrave; sao thị trường tăng mạnh v&agrave; sẽ k&eacute;o d&agrave;i trong bao l&acirc;u?&quot;.<br />\r\n		<br />\r\n		Nhiều nh&agrave; đầu tư cũng như c&aacute;c chuy&ecirc;n gia chứng kho&aacute;n kh&aacute;c c&ugrave;ng thấy kh&oacute; hiểu về đ&agrave; tăng điểm li&ecirc;n tục của nhiều cổ phiếu v&agrave; Vn-Index bởi 3 l&yacute; do. Thứ nhất, c&aacute;c cổ phiếu tăng gi&aacute; kh&ocirc;ng c&oacute; những đột biến về th&ocirc;ng tin. Thứ hai, thị trường chứng kho&aacute;n thế giới đang lao dốc do t&aacute;c động từ cuộc khủng hoảng nợ tại Ch&acirc;u &Acirc;u. Thứ ba, c&aacute;c yếu tố vĩ m&ocirc; cũng chưa c&oacute; th&ocirc;ng tin t&iacute;ch cực, thậm ch&iacute; c&ograve;n c&oacute; chiều hướng hơi ti&ecirc;u cực với l&atilde;i suất v&agrave; lạm ph&aacute;t c&ugrave;ng tăng cao.<br />\r\n		<br />\r\n		Về l&yacute; do khiến thị trường tăng mạnh 2 phi&ecirc;n gần đ&acirc;y, &ocirc;ng gi&aacute;m đốc n&oacute;i tr&ecirc;n tiết lộ, cuối phi&ecirc;n ng&agrave;y 29/11 thị trường được một số nh&agrave; đầu tư lớn đẩy l&ecirc;n. Ch&iacute;nh v&igrave; vậy, chỉ trong &iacute;t ph&uacute;t cuối, hai s&agrave;n quay ngoắt 180 độ. &Ocirc;ng n&agrave;y cho rằng, &yacute; định đẩy gi&aacute; ban đầu chỉ do một v&agrave;i người thực hiện, song n&oacute; đ&atilde; nhanh ch&oacute;ng k&iacute;ch th&iacute;ch nhiều người tham gia. Hiệu ứng đ&aacute;m đ&ocirc;ng khiến giao dịch tăng đột biến trong phi&ecirc;n s&aacute;ng 30/11. Tuy nhi&ecirc;n, con s&oacute;ng tăng n&agrave;y duy tr&igrave; bao l&acirc;u, sẽ do nhiều yếu tố chi phối như lạm ph&aacute;t, l&atilde;i suất, tỷ gi&aacute;, kết quả hoạt động kinh doanh của doanh nghiệp...<br />\r\n		<br />\r\n		Chuy&ecirc;n gia n&agrave;y dự b&aacute;o, khi gi&aacute; tăng qu&aacute; n&oacute;ng th&igrave; nhiều nh&agrave; đầu tư sẽ chốt lời n&ecirc;n đ&agrave; tăng của cổ phiếu khi l&ecirc;n 20-30% sẽ gi&aacute;n đoạn.<br />\r\n		<br />\r\n		Trưởng bộ phận ph&acirc;n t&iacute;ch C&ocirc;ng ty chứng kho&aacute;n Bản Việt - Ho&agrave;ng Thị Hoa cũng cho rằng, 2 phi&ecirc;n tăng điểm c&ugrave;ng lượng giao dịch nhảy vọt ng&agrave;y 29-30/11 l&agrave; do c&oacute; &quot;mồi lửa&quot; g&acirc;y ch&uacute; &yacute;, k&iacute;ch th&iacute;ch nhiều người tham gia. Song &quot;mồi lửa&quot; n&agrave;y xuất ph&aacute;t từ đ&acirc;u th&igrave; chưa thể x&aacute;c định. Với nh&agrave; đầu tư, họ chỉ nh&igrave;n thấy s&oacute;ng l&agrave; tham gia bất chấp thị trường kh&ocirc;ng c&oacute; th&ocirc;ng tin tốt bởi gần như đ&atilde; miễn dịch với tin xấu.<br />\r\n		<br />\r\n		Tổng gi&aacute;m đốc một c&ocirc;ng ty quản l&yacute; quỹ lớn tiết lộ, yếu tố k&iacute;ch th&iacute;ch c&oacute; thể đến từ việc mua cổ phiếu của một quỹ đầu tư Bắc &Acirc;u. Quỹ n&agrave;y vừa chuyển 200 triệu USD v&agrave;o Việt Nam v&agrave; đang thực hiện giải ng&acirc;n. Tuy nhi&ecirc;n, ph&oacute; tổng gi&aacute;m đốc của một quỹ đầu tư nước ngo&agrave;i lớn kh&aacute;c lại cho rằng, &ocirc;ng chưa thấy yếu tố n&agrave;o t&iacute;ch cực đủ mức c&oacute; thể k&iacute;ch thị trường l&ecirc;n nhanh như vậy. Việc c&aacute;c quỹ nước ngo&agrave;i mua r&ograve;ng th&igrave; h&agrave;ng ng&agrave;y ai cũng thấy v&agrave; kh&oacute; c&oacute; thể trở th&agrave;nh nh&acirc;n tố &quot;nh&oacute;m lửa&quot; trong đợt tăng gi&aacute; lần n&agrave;y. Cũng v&igrave; thế, &ocirc;ng rất ngạc nhi&ecirc;n với mức tăng gi&aacute; khủng của nhiều cổ phiếu tr&ecirc;n thị trường.<br />\r\n		<br />\r\n		Kh&ocirc;ng l&yacute; giải việc tăng gi&aacute; bằng yếu tố &quot;nh&oacute;m lửa&quot;, &ocirc;ng Trần Thanh T&acirc;n - Tổng gi&aacute;m đốc C&ocirc;ng ty li&ecirc;n doanh quản l&yacute; quỹ Việt Nam cho rằng, gi&aacute; cổ phiếu giảm qu&aacute; l&acirc;u v&agrave; s&acirc;u th&igrave; sẽ c&oacute; l&uacute;c bật trở lại. Với nhiều cổ phiếu, khi gi&aacute; đ&atilde; xuống dưới gi&aacute; trị sổ s&aacute;ch m&agrave; hoạt động c&ocirc;ng ty vẫn tăng trưởng tốt th&igrave; nh&agrave; đầu tư mua v&agrave;o l&agrave; hợp l&yacute;. &quot;Đ&acirc;y l&agrave; một cơ hội tốt cho đầu tư d&agrave;i hạn&quot;, &ocirc;ng n&agrave;y nhận x&eacute;t.<br />\r\n		<br />\r\n		C&ugrave;ng quan điểm với &ocirc;ng T&acirc;n, b&agrave; Ho&agrave;ng Thị Hoa - Trưởng bộ phận ph&acirc;n t&iacute;ch C&ocirc;ng ty chứng kho&aacute;n Bản Việt, cho rằng: Nhiều cổ phiếu giảm mạnh gấp nhiều lần mức sụt giảm của c&aacute;c chỉ số trong thời gian qua. C&oacute; m&atilde; về mức gi&aacute; l&uacute;c Vn-Index dưới 300 điểm. Ch&iacute;nh v&igrave; vậy, việc mua chứng kho&aacute;n với mức xấp xỉ hoặc thấp hơn mệnh gi&aacute; như hiện nay được nhiều người ưa chuộng do rủi ro &iacute;t, đầu tư d&agrave;i hạn cũng kh&ocirc;ng sợ lỗ.</span></p>\r\n	<p style=\\"text-align: justify;\\">\r\n		<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">&nbsp;</span></p>\r\n	<p style=\\"text-align: right;\\">\r\n		<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">(Theo VNE)</span></p>\r\n</div>', '2010-12-07 19:44:18', NULL, 1, 1, 0);
INSERT INTO `articles` VALUES (43, 7, 1, 0, 'Tháng 12: Những yếu tố tạo nên bất ngờ', 'thang-12-nhung-yeu-to-tao-nen-bat-ngo', 'Thị trường tháng 12 tiềm ẩn nhiều cơ hội hơn so với tháng 10 và 11, nhưng lại đi kèm với những rủi ro mới. Do đó, NĐT nên tiếp cận chiến lược chốt lời ngắn hạn trong các chu kỳ điều chỉnh của...', 'files/news/20101207/20101207200042.jpg', '<p>\r\n	Trong th&aacute;ng 11, Ng&acirc;n h&agrave;ng Nh&agrave; nước (NHNN) đưa ra h&agrave;ng loạt c&aacute;c ch&iacute;nh s&aacute;ch vĩ m&ocirc; điều tiết thị trường tiền tệ, thị trường v&agrave;ng v&agrave; lạm ph&aacute;t. Trong chiến lược đầu tư th&aacute;ng 11, ch&uacute;ng t&ocirc;i đưa ra nhận định về 3 khả năng NHNN can thiệp v&agrave;o thị trường tiền tệ; với lựa chọn kh&ocirc;ng điều chỉnh tỷ gi&aacute;, t&igrave;m c&aacute;c biện ph&aacute;p tăng gi&aacute; đồng nội tệ, th&igrave; đ&acirc;y thực sự l&agrave; hướng tiếp cận ch&iacute;nh s&aacute;ch mang t&iacute;nh chất d&agrave;i hạn.<br />\r\n	<br />\r\n	Nhược điểm của biện ph&aacute;p n&agrave;y l&agrave; mất nhiều th&aacute;ng để ph&aacute;t huy t&aacute;c dụng v&agrave; thị trường ngắn hạn sẽ chịu sức &eacute;p lớn. Ngay trong th&aacute;ng 11, NĐT đ&atilde; chứng kiến mặt bằng l&atilde;i suất tăng cao trong thời gian ngắn, tỷ gi&aacute; li&ecirc;n tục biến động v&agrave; lạm ph&aacute;t tăng đột biến.<br />\r\n	<br />\r\n	Liệu th&aacute;ng 12, mặt bằng kinh tế vĩ m&ocirc; ổn định hơn hay thị trường tiếp tục chịu t&aacute;c động điều chỉnh trong ngắn hạn? Khi n&agrave;o ch&iacute;nh s&aacute;ch tiền tệ bắt đầu ph&aacute;t huy t&aacute;c động t&iacute;ch cực v&agrave; liệu thị trường c&oacute; khả năng đ&oacute;n đầu c&aacute;c th&ocirc;ng tin t&iacute;ch cực hơn ngay trong th&aacute;ng 12?<br />\r\n	<br />\r\n	Thị trường hiện nay vẫn đang ở giai đoạn nhạy cảm với nhiều luồng th&ocirc;ng tin tr&aacute;i chiều. Khi NHNN chọn giải ph&aacute;p b&igrave;nh ổn thị trường tiền tệ d&agrave;i hạn, &ldquo;hy sinh&rdquo; thị trường ngắn hạn, th&igrave; về nguy&ecirc;n tắc, thị trường trung v&agrave; d&agrave;i hạn sẽ diễn biến t&iacute;ch cực.<br />\r\n	<br />\r\n	Diễn biến thị trường ngắn hạn sẽ tiếp tục chứa đựng nhiều rủi ro v&agrave; ẩn số lớn nhất l&agrave; khi n&agrave;o c&aacute;c ch&iacute;nh s&aacute;ch d&agrave;i hạn bắt đầu ph&aacute;t huy t&aacute;c dụng, ổn định lại mặt bằng kinh tế vĩ m&ocirc;? Ch&uacute;ng t&ocirc;i nhận định, thị trường th&aacute;ng 12 chịu ảnh hưởng của c&aacute;c yếu tố sau:<br />\r\n	<br />\r\n	Thị trường tiền tệ nhiều khả năng tiếp tục chịu sức &eacute;p trong ngắn hạn<br />\r\n	<br />\r\n	Chọn giải ph&aacute;p thả nổi l&atilde;i suất huy động v&agrave; cho vay, đồng thời kh&ocirc;ng can thiệp mạnh v&agrave;o thị trường ngoại tệ, để tỷ gi&aacute; USD/VND dần điều chỉnh trong d&agrave;i hạn, NHNN đ&atilde; chấp nhận phương &aacute;n để thị trường tiền tệ biến động trong ngắn hạn, tự t&igrave;m điểm c&acirc;n bằng. Lựa chọn n&agrave;y về trung v&agrave; d&agrave;i hạn sẽ gi&uacute;p thị trường dần ổn định, nhưng trong ngắn hạn sẽ tạo sức &eacute;p l&ecirc;n nhiều yếu tố vĩ m&ocirc;:<br />\r\n	<br />\r\n	(i) Mặt bằng l&atilde;i suất: Khi gi&aacute; v&agrave;ng tăng mạnh v&agrave; VND li&ecirc;n tục mất gi&aacute;, NĐT c&oacute; xu hướng chuyển từ gửi tiết kiệm VND sang c&aacute;c k&ecirc;nh đầu tư kh&aacute;c. Điều n&agrave;y sẽ ảnh hưởng tới thanh khoản của c&aacute;c ng&acirc;n h&agrave;ng. Biện ph&aacute;p hiệu quả nhất l&agrave; c&aacute;c ng&acirc;n h&agrave;ng đồng loạt tăng l&atilde;i suất huy động (từ đ&oacute; tăng l&atilde;i suất cho vay) đến mức đủ hấp dẫn NĐT quay trở lại với VND.<br />\r\n	<br />\r\n	Chỉ trong 2 tuần cuối th&aacute;ng 11, l&atilde;i suất tiền gửi tiết kiệm của một số ng&acirc;n h&agrave;ng nhỏ đ&atilde; chạm ngưỡng 15%. Tuy nhi&ecirc;n, ch&uacute;ng t&ocirc;i nhận định, mức l&atilde;i suất n&agrave;y chỉ tồn tại trong ngắn hạn, tới khi thanh khoản của c&aacute;c ng&acirc;n h&agrave;ng ổn định v&agrave; VND giảm đ&agrave; mất gi&aacute;, do DN sẽ giảm mạnh nhu cầu vay khi l&atilde;i suất vượt ngưỡng 15 - 16%. Những ng&acirc;n h&agrave;ng c&oacute; thanh khoản tốt c&oacute; thể kh&ocirc;ng bị k&eacute;o v&agrave;o cuộc đua l&atilde;i suất với c&aacute;c ng&acirc;n h&agrave;ng nhỏ.<br />\r\n	<br />\r\n	Sự ch&ecirc;nh lệch về mặt bằng l&atilde;i suất giữa c&aacute;c ng&acirc;n h&agrave;ng đồng nghĩa với l&atilde;i suất trong th&aacute;ng 12 c&oacute; thể tiếp tục bị điều chỉnh li&ecirc;n tục, tới khi vấn đề thanh khoản được giải quyết.<br />\r\n	<br />\r\n	T&aacute;c động gi&aacute;n tiếp của mặt bằng l&atilde;i suất cao tới kết quả hoạt động kinh doanh của DN cũng rất đ&aacute;ng ch&uacute; &yacute;. Với l&atilde;i suất cho vay từ 16% đến 19% th&igrave; c&aacute;c DN sử dụng nhiều l&atilde;i vay ng&acirc;n h&agrave;ng sẽ chuyển sang hoạt động cầm chừng.<br />\r\n	<br />\r\n	Trong qu&yacute; IV/2010, kết quả hoạt động kinh doanh c&oacute; thể chưa bị t&aacute;c động lớn, do DN vẫn thực hiện theo c&aacute;c hợp đồng t&iacute;n dụng cũ, nhưng nếu k&eacute;o d&agrave;i, qu&yacute; I/2011 sẽ bị t&aacute;c động ti&ecirc;u cực ở một số nh&oacute;m ng&agrave;nh. Do c&aacute;c DN chưa c&ocirc;ng bố b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh trong th&aacute;ng 12, n&ecirc;n kết quả hoạt động kinh doanh của c&aacute;c DN trong qu&yacute; IV v&agrave; năm 2010 sẽ chưa t&aacute;c động nhiều tới diễn biến thị trường.<br />\r\n	<br />\r\n	(ii) Tỷ gi&aacute; USD/VND: T&iacute;nh đến 30/11/2010, gi&aacute; USD tr&ecirc;n thị trường tự do đ&atilde; vượt mức 21.500 đồng/USD, tức l&agrave; cao hơn 10,25% so với tỷ gi&aacute; ni&ecirc;m yết của ng&acirc;n h&agrave;ng. Nếu NHNN tiếp tục chấp nhận 2 thị trường ngoại tệ tồn tại song song với khoảng c&aacute;ch lớn, th&igrave; vấn đề tỷ gi&aacute; kh&oacute; c&oacute; khả năng được giải quyết ngay trong th&aacute;ng 12. V&igrave; tới nay, chưa thấy c&aacute;c h&agrave;nh động cụ thể của NHNN đưa th&ecirc;m nguồn cung USD ra thị trường. Tuy nhi&ecirc;n, rất c&oacute; thể NHNN sẽ can thiệp khi gi&aacute; USD tăng tới ngưỡng 22.000 đồng/USD, do tỷ gi&aacute; qu&aacute; cao sẽ tạo sức &eacute;p lớn l&ecirc;n lạm ph&aacute;t v&agrave; giảm t&aacute;c dụng của ch&iacute;nh s&aacute;ch tiền tệ đang thực hiện. Tỷ gi&aacute; ngo&agrave;i t&aacute;c động tới lạm ph&aacute;t v&agrave; chi ph&iacute; t&agrave;i ch&iacute;nh của DN, c&ograve;n ảnh hưởng kh&ocirc;ng nhỏ tới d&ograve;ng vốn ngoại v&agrave;o Việt Nam.<br />\r\n	<br />\r\n	(iii) Tăng trưởng t&iacute;n dụng: D&ograve;ng tiền đ&atilde; bị r&uacute;t đ&aacute;ng kể với chọn lựa ch&iacute;nh s&aacute;ch tiền tệ của NHNN. Tăng trưởng t&iacute;n dụng c&ograve;n lại của th&aacute;ng 12 so với mục ti&ecirc;u 25% cả năm 2010 kh&aacute; khi&ecirc;m tốn. Năm 2010, nh&igrave;n chung, tăng trưởng t&iacute;n dụng kh&ocirc;ng thấp, nhưng d&ograve;ng tiền kh&ocirc;ng chảy nhiều v&agrave;o thị trường tiền tệ v&agrave; khối DN tư nh&acirc;n, n&ecirc;n hiệu quả thấp hơn năm 2009.<br />\r\n	<br />\r\n	C&oacute; thể n&oacute;i, từng yếu tố ri&ecirc;ng biệt như th&ocirc;ng tin tỷ gi&aacute; hay chỉ số gi&aacute; ti&ecirc;u d&ugrave;ng (CPI) hiện nay kh&ocirc;ng c&ograve;n t&aacute;c động qu&aacute; ti&ecirc;u cực tới thị trường, do đ&atilde; được thể hiện phần n&agrave;o trong gi&aacute;. Bức tranh tổng qu&aacute;t c&aacute;c yếu tố tr&ecirc;n l&agrave; điều NĐT n&ecirc;n quan t&acirc;m, do đ&acirc;y l&agrave; thước đo về hiệu quả của ch&iacute;nh s&aacute;ch tiền tệ điều chỉnh trong th&aacute;ng 11. Nếu tỷ gi&aacute; dần ổn định v&agrave; mặt bằng l&atilde;i suất sau khi tăng phi m&atilde; trở lại mức hợp l&yacute; hơn sau 1 - 2 th&aacute;ng tới, th&igrave; đ&acirc;y l&agrave; tin tức t&iacute;ch cực đối với viễn cảnh thị trường năm 2011.<br />\r\n	<br />\r\n	<strong> Lạm ph&aacute;t c&oacute; thể giảm so với th&aacute;ng 11, nhưng vẫn ở mức cao</strong><br />\r\n	<br />\r\n	Theo nhận định của ch&uacute;ng t&ocirc;i, lạm ph&aacute;t th&aacute;ng 12 sẽ giảm tương đối so với th&aacute;ng 11, nhưng vẫn ở mức cao, do:<br />\r\n	<br />\r\n	(i) Mặt bằng tỷ gi&aacute; đang ổn định ở mức kh&aacute; cao v&agrave; phần nhiều đ&atilde; được điều chỉnh v&agrave;o lạm ph&aacute;t th&aacute;ng 11;<br />\r\n	<br />\r\n	(ii) Gi&aacute; thực phẩm đang c&oacute; xu hướng giảm sau khi li&ecirc;n tục tăng;<br />\r\n	<br />\r\n	(iii) Gi&aacute; cả tại khu vực miền Trung đang bắt đầu giảm sau khi cuộc sống của người d&acirc;n v&ugrave;ng lũ ổn định trở lại;<br />\r\n	<br />\r\n	(iv) Đ&acirc;y l&agrave; thời điểm ti&ecirc;u thụ của thời gian gi&aacute;p Tết.<br />\r\n	<br />\r\n	Dự kiến, lạm ph&aacute;t th&aacute;ng 12 c&oacute; thể ở mức xấp xỉ 1%, đưa lạm ph&aacute;t cả năm 2010 l&ecirc;n khoảng 10,5%. Nếu thị trường kh&ocirc;ng phản ứng qu&aacute; ti&ecirc;u cực với CPI th&aacute;ng 11, th&igrave; khả năng t&aacute;c động của CPI th&aacute;ng 12 tới TTCK kh&ocirc;ng nhiều. C&oacute; thể n&oacute;i, thị trường đ&atilde; dần &#39;&#39;&#39;&#39;quen&#39;&#39;&#39;&#39; với mặt bằng lạm ph&aacute;t cao trong thời gian gần đ&acirc;y v&agrave; c&oacute; thể k&eacute;o d&agrave;i tới sau Tết &acirc;m lịch.<br />\r\n	<br />\r\n	<strong>Kinh tế vĩ m&ocirc; năm 2010 nh&igrave;n chung kh&ocirc;ng đạt kỳ vọng của NĐT, nhưng mang lại nhiều cơ hội cho năm 2011</strong><br />\r\n	<br />\r\n	Trong th&aacute;ng 12, bức tranh tổng qu&aacute;t của kinh tế vĩ m&ocirc; Việt Nam năm 2010 sẽ được ho&agrave;n thiện. Nh&igrave;n chung, kết quả kh&ocirc;ng đạt được kỳ vọng của NĐT. Thực tế, kinh tế vĩ m&ocirc; năm 2010 của Việt Nam kh&ocirc;ng k&eacute;m hơn năm 2009, nhưng điều quan trọng l&agrave; kỳ vọng của NĐT cuối năm 2009 qu&aacute; cao so với thực tế đạt được trong năm 2010, n&ecirc;n thị trường li&ecirc;n tục điều chỉnh giảm để phản &aacute;nh khoảng c&aacute;ch kỳ vọng đ&oacute;. Nếu NĐT kh&ocirc;ng qu&aacute; kỳ vọng v&agrave;o kinh tế vĩ m&ocirc; năm 2011 v&agrave; diễn biến thực tế t&iacute;ch cực hơn, th&igrave; TTCK năm 2011 sẽ mang đến nhiều cơ hội.<br />\r\n	<br />\r\n	<strong>TTCK kh&ocirc;ng nhất thiết phải phản &aacute;nh t&igrave;nh h&igrave;nh kinh tế vĩ m&ocirc; hiện tại</strong><br />\r\n	<br />\r\n	Tuần giao dịch cuối c&ugrave;ng của th&aacute;ng 11 kiểm chứng lại một lần nữa mối quan hệ phức tạp giữa t&igrave;nh h&igrave;nh kinh tế vĩ m&ocirc; hiện tại v&agrave; diễn biến thị trường. VN-Index v&agrave; HNX-Index tăng bất ngờ trong bối cảnh mặt bằng l&atilde;i suất tăng cao, CPI th&aacute;ng 11 gần đạt mức đỉnh năm 2010 v&agrave; tỷ gi&aacute; đạt mốc kỷ lục mới. C&oacute; 2 hướng giải th&iacute;ch như sau:<br />\r\n	<br />\r\n	(i) Thị trường đ&atilde; phản &aacute;nh tất cả th&ocirc;ng tin ti&ecirc;u cực trong c&aacute;c đợt suy giảm trước; nhiều m&atilde; cổ phiếu giảm s&acirc;u tới mức đủ hấp dẫn để giải ng&acirc;n sau khi t&iacute;nh đến c&aacute;c yếu tố rủi ro tr&ecirc;n. Gi&aacute; cổ phiếu qu&aacute; &#39;&#39;&#39;&#39;rẻ&#39;&#39;&#39;&#39; v&agrave; đến mức đủ &#39;&#39;&#39;&#39;an to&agrave;n&#39;&#39;&#39;&#39; l&agrave; hai nh&acirc;n tố ch&iacute;nh giải th&iacute;ch cho hướng tiếp cận n&agrave;y.<br />\r\n	<br />\r\n	(ii) Thị trường đang phản &aacute;nh kỳ vọng của NĐT v&agrave;o tương lai của kinh tế vĩ m&ocirc; Việt Nam, khi c&aacute;c yếu tố ti&ecirc;u cực nhất c&oacute; thể n&oacute;i đ&atilde; được thiết lập (CPI cao, l&atilde;i suất tăng mạnh, vụ việc Vinashin đang dần được giải quyết&hellip;). Khả năng tốt l&ecirc;n của kinh tế vĩ m&ocirc; ho&agrave;n to&agrave;n chiếm ưu thế so với khả năng thị trường xấu hơn. Như vậy, việc chỉ số tăng điểm thể hiện kỳ vọng của NĐT v&agrave;o thị trường trung v&agrave; d&agrave;i hạn, đi c&ugrave;ng với bức tranh s&aacute;ng sủa hơn của nền kinh tế Việt Nam trong thời gian tới.<br />\r\n	<br />\r\n	Như vậy, nếu trong thời gian d&agrave;i (th&aacute;ng 7 - 11/2010) thị trường suy giảm để phản &aacute;nh lo ngại về t&igrave;nh h&igrave;nh kinh tế vĩ m&ocirc; của Việt Nam v&agrave; những lo ngại n&agrave;y đ&atilde; được hiện thực h&oacute;a trong th&aacute;ng 11, th&igrave; nhiều khả năng thị trường sẽ c&oacute; cơ hội tạo ra xu hướng mới khi NĐT bắt đầu lạc quan hơn về triển vọng kinh tế trong năm 2011.<br />\r\n	<br />\r\n	<strong>Th&aacute;ng cuối năm - yếu tố n&agrave;o c&oacute; khả năng tạo n&ecirc;n &#39;&#39;&#39;&#39;bất ngờ&#39;&#39;&#39;&#39;?</strong><br />\r\n	<br />\r\n	Th&aacute;ng 12 c&oacute; thể n&oacute;i l&agrave; th&aacute;ng quan trọng nhất của c&aacute;c đơn vị đầu tư t&agrave;i ch&iacute;nh tổ chức, do đ&acirc;y l&agrave; th&aacute;ng kết th&uacute;c năm, ảnh hưởng lớn tới kết quả hoạt động của c&aacute;c quỹ đầu tư, c&ocirc;ng ty chứng kho&aacute;n, ng&acirc;n h&agrave;ng&hellip; Do đ&oacute;, th&aacute;ng 12 c&oacute; thể đ&oacute;n nhận th&ecirc;m c&aacute;c yếu tố t&aacute;c động tới xu hướng thị trường:<br />\r\n	<br />\r\n	(i) Thanh khoản: Khi năm t&agrave;i ch&iacute;nh đến gần, nh&oacute;m NĐT tổ chức c&oacute; xu hướng t&aacute;i cơ cấu danh mục đầu tư mạnh mẽ hơn để phục vụ mục đ&iacute;ch b&aacute;o c&aacute;o cuối năm. Do đ&oacute;, thanh khoản c&oacute; cơ hội được cải thiện trong th&aacute;ng 12. Với t&igrave;nh h&igrave;nh thị trường như hiện nay, thanh khoản đang quan trọng hơn rất nhiều so với việc chỉ số chứng kho&aacute;n tăng hay giảm. Nếu thanh khoản duy tr&igrave; tr&ecirc;n 2.000 tỷ đồng/phi&ecirc;n, th&igrave; xu hướng bền vững hơn của thị trường c&oacute; khả năng được x&aacute;c lập.<br />\r\n	<br />\r\n	(ii) Gi&aacute; cổ phiếu: Trong nhiều năm trở lại đ&acirc;y, một trong những yếu tố được nhắc tới thường xuy&ecirc;n l&agrave; việc c&aacute;c c&ocirc;ng ty t&agrave;i ch&iacute;nh &ldquo;đỡ&rdquo; gi&aacute; trị t&agrave;i sản r&ograve;ng (NAV) trong 2 tuần cuối th&aacute;ng 12 để &ldquo;l&agrave;m đẹp&rdquo; b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh. Nếu xu hướng n&agrave;y tiếp tục, th&igrave; thị trường c&oacute; thể c&oacute; nhiều bất ngờ trong th&aacute;ng cuối c&ugrave;ng của năm 2010.<br />\r\n	<br />\r\n	T&oacute;m lại, 2 phi&ecirc;n giao dịch &ldquo;thăng hoa&rdquo; của cuối th&aacute;ng 11 kh&ocirc;ng c&oacute; nghĩa đ&atilde; đủ để khẳng định thị trường bắt đầu x&aacute;c lập một xu hướng tăng bền vững. Khi thị trường ngắn hạn vẫn chịu nhiều sức &eacute;p vĩ m&ocirc; v&agrave; hiệu quả của ch&iacute;nh s&aacute;ch tiền tệ l&agrave; ẩn số đối với thị trường trung v&agrave; d&agrave;i hạn, th&igrave; khả năng chốt lời ngắn hạn của NĐT để bảo to&agrave;n nguồn vốn vẫn cao. Đặc biệt l&agrave; khi d&ograve;ng tiền đầu cơ đang manh nha trở lại thị trường, th&igrave; biến động của chỉ số chứng kho&aacute;n sẽ bị m&eacute;o m&oacute; kh&aacute; nhiều.<br />\r\n	<br />\r\n	Thị trường th&aacute;ng 12 tiềm ẩn nhiều cơ hội hơn so với th&aacute;ng 10 v&agrave; 11, nhưng lại đi k&egrave;m với những rủi ro mới. Do đ&oacute;, NĐT n&ecirc;n tiếp cận chiến lược chốt lời ngắn hạn trong c&aacute;c chu kỳ điều chỉnh của thị trường trong giai đoạn hiện nay.</p>\r\n<p style=\\"text-align: right;\\">\r\n	(Theo ĐTCK)</p>', '2010-12-07 19:44:18', NULL, 1, 1, 0);
INSERT INTO `articles` VALUES (44, 7, 1, 0, 'Giao dịch chứng khoán tăng mạnh: dòng tiền mới?', 'giao-dich-chung-khoan-tang-manh-dong-tien-moi', '“Choáng”, “bất ngờ”,... là những cụm từ được nhiều nhà đầu tư (NĐT) thốt ra sau khi kết thúc phiên giao dịch đầu tuần hôm qua 6.12. Khối lượng giao dịch tăng \\"khủng\\" trên cả hai sàn với 184,5 triệu chứng khoán (CK), đạt tổng trị giá 4.153,7 tỉ đồng, tăng 35,6% về khối lượng và tăng 49,5% về giá trị so với phiên cuối tuần qua.', 'files/news/20101207/20101207200531.jpg', '<div align=\\"justify\\">\r\n	<p>\r\n		<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\"><strong>Cụ thể h&oacute;a lợi nhuận</strong><br />\r\n		<br />\r\n		Giao dịch tr&ecirc;n cả hai s&agrave;n chứng kho&aacute;n h&ocirc;m qua đ&atilde; diễn ra s&ocirc;i động với sự tham gia t&iacute;ch cực của NĐT ngay từ khi mở cửa. Nếu t&iacute;nh từ mức đ&aacute;y (ng&agrave;y 22.11) đến hết tuần qua, số lượng m&atilde; CK tăng tr&ecirc;n 30% chiếm đại đa số tr&ecirc;n thị trường. V&igrave; vậy khả năng cụ thể h&oacute;a lợi nhuận của NĐT l&agrave; rất lớn dẫn đến hiện tượng &ldquo;rung lắc&rdquo; trong phi&ecirc;n h&ocirc;m qua khiến chỉ số VN-Index biến động li&ecirc;n tục từ tăng sang giảm. Tuy nhi&ecirc;n v&igrave; lực cầu&nbsp; kh&aacute; mạnh, đặc biệt v&agrave;o c&aacute;c CP blue-chips tr&ecirc;n s&agrave;n TP.HCM n&ecirc;n VN-Index đ&oacute;ng cửa vẫn tăng nhẹ trở lại với 1,21 điểm, đạt 465,56 điểm. Điều n&agrave;y cũng diễn ra tương tự tr&ecirc;n s&agrave;n H&agrave; Nội khiến HNX-Index vẫn tăng th&ecirc;m 0,41 điểm v&agrave; đạt 117,07 điểm.<br />\r\n		<br />\r\n		Đ&oacute; cũng ch&iacute;nh l&agrave; nguy&ecirc;n nh&acirc;n dẫn đến khối lượng giao dịch tăng rất mạnh trong phi&ecirc;n h&ocirc;m qua. Dẫn đầu về khối lượng giao dịch tại s&agrave;n TP.HCM l&agrave; cổ phiếu (CP) của CTCP chứng kho&aacute;n S&agrave;i G&ograve;n (<b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=aydOcrXmqUA=\\" target=\\"_blank\\">SSI</a></b>) với 7,15 triệu CP v&agrave; tại s&agrave;n H&agrave; Nội l&agrave; CTCP chứng kho&aacute;n Kim Long (<b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=YuNIMSsyRXk=\\" target=\\"_blank\\">KLS</a></b>) với 10,72 triệu CP. <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=aydOcrXmqUA=\\" target=\\"_blank\\">SSI</a></b> v&agrave; <b><a href=\\"http://finance.vinabull.com/WebPages/General_stock.aspx?MaCP=YuNIMSsyRXk=\\" target=\\"_blank\\">KLS</a></b> đang được một số NĐT xem như l&agrave; t&iacute;n hiệu chỉ b&aacute;o cho diễn biến của thị trường trong những phi&ecirc;n giao dịch gần đ&acirc;y.<br />\r\n		<br />\r\n		<strong> D&ograve;ng tiền mới&nbsp;&nbsp; </strong><br />\r\n		<br />\r\n		Theo chuy&ecirc;n gia chứng kho&aacute;n L&ecirc; Đạt Ch&iacute;, đ&acirc;y kh&ocirc;ng phải l&agrave; d&ograve;ng tiền sẵn c&oacute; của những NĐT tr&ecirc;n thị trường m&agrave; l&agrave; d&ograve;ng tiền ho&agrave;n to&agrave;n mới. Đ&oacute; c&oacute; thể l&agrave; những NĐT đ&atilde; rời bỏ thị trường v&agrave; quay trở lại c&ugrave;ng với những NĐT mới tham gia.<br />\r\n		<br />\r\n		Thị trường sẽ tiếp tục chứng kiến những phi&ecirc;n giao dịch với khối lượng cao như hiện nay n&ecirc;n chưa thể gọi l&agrave; ph&acirc;n phối đỉnh như nhiều NĐT lo ngại. Hiện tại dường như c&aacute;c NĐT vẫn chưa ch&uacute; &yacute; đến những CP tốt v&igrave; nội lực m&agrave; chỉ tập trung v&agrave;o những CP đ&atilde; giảm qu&aacute; s&acirc;u. Do đ&oacute; trong thời gian tới, NĐT sẽ xem x&eacute;t v&agrave; quay về những CP tốt về cơ bản. V&igrave; vậy, xu hướng tăng của thị trường vẫn c&ograve;n tiếp tục trong thời gian tới.<br />\r\n		<br />\r\n		C&ugrave;ng chung nhận định, &ocirc;ng Phạm Linh - Tổng gi&aacute;m đốc CTCK Quốc tế - cho rằng sau một thời gian d&agrave;i giảm mạnh, nhiều NĐT đ&atilde; nhận ra gi&aacute; CP kh&aacute; thấp v&agrave; đ&oacute; l&agrave; cơ hội đầu tư lớn. Nhất l&agrave; hiện nay những k&ecirc;nh đầu tư kh&aacute;c kh&ocirc;ng c&ograve;n cơ hội hấp dẫn. Đơn cử như gi&aacute; v&agrave;ng thế giới biến động qu&aacute; thất thường, tiềm ẩn nhiều rủi ro. Gi&aacute; v&agrave;ng trong nước c&ograve;n bị ảnh hưởng bởi tỷ gi&aacute; USD n&ecirc;n kh&oacute; dự b&aacute;o. Gi&aacute; USD cũng đ&atilde; tăng cao n&ecirc;n cũng kh&ocirc;ng c&ograve;n hấp dẫn để đầu tư.<br />\r\n		<br />\r\n		Hơn nữa, theo &ocirc;ng Phạm Linh, c&aacute;c NĐT đang tin tưởng v&agrave;o sự tăng trưởng của nền kinh tế n&oacute;i chung v&agrave; c&aacute;c doanh nghiệp khi bước v&agrave;o năm 2011. &ldquo;Hầu hết c&aacute;c ng&acirc;n h&agrave;ng chỉ huy động tiết kiệm với thời hạn ngắn dưới 3 th&aacute;ng. Điều n&agrave;y c&oacute; thể dự b&aacute;o rằng chậm nhất đến cuối qu&yacute; 1/2011 l&atilde;i suất sẽ giảm về mức doanh nghiệp chấp nhận được. Ri&ecirc;ng gi&aacute; USD khi c&aacute;c NĐT trong v&agrave; ngo&agrave;i nước đều rất quan t&acirc;m th&igrave; t&ocirc;i tin Ch&iacute;nh phủ sẽ dồn hết sức để b&igrave;nh ổn tỷ gi&aacute; trong thời gian kh&ocirc;ng xa&rdquo;, &ocirc;ng Phạm Linh n&oacute;i.<br />\r\n		<br />\r\n		Ở g&oacute;c nh&igrave;n kh&aacute;c, một chuy&ecirc;n gia t&agrave;i ch&iacute;nh tại TP.HCM cho rằng thị trường chứng kho&aacute;n đang tr&ecirc;n đường hồi phục l&agrave; t&iacute;n hiệu tốt cho nền kinh tế. Điều n&agrave;y cho thấy c&aacute;c NĐT đ&atilde; c&oacute; thể tin tưởng v&agrave;o việc ph&aacute;t triển của kinh tế n&oacute;i chung v&agrave; c&aacute;c doanh nghiệp n&oacute;i ri&ecirc;ng trong năm 2011. Ngo&agrave;i ra, khi thị trường chứng kho&aacute;n trở n&ecirc;n s&ocirc;i động cũng sẽ h&uacute;t được một nguồn vốn đầu tư từ c&aacute;c k&ecirc;nh kh&aacute;c như ngoại tệ, v&agrave;ng,... đồng thời g&oacute;p phần l&agrave;m hạ nhiệt ở hai lĩnh vực n&agrave;y.</span></p>\r\n	<p style=\\"text-align: right;\\">\r\n		<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">(Theo TN)</span></p>\r\n</div>', '2010-12-07 19:44:18', NULL, 1, 1, 0);
INSERT INTO `articles` VALUES (45, 7, 1, 0, '"Ranh giới giữa lướt sóng và làm giá rất mong manh"', 'ranh-gioi-giua-luot-song-va-lam-gia-rat-mong-manh', 'Chủ tịch Hội đồng quản trị Công ty quản lý quỹ VFM Dominic Scriven chia sẻ về hiện tượng làm giá trên thị trường chứng khoán Việt Nam.', 'files/news/20101207/20101207202209.jpg', '<div align=\\"justify\\">\r\n	<p style=\\"text-align: justify;\\">\r\n		<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\"><strong><em>- Gần đ&acirc;y, giới đầu tư tại Việt Nam x&ocirc;n xao về vụ xử l&yacute; h&igrave;nh sự đầu ti&ecirc;n đối với tội l&agrave;m gi&aacute; chứng kho&aacute;n. &Ocirc;ng nhận định như thế n&agrave;o về sự kiện n&agrave;y?</em></strong><br />\r\n		<br />\r\n		- Theo th&ocirc;ng tin t&ocirc;i được biết th&igrave; vụ việc của cựu Chủ tịch Dược Viễn Đ&ocirc;ng l&agrave; lần đầu ti&ecirc;n người ta tiến h&agrave;nh khởi tố đối với tội l&agrave;m gi&aacute; chứng kho&aacute;n. Mặc d&ugrave; ta vẫn chưa biết kết quả xử &aacute;n sẽ ra sao nhưng một điều r&otilde; r&agrave;ng l&agrave; cơ quan an ninh đ&atilde; v&agrave;o cuộc để gi&uacute;p Ủy ban Chứng kho&aacute;n. Nếu những việc như thế n&agrave;y được tiếp tục, c&ugrave;ng với c&aacute;c quy định mới về c&ocirc;ng bố th&ocirc;ng tin, t&ocirc;i tin l&agrave; t&iacute;nh minh bạch tr&ecirc;n thị trường sẽ được cải thiện.<br />\r\n		<br />\r\n		<strong><em>- Việc xử l&yacute; h&igrave;nh sự như vậy c&oacute; thường xảy ra tại c&aacute;c thị trường lớn tr&ecirc;n thế giới kh&ocirc;ng thưa &ocirc;ng?</em></strong><br />\r\n		<br />\r\n		- C&oacute;, nếu kh&ocirc;ng muốn n&oacute;i l&agrave; rất phổ biến. Thị trường c&agrave;ng ph&aacute;t triển th&igrave; họ c&agrave;ng l&agrave;m chặt vấn đề n&agrave;y. Tuy nhi&ecirc;n, c&aacute;i kh&oacute; l&agrave; t&igrave;m đủ bằng chứng v&igrave; ranh giới giữa lướt s&oacute;ng cổ phiếu với l&agrave;m gi&aacute; đ&ocirc;i khi rất mong manh. Đ&acirc;y l&agrave; vấn đề m&agrave; nước n&agrave;o cũng gặp m&agrave; kh&ocirc;ng dễ g&igrave; giải quyết.<br />\r\n		<br />\r\n		Gần đ&acirc;y, t&ocirc;i thấy Bộ T&agrave;i ch&iacute;nh của Việt Nam đang nghi&ecirc;n cứu, bổ sung những quy định mới để x&aacute;c định r&otilde; vấn đề n&agrave;y. Cơ quan chức năng cũng đầu tư hơn cho hệ thống tin học để tăng chất lượng gi&aacute;m s&aacute;t, quản l&yacute; thị trường. Việc mới chỉ đưa được một vụ trong rất nhiều trường hợp l&agrave;m gi&aacute; ra xử l&yacute; h&igrave;nh sự cho thấy đ&acirc;y vẫn l&agrave; một c&acirc;u chuyện d&agrave;i.<br />\r\n		<br />\r\n		<strong><em>- &Ocirc;ng nghĩ yếu tố n&agrave;o khiến cho việc thao t&uacute;ng gi&aacute; chứng kho&aacute;n trở n&ecirc;n tương đối phổ biến tại Việt Nam như hiện nay?</em></strong><br />\r\n		<br />\r\n		- Nhiều người nhắc đến quy m&ocirc; thị trường. Thị trường nhỏ th&igrave; dễ l&agrave;m gi&aacute; hơn. Điều đ&oacute; cũng c&oacute; &yacute; đ&uacute;ng. Nhưng quan trọng hơn l&agrave; th&agrave;nh phần tham gia. Một thị trường c&oacute; đến 85% l&agrave; nh&agrave; đầu tư c&aacute; nh&acirc;n th&igrave; rất kh&oacute; kiểm so&aacute;t chuyện l&agrave;m gi&aacute;.<br />\r\n		<br />\r\n		Ở đ&acirc;y t&ocirc;i kh&ocirc;ng c&oacute; &yacute; n&oacute;i việc nh&agrave; đầu tư c&aacute; nh&acirc;n tham gia đ&ocirc;ng l&agrave; xấu v&igrave; mỗi th&igrave; trường c&oacute; một đặc điểm, ph&ugrave; hợp với giai đoạn ph&aacute;t triển của m&igrave;nh. Tuy nhi&ecirc;n, chỉ c&oacute; ph&aacute;t triển c&aacute;c h&igrave;nh thức đầu tư c&oacute; tổ chức th&igrave; chuyện l&agrave;m gi&aacute; mới c&oacute; thể được kiềm chế. Bởi chỉ c&oacute; nh&agrave; đầu tư tổ chức mới c&oacute; khả năng nhận thức th&ocirc;ng tin v&agrave; đủ tiềm lực để chống lại c&aacute;c &quot;đội l&aacute;i&quot;.<br />\r\n		<br />\r\n		<strong><em>- L&agrave; nh&agrave; đầu tư c&oacute; tổ chức, &ocirc;ng sẽ xử sự như thế n&agrave;o trước c&aacute;c cổ phiếu bị l&agrave;m gi&aacute;?</em></strong><br />\r\n		<br />\r\n		- Mỗi nh&agrave; đầu tư c&oacute; một ti&ecirc;u ch&iacute; kh&aacute;c nhau. V&agrave; lựa chọn của ch&uacute;ng t&ocirc;i kh&ocirc;ng bao giờ l&agrave; c&aacute;c cổ phiếu bị l&agrave;m gi&aacute;.</span></p>\r\n	<p style=\\"text-align: justify;\\">\r\n		<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">&nbsp;</span></p>\r\n	<p style=\\"text-align: right;\\">\r\n		<span class=\\"p_te8\\" id=\\"UcNewsDetail1_lblNoiDung\\" style=\\"display: inline-block; width: 500px;\\">(Theo VNE)</span></p>\r\n</div>', '2010-12-07 20:20:43', NULL, 1, 0, 0);

